import React from 'react';
import { Edit, Copy, Trash2, ArrowUp, ArrowDown, X } from 'lucide-react';
import '../../styles/CreateLanding.css';

const QuickEditToolkit = ({
                              element,
                              position,
                              onEdit,
                              onDuplicate,
                              onDelete,
                              onMoveUp,
                              onMoveDown,
                              onDeleteChildren,
                              onClose,
                          }) => {
    const { id, type } = element;
    const isSection = type === 'section';

    return (
        <div
            className="lpb-quick-edit-toolkit"
            style={{
                position: 'absolute',
                top: position.y,
                left: position.x,
                zIndex: 1002,
                background: '#fff',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                display: 'flex',
                gap: '6px',
                padding: '6px',
            }}
        >
            {/* Nút Chỉnh sửa */}
            <button
                onClick={() => onEdit(id)}
                className="p-2 hover:bg-gray-100 rounded"
                title="Chỉnh sửa"
            >
                <Edit size={18} className="text-gray-600" />
            </button>

            {/* Nút Nhân bản */}
            <button
                onClick={() => onDuplicate(id)}
                className="p-2 hover:bg-gray-100 rounded"
                title="Nhân bản"
            >
                <Copy size={18} className="text-gray-600" />
            </button>

            {/* Nút Xóa */}
            <button
                onClick={() => onDelete(id)}
                className="p-2 hover:bg-red-100 rounded"
                title="Xóa"
            >
                <Trash2 size={18} className="text-red-500" />
            </button>

            {/* Nút Di chuyển lên/xuống (chỉ hiển thị nếu không phải section) */}
            {!isSection && (
                <>
                    <button
                        onClick={() => onMoveUp(id)}
                        className="p-2 hover:bg-gray-100 rounded"
                        title="Di chuyển lên"
                    >
                        <ArrowUp size={18} className="text-gray-600" />
                    </button>
                    <button
                        onClick={() => onMoveDown(id)}
                        className="p-2 hover:bg-gray-100 rounded"
                        title="Di chuyển xuống"
                    >
                        <ArrowDown size={18} className="text-gray-600" />
                    </button>
                </>
            )}

            {/* Nút Xóa Children (chỉ hiển thị nếu là section) */}
            {isSection && (
                <button
                    onClick={() => onDeleteChildren(id)}
                    className="p-2 hover:bg-red-100 rounded"
                    title="Xóa tất cả phần tử con"
                >
                    <Trash2 size={18} className="text-red-600" />
                </button>
            )}

            {/* Nút Đóng */}
            <button
                onClick={onClose}
                className="p-2 hover:bg-gray-100 rounded"
                title="Đóng"
            >
                <X size={18} className="text-gray-600" />
            </button>
        </div>
    );
};

export default QuickEditToolkit;
