import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '@landinghub/api';
import CreateModal from '../components/pages-content/CreateModal';
import DogLoader from '../components/DogLoader';

const CreateLandingPage = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [pageDescription, setPageDescription] = useState('');
    const [aiGeneratedContent, setAiGeneratedContent] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.error('No token found, redirecting to /auth');
            navigate('/auth');
            return;
        }

        try {
            const decoded = jwtDecode(token);
            if (!decoded.userId) {
                throw new Error('Invalid token: userId not found');
            }
            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        } catch (err) {
            console.error('Error decoding token:', err);
            navigate('/auth');
        }
    }, [navigate]);

    const handleCreatePage = async () => {
        if (!pageDescription) {
            setError('Vui lòng nhập mô tả landing page');
            return;
        }
        try {
            setLoading(true);
            const response = await api.post('/api/pages', { description: pageDescription });
            setAiGeneratedContent(response.data.html || '');

            // Redirect to edit page with the newly created page
            navigate(`/pages/edit/${response.data.id}`, {
                state: {
                    page: response.data,
                    isNew: true
                }
            });
        } catch (err) {
            console.error('Create error:', err);
            setError('Lỗi khi tạo landing page: ' + (err.response?.data?.msg || err.message));
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <DogLoader />;
    }

    return (
        <div className="create-landing-page">
            <div className="create-header">
                <button onClick={() => navigate('/pages')} className="back-button">
                    ← Quay lại
                </button>
                <h1>Tạo Landing Page Mới</h1>
            </div>

            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)}>Đóng</button>
                </div>
            )}

            <div className="create-content">
                <div className="create-form">
                    <label htmlFor="description">Mô tả Landing Page</label>
                    <textarea
                        id="description"
                        value={pageDescription}
                        onChange={(e) => setPageDescription(e.target.value)}
                        placeholder="Nhập mô tả chi tiết về landing page bạn muốn tạo..."
                        rows="10"
                        className="description-textarea"
                    />

                    <div className="create-actions">
                        <button
                            onClick={handleCreatePage}
                            disabled={!pageDescription || loading}
                            className="create-button primary"
                        >
                            {loading ? 'Đang tạo...' : 'Tạo Landing Page'}
                        </button>
                        <button
                            onClick={() => navigate('/pages')}
                            className="create-button secondary"
                        >
                            Hủy
                        </button>
                    </div>
                </div>

                {aiGeneratedContent && (
                    <div className="preview-section">
                        <h3>Preview</h3>
                        <div dangerouslySetInnerHTML={{ __html: aiGeneratedContent }} />
                    </div>
                )}
            </div>
        </div>
    );
};

export default CreateLandingPage;
