// F:\landinghub-iconic\apps\web\src\pages\UserDashboard.js
import React, { useState, useEffect } from 'react';

const UserDashboard = () => {
    const [pages, setPages] = useState([]);
    const [leads, setLeads] = useState([]);
    const [reports, setReports] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Tạm thời vô hiệu hóa API call vì không có endpoint
                // const [pagesRes, leadsRes, reportsRes] = await Promise.all([
                //   api.get('/pages'),
                //   api.get('/leads'),
                //   api.get('/reports'),
                // ]);
                // setPages(pagesRes.data);
                // setLeads(leadsRes.data);
                // setReports(reportsRes.data);

                // Dữ liệu mẫu
                setPages([{ _id: 1, title: 'Page 1', status: 'Active' }]);
                setLeads([{ _id: 1, email: 'user@example.com', phone: '123456' }]);
                setReports({ view_count: 100, lead_count: 50 });
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu. Sử dụng dữ liệu mẫu.');
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <div>Đang tải...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div className="user-dashboard">
            <h1>User Dashboard</h1>
            <section>
                <h2>Tổng quan</h2>
                <p>Số Landing Page: {pages.length}</p>
                <p>Số Leads: {leads.length}</p>
                <p>View Count: {reports.view_count || 0}</p>
            </section>
        </div>
    );
};

export default React.memo(UserDashboard);
