import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import api from "@landinghub/api";
import DogLoader from "../components/DogLoader";
import "grapesjs/dist/css/grapes.min.css";
import grapesjs from "grapesjs";
import '../styles/CreateLanding.css'
const CreateLandingPage = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [pageDescription, setPageDescription] = useState("");
    const [aiGeneratedContent, setAiGeneratedContent] = useState("");
    const [showConfirmAI, setShowConfirmAI] = useState(false);
    const editorRef = useRef(null);
    const grapesRef = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        // check token
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/auth");
            return;
        }
        try {
            const decoded = jwtDecode(token);
            if (!decoded.userId) throw new Error("Invalid token");
            api.defaults.headers.common["Authorization"] = `Bearer ${token}`;
        } catch (err) {
            navigate("/auth");
        }
    }, [navigate]);

    useEffect(() => {
        if (!grapesRef.current) {
            grapesRef.current = grapesjs.init({
                container: editorRef.current,
                height: "100%",
                fromElement: false,
                storageManager: false,
                plugins: ["gjs-preset-webpage"],
            });
        }
    }, []);

    const handleCreatePage = async () => {
        if (!pageDescription) {
            setError("Vui lòng nhập mô tả landing page");
            return;
        }
        try {
            setLoading(true);
            const htmlContent = grapesRef.current.getHtml();
            const cssContent = grapesRef.current.getCss();

            // gửi dữ liệu + description cho AI
            const response = await api.post("/api/pages", {
                description: pageDescription,
                html: htmlContent,
                css: cssContent,
            });

            setAiGeneratedContent(response.data.html || "");
            setShowConfirmAI(true);
        } catch (err) {
            setError(
                "Lỗi khi tạo landing page: " + (err.response?.data?.msg || err.message)
            );
        } finally {
            setLoading(false);
        }
    };

    const handleConfirmAI = () => {
        navigate(`/pages/edit/${Date.now()}`, {
            state: {
                html: aiGeneratedContent,
                isNew: true,
            },
        });
    };

    if (loading) return <DogLoader />;

    return (
        <div className="create-landing-page">
            <div className="create-header">
                <button onClick={() => navigate("/pages")} className="back-button">
                    ← Quay lại
                </button>
                <h1>Tạo Landing Page Mới</h1>
            </div>

            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)}>Đóng</button>
                </div>
            )}

            <div className="create-content">
                {/* FORM nhập mô tả */}
                <div className="create-form">
                    <label htmlFor="description">Mô tả Landing Page</label>
                    <textarea
                        id="description"
                        value={pageDescription}
                        onChange={(e) => setPageDescription(e.target.value)}
                        placeholder="Nhập mô tả chi tiết về landing page bạn muốn tạo..."
                        rows="6"
                        className="description-textarea"
                    />

                    <div className="create-actions">
                        <button
                            onClick={handleCreatePage}
                            disabled={!pageDescription || loading}
                            className="create-button primary"
                        >
                            {loading ? "Đang tạo..." : "Tạo Landing Page"}
                        </button>
                        <button
                            onClick={() => navigate("/pages")}
                            className="create-button secondary"
                        >
                            Hủy
                        </button>
                    </div>
                </div>

                {/* Khu vực kéo thả */}
                <div className="editor-section">
                    <h3>Kéo thả bố cục</h3>
                    <div ref={editorRef} className="gjs-editor" />
                </div>
            </div>

            {/* Preview AI */}
            {showConfirmAI && (
                <div className="ai-preview-modal">
                    <div className="ai-preview-content">
                        <h3>AI gợi ý Landing Page</h3>
                        <div
                            className="ai-preview"
                            dangerouslySetInnerHTML={{ __html: aiGeneratedContent }}
                        />
                        <div className="ai-actions">
                            <button onClick={handleConfirmAI} className="confirm-btn">
                                Đồng ý sử dụng AI
                            </button>
                            <button
                                onClick={() => setShowConfirmAI(false)}
                                className="cancel-btn"
                            >
                                Giữ thiết kế cũ
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CreateLandingPage;
