import React, {useEffect, useState} from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../styles/doashboard-sidebar.css'

const Sidebar = ({ role }) => {
    const [isCompact, setIsCompact] = useState(false);
    const location = useLocation();

    useEffect(() => {
        const saved = localStorage.getItem("sidebarCompact");
        if (saved === "true") setIsCompact(true);
    }, []);

    useEffect(() => {
        // ✅ AUTO COMPACT ON /pages | EXPAND ON /dashboard
        if (location.pathname === "/pages" || location.pathname.includes('/create')) {
            setIsCompact(true);
            localStorage.setItem("sidebarCompact", "true");
        } else {
            setIsCompact(false);
            localStorage.setItem("sidebarCompact", "false");
        }
    }, [location.pathname]);

    // ✅ CREATOR MENU (Dashboard FIRST!)
    const creatorMenu = [
        {
            name: 'Dashboard',
            path: '/dashboard',
            icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )
        },
        {
            name: 'Landing Pages',
            path: '/pages',
            icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
            )
        },
        {
            name: 'Templates',
            path: '/templates',
            icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
            )
        },
        {
            name: 'Earnings',
            path: '/payments',
            icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                </svg>
            )
        }
    ];

    // ✅ ADMIN MENU
    const adminMenu = [
        {
            name: 'Dashboard',
            path: '/dashboard',
            icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )
        },
        { name: 'Users', path: '/users', icon: <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" /></svg> },
        { name: 'Templates', path: '/qltemplates', icon: <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" /></svg> },
        { name: 'Payments', path: '/payments', icon: <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" /></svg> },
        { name: 'Reports', path: '/reports', icon: <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" /></svg> }
    ];

    const menu = role === 'admin' ? adminMenu : creatorMenu;

    return (
        <aside className={`sidebar ${isCompact ? 'compact' : ''}`}>
            <div className="sidebar-bg-pattern"></div>
            <div className="sidebar-header">
                {!isCompact && (
                    <div className="sidebar-brand">
                        <div className="sidebar-brand-icon">
                            <svg fill="currentColor" viewBox="0 0 24 24">
                                <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
                            </svg>
                        </div>
                        <span className="sidebar-brand-text">LandingHub</span>
                    </div>
                )}
                <button className="sidebar-toggle" onClick={() => setIsCompact(!isCompact)}>
                    <svg className={`sidebar-toggle-icon ${isCompact ? 'rotated' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                </button>
            </div>
            <nav className="sidebar-nav">
                {menu.map((item) => {
                    const isActive = location.pathname === item.path;
                    return (
                        <Link
                            key={item.name}
                            to={item.path}
                            className={`sidebar-item ${isActive ? 'active' : ''} ${isCompact ? 'compact' : ''}`}
                            title={isCompact ? item.name : ''}
                            onClick={() => {
                                if (item.path === "/pages") {
                                    setIsCompact(true);
                                    localStorage.setItem("sidebarCompact", "true");
                                }
                            }}
                        >
                            {isActive && <div className="sidebar-item-indicator"></div>}
                            <div className="sidebar-icon">{item.icon}</div>
                            {!isCompact && <span className="sidebar-text">{item.name}</span>}
                            <div className="sidebar-item-glow"></div>
                        </Link>
                    );
                })}
            </nav>
            {!isCompact && (
                <div className="sidebar-footer">
                    <div className="sidebar-status">
                        <div className="sidebar-status-icon">
                            <div className="sidebar-status-dot"></div>
                        </div>
                        <span className="sidebar-status-text">Live: {role === 'admin' ? 'Admin' : 'Creator'}</span>
                    </div>
                </div>
            )}
        </aside>
    );
};

export default React.memo(Sidebar);
