import React from 'react';

const SelectionOverlay = ({ selectedElements, onResize, zoomLevel }) => {
    if (!selectedElements.length) return null;

    return (
        <>
            {selectedElements.map((element) => (
                <div
                    key={element.id}
                    style={{
                        position: 'absolute',
                        transform: `translate(${element.position.desktop?.x || 0}px, ${element.position.desktop?.y || 0}px)`,
                        width: element.size.width,
                        height: element.size.height,
                        border: '2px solid #2563eb',
                        pointerEvents: 'none',
                        zIndex: 1000,
                    }}
                />
            ))}
        </>
    );
};

export default SelectionOverlay;
