import React, { useState, useEffect, useRef, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../context/UserContext';
import api from '@landinghub/api';
import SidebarComponent from './pages-content/SidebarComponent';
import HeaderComponent from './pages-content/HeaderComponent';
import ControlsComponent from './pages-content/ControlsComponent';
import LandingList from './pages-content/LandingList';
import PaginationComponent from './pages-content/PaginationComponent';
import CreateModal from './pages-content/CreateModal';
import EditorModal from './pages-content/EditorModal';
import PreviewPanel from './pages-content/PreviewPanel';
import '../styles/PageContent.css';

const PageContent = () => {
    const { user } = useContext(UserContext);
    const [landingPages, setLandingPages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('Trạng thái');
    const [menuOpen, setMenuOpen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showCreateModal, setShowCreateModal] = useState(false);
    const [showEditorModal, setShowEditorModal] = useState(false);
    const [pageDescription, setPageDescription] = useState('');
    const [editingPage, setEditingPage] = useState(null);
    const [aiGeneratedContent, setAiGeneratedContent] = useState('');
    const editorRef = useRef(null);
    const navigate = useNavigate();

    // Configure API with JWT token from localStorage
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        } else if (!user) {
            setError('Vui lòng đăng nhập để tiếp tục');
            setLoading(false);
            navigate('/auth');
        }
    }, [user, navigate]);

    const fetchLandingPages = async () => {
        if (!user?.id) {
            setError('Không tìm thấy thông tin người dùng');
            setLoading(false);
            return;
        }
        try {
            setLoading(true);
            const response = await api.get('/api/pages');
            setLandingPages(response.data);
        } catch (err) {
            setError('Không thể tải danh sách landing page: ' + (err.response?.data?.error || err.message));
            console.error('Fetch error:', err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchLandingPages();
    }, [user]);

    const filteredPages = landingPages.filter((page) => {
        const matchesSearch = page.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesStatus = statusFilter === 'Trạng thái' || page.status === statusFilter;
        return matchesSearch && matchesStatus;
    });

    const handleCreatePage = async () => {
        if (!pageDescription) {
            setError('Vui lòng nhập mô tả landing page');
            return;
        }
        try {
            setLoading(true);
            const response = await api.post('/api/pages', { description: pageDescription });
            setLandingPages([...landingPages, response.data]);
            setAiGeneratedContent(response.data.html); // Hiển thị preview từ AI
            setShowCreateModal(false);
            setPageDescription('');
            navigate('/create-landing'); // Chuyển đến trang kéo thả
        } catch (err) {
            setError('Lỗi khi tạo landing page: ' + (err.response?.data?.error || err.message));
            console.error('Create error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleSavePage = async () => {
        if (editorRef.current && editingPage) {
            const html = editorRef.current.getHtml();
            const css = editorRef.current.getCss();
            const js = editorRef.current.getJs();
            const fullContent = `<style>${css}</style>${html}<script>${js}</script>`;
            try {
                setLoading(true);
                const response = await api.put(`/api/pages/${editingPage.id}`, { html: fullContent });
                setLandingPages(landingPages.map((p) => (p.id === editingPage.id ? response.data : p)));
                setShowEditorModal(false);
                setEditingPage(null);
            } catch (err) {
                setError('Lỗi khi lưu landing page: ' + (err.response?.data?.error || err.message));
                console.error('Save error:', err);
            } finally {
                setLoading(false);
            }
        }
    };

    const handlePublishPage = async (pageId) => {
        try {
            setLoading(true);
            const response = await api.post(`/api/pages/${pageId}/publish`);
            setLandingPages(landingPages.map((p) => (p.id === pageId ? response.data : p)));
        } catch (err) {
            setError('Lỗi khi triển khai landing page: ' + (err.response?.data?.error || err.message));
            console.error('Publish error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleDeletePage = async (pageId) => {
        if (!window.confirm('Bạn có chắc muốn xóa landing page này?')) return;
        try {
            setLoading(true);
            await api.delete(`/api/pages/${pageId}`);
            setLandingPages(landingPages.filter((p) => p.id !== pageId));
        } catch (err) {
            setError('Lỗi khi xóa landing page: ' + (err.response?.data?.error || err.message));
            console.error('Delete error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handlePreviewPage = (url) => {
        if (url) window.open(url, '_blank');
    };

    const handleEditPage = (page) => {
        setEditingPage(page);
        setShowEditorModal(true);
    };

    const handleMoreClick = (pageId) => {
        setMenuOpen(menuOpen === pageId ? null : pageId);
    };

    if (loading) {
        return <div className="loading">Đang tải...</div>;
    }

    return (
        <div className="page-content">
            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)} className="error-dismiss">
                        Đóng
                    </button>
                </div>
            )}
            <SidebarComponent />
            <div className="main-content">
                <HeaderComponent onCreateClick={() => setShowCreateModal(true)} />
                <CreateModal
                    showCreateModal={showCreateModal}
                    setShowCreateModal={setShowCreateModal}
                    pageDescription={pageDescription}
                    setPageDescription={setPageDescription}
                    handleCreatePage={handleCreatePage}
                />
                <EditorModal
                    showEditorModal={showEditorModal}
                    setShowEditorModal={setShowEditorModal}
                    editingPage={editingPage}
                    handleSavePage={handleSavePage}
                    editorRef={editorRef}
                />
                <ControlsComponent
                    searchTerm={searchTerm}
                    setSearchTerm={setSearchTerm}
                    statusFilter={statusFilter}
                    setStatusFilter={setStatusFilter}
                />
                <LandingList
                    landingPages={landingPages}
                    filteredPages={filteredPages}
                    handleMoreClick={handleMoreClick}
                    menuOpen={menuOpen}
                    handlePreviewPage={handlePreviewPage}
                    handleEditPage={handleEditPage}
                    handlePublishPage={handlePublishPage}
                    handleDeletePage={handleDeletePage}
                />
                <PaginationComponent filteredPages={filteredPages} landingPages={landingPages} />
                {aiGeneratedContent && <PreviewPanel ai generatedContent={aiGeneratedContent} />}
            </div>
        </div>
    );
};

export default PageContent;
