import React, { useState } from 'react';
import logo from '../../assets/logo.png';
import '../../styles/Toolbar.css';

const Toolbar = ({
                     onSave,
                     onPreview,
                     onGenerateCode,
                     viewMode,
                     onViewModeChange,
                     onUndo,
                     onRedo,
                     canUndo,
                     canRedo,
                     pageData,
                     onSelectElement,
                     selectedIds, // Thay selectedElement bằng selectedIds
                     onShowAddSectionGuide,
                 }) => {
    const [showLayers, setShowLayers] = useState(false);
    const [showGuide, setShowGuide] = useState(false);

    const handleToggleLayers = () => {
        console.log('Đang mở/đóng danh sách lớp, showLayers:', !showLayers);
        setShowLayers(!showLayers);
    };

    const handleSelectLayer = (element) => {
        console.log('Đã chọn lớp:', element);
        if (typeof onSelectElement === 'function') {
            onSelectElement([element.id]); // Truyền mảng ID để đồng bộ với Canvas
            setShowLayers(false); // Đóng panel sau khi chọn
        } else {
            console.error('onSelectElement không phải là hàm hoặc không được truyền');
        }
    };

    const handleToggleGuide = () => {
        const newShowGuide = !showGuide;
        console.log('Đang chuyển đổi hiển thị đường kẻ:', newShowGuide);
        setShowGuide(newShowGuide);
        if (typeof onShowAddSectionGuide === 'function') {
            onShowAddSectionGuide(newShowGuide);
        } else {
            console.error('onShowAddSectionGuide không phải là hàm');
        }
    };

    const handleViewModeChange = (mode) => {
        console.log('Thay đổi chế độ xem thành:', mode);
        if (typeof onViewModeChange === 'function') {
            onViewModeChange(mode);
        } else {
            console.error('onViewModeChange không phải là hàm');
        }
    };

    return (
        <div className="lpb-toolbar-container">
            <div className="lpb-toolbar-inner">
                <div className="lpb-toolbar-left">
                    <img src={logo} alt="Logo" className="logo-image" />
                </div>
                <div className="lpb-toolbar-right">
                    <button
                        onClick={() => handleViewModeChange('desktop')}
                        className={`lpb-toolbar-action-btn ${viewMode === 'desktop' ? 'lpb-toolbar-action-active' : ''}`}
                    >
                        <i className="fas fa-desktop"></i> Desktop
                    </button>
                    <button
                        onClick={() => handleViewModeChange('mobile')}
                        className={`lpb-toolbar-action-btn ${viewMode === 'mobile' ? 'lpb-toolbar-action-active' : ''}`}
                    >
                        <i className="fas fa-mobile-alt"></i> Mobile
                    </button>
                    <button
                        onClick={handleToggleGuide}
                        className={`lpb-toolbar-action-btn ${showGuide ? 'lpb-toolbar-action-active' : ''}`}
                    >
                        <i className="fas fa-ruler-horizontal"></i> Hiển thị đường kẻ
                    </button>
                    <button onClick={onUndo} className="lpb-toolbar-action-btn" disabled={!canUndo}>
                        <i className="fas fa-undo"></i> Undo
                    </button>
                    <button onClick={onRedo} className="lpb-toolbar-action-btn" disabled={!canRedo}>
                        <i className="fas fa-redo"></i> Redo
                    </button>
                    <button onClick={onPreview} className="lpb-toolbar-action-btn">
                        <i className="fas fa-eye"></i> Xem trước
                    </button>
                    <button onClick={handleToggleLayers} className="lpb-toolbar-action-btn">
                        <i className="fas fa-layer-group"></i> Danh sách lớp
                    </button>
                    <button onClick={onSave} className="lpb-toolbar-action-btn">
                        <i className="fas fa-save"></i> Lưu
                    </button>
                    <button onClick={onGenerateCode} className="lpb-toolbar-action-primary">
                        <i className="fas fa-code"></i> Triển khai
                    </button>
                </div>
            </div>
            {showLayers && (
                <div
                    className="lpb-layers-panel"
                    style={{
                        position: 'absolute',
                        top: '60px',
                        right: '10px',
                        background: '#fff',
                        borderRadius: '8px',
                        boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                        padding: '10px',
                        maxHeight: '300px',
                        overflowY: 'auto',
                        zIndex: 1000,
                        minWidth: '200px',
                    }}
                >
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <h4 style={{ margin: '0' }}>Danh sách lớp</h4>
                        <button
                            onClick={handleToggleLayers}
                            style={{
                                background: 'none',
                                border: 'none',
                                cursor: 'pointer',
                                padding: '5px',
                            }}
                        >
                            <i className="fas fa-times"></i>
                        </button>
                    </div>
                    {pageData?.elements?.length > 0 ? (
                        pageData.elements.map((element) => (
                            <div
                                key={element.id}
                                className={`lpb-layer-item ${selectedIds.includes(element.id) ? 'lpb-layer-item-active' : ''}`}
                                onClick={() => handleSelectLayer(element)}
                                style={{
                                    padding: '8px',
                                    cursor: 'pointer',
                                    backgroundColor: element.type === 'popup' ? '#e6f3ff' : '#f9f9f9',
                                    marginBottom: '4px',
                                    borderRadius: '4px',
                                    border: selectedIds.includes(element.id) ? '1px solid #3b82f6' : 'none',
                                }}
                            >
                                {element.type === 'section' ? 'Section' : element.type === 'popup' ? 'Popup' : element.type}: {element.componentData?.name || element.id}
                            </div>
                        ))
                    ) : (
                        <div className="lpb-no-results" style={{ padding: '10px', textAlign: 'center' }}>
                            <i className="fas fa-exclamation-circle"></i>
                            <p>Không có lớp nào</p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Toolbar;
