import React from 'react';
import { createPortal } from 'react-dom';

import {
    ChevronDown,
    MoreHorizontal,
    Clock,
} from 'lucide-react';

const styles = {
    container: 'landing-list-container w-full overflow-x-auto',
    table: 'landing-table w-full border-collapse bg-white shadow-sm rounded-lg overflow-hidden',
    thead: 'bg-gray-50 text-sm text-gray-600',
    th: 'p-3 text-left font-semibold border-b',
    checkboxColumn: 'w-12',
    nameColumn: 'w-1/4',
    statusColumn: 'w-1/5',
    metricColumn: 'w-1/6 text-center',
    actionsColumn: 'w-20 text-right',
    tbody: 'text-sm text-gray-700',
    noData: 'p-4 text-center text-gray-500',
    row: 'hover:bg-gray-50 transition-colors border-b',
    checkboxCell: 'p-2',
    pageInfo: 'flex flex-col',
    pageName: 'font-medium text-gray-900',
    pageUrl: 'text-blue-600 text-sm hover:underline truncate max-w-xs',
    statusContainer: 'flex flex-col items-start',
    status: 'status inline-block px-2 py-1 text-xs rounded-full font-medium',
    statusInfo: 'flex items-center gap-1 text-gray-500 text-xs mt-1',
    statusIcon: 'text-gray-400',
    timestamp: 'ml-1',
    moreBtn: 'p-1 text-gray-600 hover:text-gray-900 rounded focus:outline-none',
    contextMenu: 'absolute right-0 mt-2 w-48 bg-white border rounded shadow-lg z-20',
    contextMenuButton: 'w-full text-left p-2 hover:bg-gray-100 rounded text-sm',
};

const LandingList = ({
                         filteredPages,
                         menuOpen,
                         handlePreviewPage,
                         handleEditPage,
                         handlePublishPage,
                         handleDeletePage,
                     }) => {

    const displayPages = filteredPages;
    const [buttonPosition, setButtonPosition] = useState({ top: 0, left: 0 });

    const formatDate = (dateString) => {
        if (!dateString) return 'Chưa cập nhật';
        const date = new Date(dateString);
        return date.toLocaleString('vi-VN', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric',
            hour: '2-digit',
            minute: '2-digit',
        });
    };

    const getStatusClass = (status) => {
        switch (status) {
            case 'ĐÃ XUẤT BẢN':
                return 'bg-green-100 text-green-700';
            case 'CHƯA XUẤT BẢN':
                return 'bg-yellow-100 text-yellow-700';
            case 'ARCHIVED':
                return 'bg-gray-200 text-gray-600';
            default:
                return 'bg-gray-100 text-gray-800';
        }
    };

    return (
        <div className={styles.container}>
            <table className={styles.table}>
                <thead className={styles.thead}>
                <tr>
                    <th className={`${styles.th} ${styles.checkboxColumn}`}>
                        <input type="checkbox" />
                    </th>
                    <th className={`${styles.th} ${styles.nameColumn}`}>Tên Trang</th>
                    <th className={`${styles.th} ${styles.statusColumn}`}>Trạng Thái</th>
                    <th className={styles.th}>Lượt Truy Cập <ChevronDown size={14} className="inline ml-1" /></th>
                    <th className={styles.th}>Chuyển đổi</th>
                    <th className={styles.th}>Doanh thu</th>
                    <th className={`${styles.th} ${styles.actionsColumn}`}>Hành động</th>
                </tr>
                </thead>
                <tbody className={styles.tbody}>
                {displayPages.length === 0 ? (
                    <tr>
                        <td colSpan="7" className={styles.noData}>
                            Không có landing page nào để hiển thị
                        </td>
                    </tr>
                ) : (
                    displayPages.map((page) => (
                        <tr key={page._id} className={styles.row}>
                            <td className={styles.checkboxCell}>
                                <input type="checkbox" />
                            </td>
                            <td className={styles.nameColumn}>
                                <div className={styles.pageInfo}>
                                    <span className={styles.pageName}>{page.name}</span>
                                    {page.url && (
                                        <a
                                            href={page.url}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className={styles.pageUrl}
                                        >
                                            {page.url}
                                        </a>
                                    )}
                                </div>
                            </td>
                            <td className={styles.statusColumn}>
                                <div className={styles.statusContainer}>
                                        <span className={`${styles.status} ${getStatusClass(page.status)}`}>
                                            {page.status}
                                        </span>
                                    <div className={styles.statusInfo}>
                                        <Clock size={12} className={styles.statusIcon} />
                                        <span className={styles.timestamp}>
                                                {formatDate(page.lastUpdated)}
                                            </span>
                                    </div>
                                </div>
                            </td>
                            <td className="text-center">{page.views || 0}</td>
                            <td className="text-center">{page.conversions || 0}</td>
                            <td className="text-center">{page.revenue || '0đ'}</td>
                            <td className={styles.actionsColumn + " relative"}>
                                <button
                                    className={styles.moreBtn}
                                    onClick={() => handleMoreClick(page.id)}
                                    aria-label="More actions"
                                >
                                    <MoreHorizontal size={16} />
                                </button>
                                {menuOpen === page.
                                    id && createPortal(
                                    <div
                                        className={`${styles.contextMenu} z-50`}
                                        style={{
                                            position: 'fixed',
                                            right: '${buttonPosition.top}px',
                                            top: '${buttonPosition.top}px',
                                        }}
                                    >
                                        <button
                                            className={styles.contextMenuButton}
                                            onClick={() => handlePreviewPage(page.url)}
                                        >
                                            Xem trước
                                        </button>
                                        <button
                                            className={styles.contextMenuButton}
                                            onClick={() => handleEditPage(page)}
                                        >
                                            Chỉnh sửa
                                        </button>
                                        <button
                                            className={styles.contextMenuButton}
                                            onClick={() => handlePublishPage(page.id)}
                                        >
                                            Triển khai
                                        </button>
                                        <button
                                            className={styles.contextMenuButton}
                                            onClick={() => handleDeletePage(page.id)}
                                        >
                                            Xóa
                                        </button>
                                    </div>,
                                    document.body
                                )}
                            </td>
                        </tr>
                    ))
                )}
                </tbody>
            </table>
        </div>
    );
};

export default LandingList;
