// File: Element.js - Cải tiến
import React, { useRef, useEffect, useCallback } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';
import { ItemTypes, getCanvasPosition, snapToGrid, getElementBounds, renderComponentContent } from './helpers';

export const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         componentData = {},
         styles = {},
         isSelected,
         onSelectChild,
         zoomLevel,
         gridSize,
         showGrid,
         onUpdateChildPosition,
         onMoveChild,
         position,
         size,
         viewMode,
         visible = true,
         locked = false,
     }) => {
        const dragRef = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            canDrag: () => !locked && !componentData.locked,
            item: () => {
                if (locked || componentData.locked) {
                    toast.warning('Child element đã bị khóa!');
                    return null;
                }
                console.log('ChildElement drag started:', { childId: id, parentId });
                return { childId: id, parentId, isExisting: true, position, size };
            },
            end: (item, monitor) => {
                if (!monitor.didDrop()) {
                    console.log('ChildElement drag ended without drop, clearing preview');
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, parentId, position, size, locked, componentData.locked]);

        const handleClick = useCallback((e) => {
            e.stopPropagation();
            console.log('ChildElement clicked:', { parentId, id, isSelected });
            if (typeof onSelectChild === 'function') {
                onSelectChild(parentId, id);
            } else {
                console.warn('onSelectChild is not a function');
            }
        }, [parentId, id, isSelected, onSelectChild]);

        useEffect(() => {
            if (!locked && !componentData.locked) {
                drag(dragRef);
            }
        }, [drag, locked, componentData.locked]);

        if (visible === false || componentData.visible === false) {
            return null;
        }

        const lockedStyles = (locked || componentData.locked) ? {
            opacity: 0.7,
            cursor: 'not-allowed',
            pointerEvents: 'none',
        } : {};

        return (
            <div
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${isDragging ? 'lpb-child-element-dragging' : ''} ${(locked || componentData.locked) ? 'lpb-element-locked' : ''}`}
                style={{
                    position: 'absolute',
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size?.width || 200,
                    height: size?.height || 50,
                    zIndex: isDragging ? 1000 : (position.z || 10),
                    cursor: (locked || componentData.locked) ? 'not-allowed' : (isDragging ? 'grabbing' : 'grab'),
                    opacity: isDragging ? 0.5 : 1,
                    ...styles,
                    ...lockedStyles,
                }}
                onClick={handleClick}
            >
                <div ref={dragRef}>
                    {renderComponentContent(type, componentData, styles)}
                </div>

                {(locked || componentData.locked) && (
                    <div style={{
                        position: 'absolute',
                        top: '2px',
                        right: '2px',
                        zIndex: 10001,
                        background: 'rgba(0,0,0,0.7)',
                        color: '#fff',
                        padding: '2px 6px',
                        borderRadius: '3px',
                        fontSize: '10px',
                        pointerEvents: 'none',
                    }}>
                        <i className="fas fa-lock"></i>
                    </div>
                )}

                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => console.log('Edit child:', id)}
                        onDuplicate={() => console.log('Duplicate child:', id)}
                        onDelete={() => console.log('Delete child:', id)}
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);

// Element Component
export const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         showGrid,
         setDragPreview,
         visiblePopups = [],
     }) => {
        const {
            id,
            type,
            componentData = {},
            position = {},
            size = { width: viewMode === 'mobile' ? 375 : 1200, height: 400 },
            styles = {},
            children = [],
            visible = true,
            locked = false
        } = element;

        const elementRef = useRef(null);
        const containerRef = useRef(null);

        const handleMouseDown = useCallback(
            (e) => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return;
                }

                e.stopPropagation();
                console.log('Element clicked:', { id, type });
                if (typeof onSelectElement === 'function') {
                    onSelectElement([id], e.ctrlKey);
                } else {
                    console.error('onSelectElement is not a function');
                }
                onSelectChild(id, null);
            },
            [id, type, locked, onSelectElement, onSelectChild]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        const snapPoints = [
            { x: 0, y: 0 },
            { x: size.width, y: size.height },
            { x: size.width / 2, y: size.height / 2 },
        ];
        children.forEach((child) => {
            const bounds = getElementBounds(child);
            snapPoints.push(
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY },
                { x: bounds.left, y: bounds.centerY },
                { x: bounds.right, y: bounds.centerY },
                { x: bounds.centerX, y: bounds.top },
                { x: bounds.centerX, y: bounds.bottom }
            );
        });

        if (visible === false) {
            return null;
        }

        // ✅ POPUP LOGIC - Hiển thị khi được select hoặc có trong visiblePopups
        const isPopup = type === 'popup';
        // Popup sẽ hiển thị nếu: đang được select HOẶC có trong danh sách visiblePopups
        const shouldShowPopup = isSelected || visiblePopups.includes(id);
        if (isPopup && !shouldShowPopup) {
            return null;
        }

        // ✅ SECTION với Drop Zone cho elements
        if (type === 'section' && componentData.structure === 'ladi-standard') {
            const [{ isOverContainer, canDropContainer }, drop] = useDrop(() => ({
                accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
                canDrop: (item, monitor) => {
                    if (!type || type !== 'section' || componentData?.structure !== 'ladi-standard') {
                        return false;
                    }
                    if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT && item.parentId && item.parentId !== id) {
                        return false;
                    }
                    return true;
                },
                drop: (item, monitor) => {
                    if (!monitor.canDrop()) {
                        toast.error('Không thể thả vào container này!');
                        setDragPreview(null);
                        return { moved: false };
                    }
                    const clientOffset = monitor.getClientOffset();
                    if (!clientOffset || !containerRef.current) {
                        toast.error('Không thể xác định vị trí thả!');
                        setDragPreview(null);
                        return { moved: false };
                    }

                    const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                    const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                    if (monitor.getItemType() === ItemTypes.ELEMENT) {
                        const newId = `${item.id}-${Date.now()}`;
                        const newChild = {
                            id: newId,
                            type: item.json.type,
                            componentData: item.json.componentData || {},
                            position: {
                                [viewMode]: snapped,
                                desktop: snapped,
                                tablet: snapped,
                                mobile: snapped,
                            },
                            size: item.json.size || { width: 200, height: 50 },
                            styles: item.json.styles || {},
                            children: [],
                            visible: true,
                            locked: false,
                        };
                        onAddChild(id, newChild);
                        toast.success('Đã thêm thành phần con vào section!');
                        setDragPreview(null);
                        return { moved: true, newPosition: snapped };
                    } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                        if (item.parentId === id) {
                            onUpdateChildPosition(id, item.childId, snapped);
                            toast.success('Đã di chuyển thành phần con trong section!');
                        } else {
                            onMoveChild(item.parentId, item.childId, id, snapped);
                            toast.success('Đã di chuyển thành phần con sang section khác!');
                        }
                        setDragPreview(null);
                        return { moved: true, newPosition: snapped };
                    }
                    setDragPreview(null);
                    return { moved: false };
                },
                collect: (monitor) => ({
                    isOverContainer: monitor.isOver(),
                    canDropContainer: monitor.canDrop(),
                }),
            }), [id, type, componentData, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild]);

            useEffect(() => {
                drop(containerRef);
            }, [drop]);

            return (
                <div
                    ref={elementRef}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                    style={{
                        position: 'absolute',
                        top: position[viewMode]?.y || 0,
                        width: viewMode === 'mobile' ? '375px' : '1200px',
                        height: size.height,
                        left: '50%',
                        transform: `translateX(-50%)`,
                        zIndex: position.z || 1,
                        userSelect: 'none',
                        ...(locked ? { opacity: 0.85, pointerEvents: 'none' } : {}),
                    }}
                    onMouseDown={locked ? undefined : handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    {locked && (
                        <div style={{
                            position: 'absolute',
                            top: '10px',
                            right: '10px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '6px 12px',
                            borderRadius: '6px',
                            fontSize: '12px',
                            pointerEvents: 'none',
                        }}>
                            <i className="fas fa-lock"></i> Section Locked
                        </div>
                    )}

                    <div className="ladi-section" style={{ width: '100%', height: '100%' }}>
                        <div className="ladi-section-background" style={{
                            backgroundColor: componentData.backgroundColor || 'transparent',
                            backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                            backgroundSize: 'cover',
                            backgroundPosition: 'center',
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            zIndex: 0,
                        }}></div>
                        <div className="ladi-overlay" style={{
                            backgroundColor: componentData.overlayColor || 'transparent',
                            opacity: componentData.overlayOpacity || 0,
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            zIndex: 0,
                        }}></div>
                        <div
                            ref={containerRef}
                            className={`ladi-container ${isOverContainer && canDropContainer ? 'lpb-child-element-hover' : ''}`}
                            style={{
                                position: 'relative',
                                zIndex: 1,
                                padding: componentData.padding || '20px',
                                width: '100%',
                                height: '100%',
                                border: isOverContainer && canDropContainer ? '2px dashed #2563eb' : 'none',
                            }}
                        >
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            );
        }

        // ✅ POPUP với Drop Zone
        if (isPopup) {
            const [{ isOverPopup, canDropPopup }, dropPopup] = useDrop(() => ({
                accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
                canDrop: () => true,
                drop: (item, monitor) => {
                    const clientOffset = monitor.getClientOffset();
                    if (!clientOffset || !containerRef.current) {
                        toast.error('Không thể xác định vị trí thả!');
                        setDragPreview(null);
                        return { moved: false };
                    }

                    const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                    const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                    if (monitor.getItemType() === ItemTypes.ELEMENT) {
                        const newId = `${item.id}-${Date.now()}`;
                        const newChild = {
                            id: newId,
                            type: item.json.type,
                            componentData: item.json.componentData || {},
                            position: {
                                [viewMode]: snapped,
                                desktop: snapped,
                                tablet: snapped,
                                mobile: snapped,
                            },
                            size: item.json.size || { width: 200, height: 50 },
                            styles: item.json.styles || {},
                            children: [],
                            visible: true,
                            locked: false,
                        };
                        onAddChild(id, newChild);
                        toast.success('Đã thêm thành phần vào popup!');
                        setDragPreview(null);
                        return { moved: true, newPosition: snapped };
                    }
                    setDragPreview(null);
                    return { moved: false };
                },
                collect: (monitor) => ({
                    isOverPopup: monitor.isOver(),
                    canDropPopup: monitor.canDrop(),
                }),
            }), [id, viewMode, zoomLevel, gridSize, showGrid, onAddChild]);

            useEffect(() => {
                if (containerRef.current) {
                    dropPopup(containerRef);
                }
            }, [dropPopup]);

            return (
                <div
                    ref={elementRef}
                    className={`lpb-canvas-element lpb-popup ${isSelected ? 'lpb-canvas-element-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                    style={{
                        position: 'fixed',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        width: viewMode === 'mobile' ? '90%' : '600px',
                        minHeight: '400px',
                        maxHeight: '80vh',
                        zIndex: 10001, // Above backdrop
                        background: componentData.background || 'rgba(255, 255, 255, 0.98)',
                        borderRadius: componentData.borderRadius || '12px',
                        boxShadow: '0 8px 32px rgba(0,0,0,0.25)',
                        padding: '0',
                        overflow: 'hidden',
                        userSelect: 'none',
                        ...styles,
                        ...(locked ? { opacity: 0.85, pointerEvents: 'none' } : {}),
                    }}
                    onMouseDown={locked ? undefined : handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    {/* ✅ Backdrop overlay */}
                    <div
                        onClick={(e) => {
                            e.stopPropagation();
                            onSelectElement([]);
                        }}
                        style={{
                            position: 'fixed',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'rgba(0, 0, 0, 0.5)',
                            zIndex: -1,
                            backdropFilter: 'blur(4px)',
                        }}
                    />

                    {/* Popup Header */}
                    <div style={{
                        padding: '20px 24px',
                        borderBottom: '1px solid #e5e7eb',
                        background: 'linear-gradient(to bottom, #f9fafb, #ffffff)',
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                    }}>
                        <h3 style={{
                            margin: 0,
                            fontSize: '18px',
                            fontWeight: '600',
                            color: '#111827',
                        }}>
                            {componentData.title || 'Popup Title'}
                        </h3>
                        <button
                            onClick={(e) => {
                                e.stopPropagation();
                                onSelectElement([]);
                            }}
                            style={{
                                width: '32px',
                                height: '32px',
                                border: 'none',
                                background: 'transparent',
                                cursor: 'pointer',
                                borderRadius: '6px',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                transition: 'all 0.15s ease',
                            }}
                            onMouseEnter={(e) => e.target.style.background = '#f3f4f6'}
                            onMouseLeave={(e) => e.target.style.background = 'transparent'}
                        >
                            <i className="fas fa-times" style={{ fontSize: '16px', color: '#6b7280' }}></i>
                        </button>
                    </div>

                    {/* Popup Content with Drop Zone */}
                    <div
                        ref={containerRef}
                        className={`popup-content ${isOverPopup && canDropPopup ? 'lpb-child-element-hover' : ''}`}
                        style={{
                            position: 'relative',
                            padding: componentData.padding || '24px',
                            minHeight: '300px',
                            maxHeight: 'calc(80vh - 80px)',
                            overflowY: 'auto',
                            border: isOverPopup && canDropPopup ? '2px dashed #2563eb' : 'none',
                            background: isOverPopup && canDropPopup ? 'rgba(37, 99, 235, 0.05)' : 'transparent',
                        }}
                    >
                        {children.length === 0 && (
                            <div style={{
                                display: 'flex',
                                flexDirection: 'column',
                                alignItems: 'center',
                                justifyContent: 'center',
                                padding: '60px 24px',
                                color: '#9ca3af',
                                textAlign: 'center',
                            }}>
                                <i className="fas fa-hand-pointer" style={{ fontSize: '48px', marginBottom: '16px', opacity: 0.3 }}></i>
                                <p style={{ margin: '0 0 8px', fontSize: '14px', fontWeight: '500', color: '#6b7280' }}>
                                    Kéo thả elements vào đây
                                </p>
                                <small style={{ fontSize: '12px' }}>
                                    Popup có thể chứa bất kỳ element nào
                                </small>
                            </div>
                        )}
                        {children.map((child) => (
                            <ChildElement
                                key={child.id}
                                parentId={id}
                                id={child.id}
                                type={child.type}
                                componentData={child.componentData || {}}
                                styles={child.styles || {}}
                                position={child.position || {}}
                                size={child.size || { width: 200, height: 50 }}
                                visible={child.visible !== false}
                                locked={child.locked || false}
                                isSelected={selectedChildId === child.id}
                                onSelectChild={onSelectChild}
                                zoomLevel={zoomLevel}
                                gridSize={gridSize}
                                showGrid={showGrid}
                                onUpdateChildPosition={onUpdateChildPosition}
                                onMoveChild={onMoveChild}
                                viewMode={viewMode}
                            />
                        ))}
                    </div>

                    {locked && (
                        <div style={{
                            position: 'absolute',
                            top: '5px',
                            right: '5px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '4px 8px',
                            borderRadius: '4px',
                            fontSize: '11px',
                            pointerEvents: 'none',
                        }}>
                            <i className="fas fa-lock"></i> Locked
                        </div>
                    )}
                </div>
            );
        }

        // REGULAR ELEMENTS (không phải section hoặc popup)
        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            canDrag: () => !locked,
            item: () => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return null;
                }
                console.log('Element drag started:', { id, position: position[viewMode] });
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                };
            },
            end: (item, monitor) => {
                if (!monitor.didDrop()) {
                    console.log('Element drag ended without drop, clearing preview');
                    setDragPreview(null);
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode, locked]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        const lockedStyles = locked ? {
            opacity: 0.7,
            cursor: 'not-allowed',
            pointerEvents: 'none',
        } : {};

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    if (!locked) {
                        drag(node);
                    }
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                style={{
                    position: 'absolute',
                    top: position[viewMode]?.y || 0,
                    width: viewMode === 'mobile' ? '375px' : '1200px',
                    height: size.height,
                    left: '50%',
                    transform: 'translateX(-50%)',
                    zIndex: position.z || 1,
                    opacity: isDragging ? 0.5 : 1,
                    cursor: locked ? 'not-allowed' : (isDragging ? 'grabbing' : 'grab'),
                    userSelect: 'none',
                    ...styles,
                    ...lockedStyles,
                }}
                onMouseDown={locked ? undefined : handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {locked && (
                    <div style={{
                        position: 'absolute',
                        top: '5px',
                        right: '5px',
                        zIndex: 10001,
                        background: 'rgba(0,0,0,0.7)',
                        color: '#fff',
                        padding: '4px 8px',
                        borderRadius: '4px',
                        fontSize: '11px',
                        pointerEvents: 'none',
                    }}>
                        <i className="fas fa-lock"></i> Locked
                    </div>
                )}

                {renderComponentContent(type, componentData, styles, children)}
            </div>
        );
    }
);
