import React, { useState, useMemo, useCallback, useEffect, useRef } from 'react';
import { toast } from 'react-toastify';
import { EXTENDED_LIBRARY } from '../../constants/ExtendedLibrary';
import '../../styles/ComponentLibrary.css';

// Ãnh xáº¡ icon cho cÃ¡c tab
const TAB_ICONS = {
    elements: 'fa-cube',
    assets: 'fa-image',
    sections: 'fa-layer-group',
    popups: 'fa-window-restore',
    dropbox: 'fa-dropbox',
    products: 'fa-shopping-cart',
    blog: 'fa-blog',
    utilities: 'fa-tools',
    content: 'fa-file-alt',
    media: 'fa-photo-video',
    documents: 'fa-file-pdf',
    fonts: 'fa-font',
};

// HÃ m táº¡o section chuáº©n vá»›i cáº¥u trÃºc ladi-section
const createStandardSection = (sectionData, yPosition = 0) => {
    return {
        id: `SECTION${Date.now()}`,
        type: 'section',
        componentData: {
            ...sectionData.json.componentData,
            className: 'ladi-section',
            structure: 'ladi-standard',
        },
        position: {
            desktop: { x: 0, y: yPosition },
            tablet: { x: 0, y: yPosition },
            mobile: { x: 0, y: yPosition },
        },
        size: {
            width: 420,
            height: sectionData.json.size?.height || 574,
        },
        styles: {
            width: '420px',
            minWidth: '420px',
            height: `${sectionData.json.size?.height || 574}px`,
            margin: '0 auto',
            position: 'relative',
            backgroundColor: '#ffffff',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            overflow: 'hidden',
            ...sectionData.json.styles,
        },
        children: sectionData.json.children || [],
        isSection: true,
    };
};

// HÃ m tÃ­nh toÃ¡n vá»‹ trÃ­ Y cho section má»›i
const calculateNextSectionPosition = (existingElements) => {
    const sections = existingElements.filter((el) => el.isSection || el.type === 'section');
    if (sections.length === 0) return 0;

    const lastSection = sections.reduce((latest, section) => {
        const sectionY = section.position?.desktop?.y || 0;
        const latestY = latest.position?.desktop?.y || 0;
        return sectionY > latestY ? section : latest;
    }, sections[0]);

    return (lastSection.position?.desktop?.y || 0) + (lastSection.size?.height || 574) + 20; // 20px gap
};

// HÃ m hiá»ƒn thá»‹ thÃ´ng tin dataSource vÃ  events trong tooltip
const getTooltipContent = (item) => {
    const { dataSource, events } = item.json.componentData || {};
    let tooltip = item.description || 'No description';
    tooltip += '<br /><small><i className="fas fa-mouse-pointer"></i> Click Ä‘á»ƒ thÃªm vÃ o section</small>';

    if (dataSource?.type) {
        tooltip += `<br /><small><strong>Data Source:</strong> ${dataSource.type}`;
        if (dataSource.type === 'api' && dataSource.url) {
            tooltip += ` (URL: ${dataSource.url}, Key: ${dataSource.key || 'N/A'})`;
        } else if (dataSource.type === 'state' && dataSource.stateKey) {
            tooltip += ` (State Key: ${dataSource.stateKey})`;
        }
        tooltip += '</small>';
    }

    if (events) {
        Object.entries(events).forEach(([eventType, eventConfig]) => {
            tooltip += `<br /><small><strong>${eventType}:</strong> ${eventConfig.type}`;
            if (eventConfig.url) tooltip += ` (URL: ${eventConfig.url})`;
            if (eventConfig.apiUrl) tooltip += ` (API: ${eventConfig.apiUrl})`;
            if (eventConfig.popupId) tooltip += ` (Popup ID: ${eventConfig.popupId})`;
            tooltip += '</small>';
        });
    }

    return tooltip;
};

// HÃ m upload áº£nh lÃªn Cloudinary
const uploadImage = async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('upload_preset', 'your_upload_preset'); // Thay báº±ng upload preset cá»§a báº¡n

    const response = await fetch(`https://api.cloudinary.com/v1_1/your_cloud_name/image/upload`, {
        method: 'POST',
        body: formData,
    });

    if (!response.ok) {
        throw new Error('Upload failed');
    }

    const data = await response.json();
    return data.secure_url;
};

const ComponentLibrary = ({ isCollapsed, onToggle, onAddElement, onAddChild, pageData, selectedSection }) => {
    const [activeTab, setActiveTab] = useState('elements');
    const [searchTerm, setSearchTerm] = useState('');
    const [activeCategory, setActiveCategory] = useState(null);
    const [activeSubcategory, setActiveSubcategory] = useState(null);
    const [assets, setAssets] = useState([]);
    const templateListRef = useRef(null);

    const categories = useMemo(() => {
        return {
            elements: EXTENDED_LIBRARY.elements?.subCategories || [],
            assets: EXTENDED_LIBRARY.assets?.subCategories || [],
            sections: EXTENDED_LIBRARY.sections?.subCategories || [],
            popups: EXTENDED_LIBRARY.popups?.subCategories || [],
            dropbox: EXTENDED_LIBRARY.dropbox?.subCategories || [],
            products: EXTENDED_LIBRARY.products?.subCategories || [],
            blog: EXTENDED_LIBRARY.blog?.subCategories || [],
            utilities: EXTENDED_LIBRARY.utilities?.subCategories || [],
            content: EXTENDED_LIBRARY.content?.subCategories || [],
            media: EXTENDED_LIBRARY.media?.subCategories || [],
            documents: EXTENDED_LIBRARY.documents?.subCategories || [],
            fonts: EXTENDED_LIBRARY.fonts?.subCategories || [],
        };
    }, []);

    const filteredItems = useMemo(() => {
        if (activeTab === 'assets') {
            if (!searchTerm) return assets;
            return assets.filter((item) =>
                item.name.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        const currentCategory = categories[activeTab] || [];
        let items = [];
        if (activeSubcategory) {
            const subcategory = currentCategory.find((cat) => cat.id === activeSubcategory);
            items = subcategory ? subcategory.templates : [];
        } else if (activeCategory) {
            const category = currentCategory.find((cat) => cat.id === activeCategory);
            items = category ? category.templates : [];
        } else {
            items = currentCategory.flatMap((cat) => cat.templates || []);
        }
        if (!searchTerm) return items;
        return items.filter((item) =>
            item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            item.json.componentData?.dataSource?.type?.toLowerCase().includes(searchTerm.toLowerCase())
        );
    }, [activeTab, activeCategory, activeSubcategory, searchTerm, categories, assets]);

    // Xá»­ lÃ½ upload áº£nh khi tháº£ vÃ o tab Assets
    useEffect(() => {
        const list = templateListRef.current;
        if (!list || activeTab !== 'assets') return;

        const handleDragOver = (e) => e.preventDefault();

        const handleDrop = async (e) => {
            e.preventDefault();
            const files = Array.from(e.dataTransfer.files).filter((f) => f.type.startsWith('image/') || f.type.startsWith('video/'));
            for (const file of files) {
                try {
                    const url = await uploadImage(file);
                    const newAsset = {
                        id: `asset-${Date.now()}`,
                        name: file.name,
                        previewImage: url,
                        icon: file.type.startsWith('video/') ? 'fa-video' : 'fa-image',
                        json: {
                            type: file.type.startsWith('video/') ? 'video' : 'image',
                            componentData: {
                                src: url,
                                alt: file.name,
                                dataSource: { type: 'static' },
                                events: {},
                            },
                            size: { width: 200, height: 200 },
                            styles: { maxWidth: '100%' },
                        },
                    };
                    setAssets((prev) => [...prev, newAsset]);
                    toast.success(`ÄÃ£ upload "${file.name}" thÃ nh cÃ´ng!`);
                } catch (error) {
                    toast.error(`Lá»—i upload "${file.name}": ${error.message}`);
                }
            }
        };

        list.addEventListener('dragover', handleDragOver);
        list.addEventListener('drop', handleDrop);

        return () => {
            list.removeEventListener('dragover', handleDragOver);
            list.removeEventListener('drop', handleDrop);
        };
    }, [activeTab]);

    // Component cho táº¥t cáº£ cÃ¡c thÃ nh pháº§n (chá»‰ click)
    const ClickableComponentItem = ({ item }) => {
        const [isClicking, setIsClicking] = useState(false);

        const handleClick = useCallback(async () => {
            setIsClicking(true);

            try {
                if (activeTab === 'sections') {
                    const nextYPosition = calculateNextSectionPosition(pageData?.elements || []);
                    const newSection = createStandardSection(item, nextYPosition);
                    await onAddElement(newSection);
                    toast.success(`âœ¨ ÄÃ£ thÃªm section "${item.name}" vÃ o cuá»‘i trang!`, {
                        position: 'bottom-right',
                        autoClose: 2000,
                    });
                } else if (activeTab === 'popups' || item.json.type === 'popup') {
                    const centerX = Math.max(0, (pageData?.canvas?.width || 1200) / 2 - (item.json.size?.width || 200) / 2);
                    const centerY = 100;

                    const newElement = {
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: {
                            ...item.json.componentData,
                            dataSource: item.json.componentData?.dataSource || { type: 'static' },
                            events: item.json.componentData?.events || {},
                        },
                        position: {
                            desktop: { x: centerX, y: centerY },
                            tablet: { x: centerX, y: centerY },
                            mobile: { x: centerX, y: centerY },
                        },
                        size: item.json.size || { width: 300, height: 200 },
                        styles: {
                            borderRadius: '8px',
                            boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                            backgroundColor: '#ffffff',
                            ...item.json.styles,
                        },
                        children: item.json.children || [],
                    };
                    await onAddElement(newElement);
                    toast.success(`ðŸŽ¯ ÄÃ£ thÃªm "${item.name}" vÃ o trang!`, {
                        position: 'bottom-right',
                        autoClose: 2000,
                    });
                } else {
                    // Kiá»ƒm tra xem cÃ³ section Ä‘Æ°á»£c chá»n khÃ´ng
                    if (!selectedSection) {
                        toast.error('Vui lÃ²ng chá»n má»™t section trÆ°á»›c khi thÃªm pháº§n tá»­!', {
                            position: 'bottom-right',
                            autoClose: 3000,
                        });
                        return;
                    }

                    const newElement = {
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: {
                            ...item.json.componentData,
                            dataSource: item.json.componentData?.dataSource || { type: 'static' },
                            events: item.json.componentData?.events || {},
                        },
                        position: {
                            desktop: { x: 20, y: 20 },
                            tablet: { x: 20, y: 20 },
                            mobile: { x: 20, y: 20 },
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                    };
                    await onAddChild(selectedSection, newElement);
                    toast.success(`ðŸŽ¯ ÄÃ£ thÃªm "${item.name}" vÃ o section!`, {
                        position: 'bottom-right',
                        autoClose: 2000,
                    });
                }
            } catch (error) {
                toast.error(`âŒ Lá»—i khi thÃªm ${item.name}: ${error.message}`);
            } finally {
                setTimeout(() => setIsClicking(false), 200);
            }
        }, [item, activeTab, pageData, onAddElement, onAddChild, selectedSection]);

        return (
            <div
                className={`lpb-template-item lpb-template-item-clickable ${isClicking ? 'lpb-clicking' : ''}`}
                onClick={handleClick}
            >
                {item.previewImage ? (
                    <img src={item.previewImage} alt={item.name} className="lpb-template-preview" />
                ) : (
                    <i className={`fas ${item.icon || 'fa-cube'}`}></i>
                )}
                <span>{item.name}</span>
                <div
                    className="lpb-template-tooltip"
                    dangerouslySetInnerHTML={{ __html: getTooltipContent(item) }}
                />
            </div>
        );
    };

    const handleTabClick = useCallback((tab) => {
        setActiveTab(tab);
        setActiveCategory(null);
        setActiveSubcategory(null);
        setSearchTerm('');
        if (isCollapsed) {
            onToggle();
        }
    }, [isCollapsed, onToggle]);

    const handleCategoryClick = useCallback((categoryId) => {
        setActiveCategory(activeCategory === categoryId ? null : categoryId);
        setActiveSubcategory(null);
    }, [activeCategory]);

    const handleSubcategoryClick = useCallback((subcategoryId) => {
        setActiveSubcategory(activeSubcategory === subcategoryId ? null : subcategoryId);
    }, [activeSubcategory]);

    return (
        <div className={`lpb-sidebar-left ${isCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
            <div className="lpb-component-library-header">
                <h2 className="lpb-component-library-title">
                    {isCollapsed ? <i className="" /> : 'ThÆ° viá»‡n thÃ nh pháº§n'}
                </h2>
                <button onClick={onToggle} className="lpb-component-library-toggle">
                    <i className={`fas fa-chevron-${isCollapsed ? 'right' : 'left'}`}></i>
                </button>
            </div>
            <div className="lpb-component-library">
                <div className="lpb-component-library-tabs">
                    {['elements', 'assets', 'sections', 'popups', 'dropbox', 'products', 'blog', 'utilities', 'content', 'media', 'documents', 'fonts'].map((tab) => (
                        <button
                            key={tab}
                            className={`lpb-component-library-tab ${activeTab === tab ? 'active' : ''} ${isCollapsed ? 'icon-only' : ''}`}
                            onClick={() => handleTabClick(tab)}
                            title={isCollapsed ? (EXTENDED_LIBRARY[tab]?.name || tab.charAt(0).toUpperCase() + tab.slice(1)) : ''}
                        >
                            {isCollapsed ? (
                                <i className={`fas ${TAB_ICONS[tab]}`} />
                            ) : (
                                EXTENDED_LIBRARY[tab]?.name || tab.charAt(0).toUpperCase() + tab.slice(1)
                            )}
                        </button>
                    ))}
                </div>
                {!isCollapsed && (
                    <div className="lpb-component-library-wrapper">
                        <div>
                            {Array.isArray(categories[activeTab]) && categories[activeTab].length > 0 ? (
                                categories[activeTab].map((category) => (
                                    <div key={category.id} className="lpb-component-library-category-nav">
                                        <button
                                            className={`lpb-component-library-category-btn ${activeCategory === category.id ? 'lpb-component-library-category-btn-active' : ''}`}
                                            onClick={() => handleCategoryClick(category.id)}
                                        >
                                            <i className={`fas ${TAB_ICONS[activeTab]} lpb-category-icon`}></i>
                                            {category.name}
                                            <span className="lpb-template-count">({category.templates?.length || 0})</span>
                                        </button>
                                        {activeCategory === category.id && Array.isArray(category.subCategories) && (
                                            <div className="lpb-component-library-subcategory-nav">
                                                {category.subCategories.map((subcategory) => (
                                                    <button
                                                        key={subcategory.id}
                                                        className={`lpb-component-library-subcategory-btn ${activeSubcategory === subcategory.id ? 'lpb-component-library-subcategory-btn-active' : ''}`}
                                                        onClick={() => handleSubcategoryClick(subcategory.id)}
                                                    >
                                                        {subcategory.name}
                                                        <span className="lpb-template-count">({subcategory.templates?.length || 0})</span>
                                                    </button>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                ))
                            ) : (
                                <div className="lpb-no-results">
                                    <i className="fas fa-exclamation-circle"></i>
                                    <p>KhÃ´ng cÃ³ danh má»¥c nÃ o</p>
                                    <small>Vui lÃ²ng kiá»ƒm tra cáº¥u hÃ¬nh thÆ° viá»‡n</small>
                                </div>
                            )}
                        </div>
                        <div className="lpb-component-library-template-column">
                            <div className="lpb-component-library-search">
                                <div className="lpb-search-wrapper">
                                    <i className="fas fa-search lpb-search-icon"></i>
                                    <input
                                        type="text"
                                        placeholder="TÃ¬m kiáº¿m thÃ nh pháº§n hoáº·c nguá»“n dá»¯ liá»‡u..."
                                        value={searchTerm}
                                        onChange={(e) => setSearchTerm(e.target.value)}
                                    />
                                    {searchTerm && (
                                        <button
                                            className="lpb-clear-search"
                                            onClick={() => setSearchTerm('')}
                                        >
                                            <i className="fas fa-times"></i>
                                        </button>
                                    )}
                                </div>
                            </div>
                            <div className="lpb-interaction-hint">
                                {activeTab === 'assets' ? (
                                    <>
                                        <i className="fas fa-hand-rock"></i>
                                        <span>Tháº£ áº£nh hoáº·c video vÃ o Ä‘Ã¢y Ä‘á»ƒ upload</span>
                                    </>
                                ) : (
                                    <>
                                        <i className="fas fa-mouse-pointer"></i>
                                        <span>Click Ä‘á»ƒ thÃªm vÃ o {activeTab === 'sections' || activeTab === 'popups' ? 'trang' : 'section'}</span>
                                    </>
                                )}
                            </div>
                            <div className="lpb-component-library-template-list" ref={templateListRef}>
                                {filteredItems.length > 0 ? (
                                    filteredItems.map((item) => (
                                        <ClickableComponentItem key={item.id} item={item} />
                                    ))
                                ) : (
                                    <div className="lpb-no-results">
                                        <i className="fas fa-search"></i>
                                        <p>KhÃ´ng tÃ¬m tháº¥y thÃ nh pháº§n nÃ o</p>
                                        <small>Thá»­ tá»« khÃ³a khÃ¡c hoáº·c chá»n danh má»¥c khÃ¡c</small>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ComponentLibrary;
