import React from 'react';
import './CreateModal.css';

const CreateModal = ({ showCreateModal, setShowCreateModal, pageDescription, setPageDescription, handleCreatePage }) => {
    if (!showCreateModal) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Tạo Landing Page Mới</h2>
                <textarea
                    value={pageDescription}
                    onChange={(e) => setPageDescription(e.target.value)}
                    placeholder="Nhập mô tả (ví dụ: Trang bán căn hộ Quận 7, màu xanh, có form liên hệ)"
                    rows="5"
                    className="description-input"
                />
                <div className="modal-actions">
                    <button onClick={handleCreatePage} className="create-btn">Tạo</button>
                    <button onClick={() => setShowCreateModal(false)} className="cancel-btn">Hủy</button>
                </div>
            </div>
        </div>
    );
};

export default CreateModal;
