import React from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useSearchParams } from 'react-router-dom';
import Canvas from './create-page/Canvas';
import ComponentLibrary from './create-page/ComponentLibrary';
import PropertiesPanel from './create-page/PropertiesPanel';
import Toolbar from './create-page/Toolbar';
import '../styles/CreateLanding.css';
import { ErrorBoundary } from './create-page/helpersPr';
import { usePageState, useViewControls, useApiHandlers, useSidebarControls } from './create-page/hooks';

const CreateLanding = () => {
    const [searchParams] = useSearchParams();
    const pageId = searchParams.get('id');
    const navigate = () => {}; // Placeholder, assuming navigate is passed or handled in hooks

    const {
        pageData,
        selectedIds,
        selectedChildId,
        isLoading,
        handleSelectElement,
        handleSelectChild,
        handleAddElement,
        handleAddChild,
        handleUpdatePosition,
        handleUpdateSize,
        handleDeleteElement,
        handleEditElement,
        handleDuplicateElement,
        handleMoveElementUp,
        handleMoveElementDown,
        handleGroupElements,
        handleUpdateChildPosition,
        handleMoveChild,
        handleUpdateChildren,
        handleUndo,
        handleRedo,
        handleUpdateCanvasHeight,
    } = usePageState(pageId, navigate);

    const {
        viewMode,
        zoomLevel,
        gridSize,
        showGrid,
        guideLine,
        handleViewModeChange,
        handleShowAddSectionGuide,
    } = useViewControls(pageData, usePageState.history, usePageState.historyIndex, usePageState.setPageData, usePageState.setHistory, usePageState.setHistoryIndex);

    const {
        handleSave,
        handlePreview,
        handleGenerateCode,
    } = useApiHandlers(pageId, navigate, pageData);

    const {
        isLibraryCollapsed,
        isPropertiesCollapsed,
        handleToggleLibrary,
        setIsPropertiesCollapsed,
    } = useSidebarControls();

    const selectedElement = (() => {
        if (selectedChildId) {
            const parent = pageData.elements.find((el) => el.id === selectedIds[0]);
            if (!parent || !parent.children) return null;
            const child = parent.children.find((child) => child.id === selectedChildId);
            return child ? { json: child, isChild: true } : null;
        }
        const element = pageData.elements.find((el) => el.id === selectedIds[0]);
        return element ? { json: element, isChild: false } : null;
    })();

    if (isLoading) {
        return (
            <div className="lpb-loading">
                <div className="lpb-loading-spinner"></div>
                <p>Đang tải trang...</p>
            </div>
        );
    }

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container flex h-screen">
                <ErrorBoundary>
                    <link
                        rel="stylesheet"
                        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                        crossOrigin="anonymous"
                        referrerPolicy="no-referrer"
                    />
                    <ComponentLibrary
                        isCollapsed={isLibraryCollapsed}
                        onToggle={handleToggleLibrary}
                        onAddElement={handleAddElement}
                        onAddChild={handleAddChild}
                        pageData={pageData}
                    />
                </ErrorBoundary>
                <div className="lpb-main-content flex-1 flex flex-col">
                    <ErrorBoundary>
                        <Toolbar
                            onSave={handleSave}
                            onPreview={handlePreview}
                            onGenerateCode={handleGenerateCode}
                            viewMode={viewMode}
                            onViewModeChange={handleViewModeChange}
                            onUndo={handleUndo}
                            onRedo={handleRedo}
                            canUndo={usePageState.historyIndex >= 0}
                            canRedo={usePageState.historyIndex < usePageState.history.length - 1}
                            zoomLevel={zoomLevel}
                            onZoom={(direction) => useViewControls.setZoomLevel(direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10))}
                            onToggleGrid={() => useViewControls.setShowGrid(!showGrid)}
                            showGrid={showGrid}
                            gridSize={gridSize}
                            pageData={pageData}
                            onSetGridSize={useViewControls.setGridSize}
                            selectedElement={selectedElement}
                            onShowAddSectionGuide={handleShowAddSectionGuide}
                        />
                    </ErrorBoundary>
                    <ErrorBoundary>
                        <Canvas
                            pageData={pageData}
                            selectedIds={selectedIds}
                            onSelectElement={handleSelectElement}
                            onUpdatePosition={handleUpdatePosition}
                            onUpdateSize={handleUpdateSize}
                            onDeleteElement={handleDeleteElement}
                            onAddElement={handleAddElement}
                            onGroupElements={handleGroupElements}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={gridSize}
                            showGrid={showGrid}
                            onEditElement={handleEditElement}
                            onDuplicateElement={handleDuplicateElement}
                            onMoveElementUp={handleMoveElementUp}
                            onMoveElementDown={handleMoveElementDown}
                            onSelectChild={handleSelectChild}
                            selectedChildId={selectedChildId}
                            onUpdateChildren={handleUpdateChildren}
                            onAddChild={handleAddChild}
                            onUpdateChildPosition={handleUpdateChildPosition}
                            onMoveChild={handleMoveChild}
                            guideLine={guideLine}
                            onUpdateCanvasHeight={handleUpdateCanvasHeight}
                        />
                    </ErrorBoundary>
                </div>
                <ErrorBoundary>
                    <div className="lpb-sidebar-right">
                        <PropertiesPanel
                            selectedElement={selectedElement}
                            onUpdateElement={handleEditElement}
                            onUpdateCanvas={usePageState.handleUpdateCanvas}
                            isCollapsed={isPropertiesCollapsed}
                            onToggle={() => setIsPropertiesCollapsed(!isPropertiesCollapsed)}
                            pageData={pageData}
                        />
                    </div>
                </ErrorBoundary>
            </div>
        </DndProvider>
    );
};

export default CreateLanding;
