import {useRef} from "react";
import React, { useState, useCallback } from 'react';

import QuickEditToolkit from "../pages-content/QuickEditToolkit  ";
const Canvas = ({
                    pageStructure,
                    onDrop,
                    selectedElement,
                    onSelectElement,
                    quickEditElement,
                    quickEditPosition,
                    onQuickEdit,
                    onCloseQuickEdit,
                    onDuplicateElement,
                    onDeleteElement,
                    onMoveElement,
                    viewMode
                }) => {
    const canvasRef = useRef(null);

    const handleDrop = (e) => {
        e.preventDefault();
        const componentData = JSON.parse(e.dataTransfer.getData('component'));
        const rect = canvasRef.current.getBoundingClientRect();
        const position = {
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
        };
        onDrop(componentData, position);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleElementHover = (e, component) => {
        if (quickEditElement?.id === component.id) return;

        const rect = e.currentTarget.getBoundingClientRect();
        const canvasRect = canvasRef.current.getBoundingClientRect();

        onQuickEdit(component, {
            x: rect.left - canvasRect.left,
            y: rect.top - canvasRect.top
        });
    };

    const renderComponent = (component, index) => {
        const isSelected = selectedElement?.id === component.id;
        const hasQuickEdit = quickEditElement?.id === component.id;

        const baseClasses = `relative transition-all duration-200 ${
            isSelected ? 'ring-2 ring-blue-500' : ''
        } ${hasQuickEdit ? 'shadow-lg' : ''}`;

        switch (component.json.type) {
            case 'heading':
                const HeadingTag = component.json.level === 2 ? 'h2' : 'h1';
                return (
                    <div
                        key={component.id || index}
                        className={baseClasses}
                        onClick={() => onSelectElement(component)}
                        onMouseEnter={(e) => handleElementHover(e, component)}
                        style={component.json.styles}
                    >
                        <HeadingTag>{component.json.content}</HeadingTag>
                        {isSelected && (
                            <div className="absolute -top-6 left-0 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                {component.json.level === 2 ? 'Heading 2' : 'Tiêu đề H1'}
                            </div>
                        )}
                    </div>
                );

            case 'paragraph':
                return (
                    <div
                        key={component.id || index}
                        className={baseClasses}
                        onClick={() => onSelectElement(component)}
                        onMouseEnter={(e) => handleElementHover(e, component)}
                        style={component.json.styles}
                    >
                        <p>{component.json.content}</p>
                        {isSelected && (
                            <div className="absolute -top-6 left-0 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                Đoạn văn
                            </div>
                        )}
                    </div>
                );

            case 'button':
                return (
                    <div
                        key={component.id || index}
                        className={`${baseClasses} inline-block`}
                        onClick={() => onSelectElement(component)}
                        onMouseEnter={(e) => handleElementHover(e, component)}
                    >
                        <button style={component.json.styles}>
                            {component.json.content}
                        </button>
                        {isSelected && (
                            <div className="absolute -top-6 left-0 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                Nút bấm
                            </div>
                        )}
                    </div>
                );

            case 'section':
                return (
                    <section
                        key={component.id || index}
                        className={baseClasses}
                        onClick={() => onSelectElement(component)}
                        onMouseEnter={(e) => handleElementHover(e, component)}
                        style={{
                            ...component.json.styles,
                            background: component.json.background?.value
                        }}
                    >
                        {component.json.layout === 'hero' && (
                            <div className="container mx-auto">
                                <h1 className="text-4xl font-bold mb-4 text-white">{component.json.content.title}</h1>
                                <p className="text-xl mb-8 text-white">{component.json.content.subtitle}</p>
                                <div className="flex gap-4 justify-center">
                                    {component.json.content.buttons?.map((btn, idx) => (
                                        <button
                                            key={idx}
                                            className={`px-6 py-3 rounded-lg font-semibold ${
                                                btn.type === 'primary'
                                                    ? 'bg-white text-blue-600'
                                                    : 'border-2 border-white text-white bg-transparent'
                                            }`}
                                        >
                                            {btn.text}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        )}
                        {isSelected && (
                            <div className="absolute top-2 left-2 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                Hero Section
                            </div>
                        )}
                    </section>
                );

            default:
                return null;
        }
    };

    const getCanvasClasses = () => {
        const baseClasses = "bg-white overflow-auto transition-all duration-300";

        switch (viewMode) {
            case 'mobile':
                return `${baseClasses} max-w-sm mx-auto border-x-2 border-gray-300`;
            case 'tablet':
                return `${baseClasses} max-w-2xl mx-auto border-x-2 border-gray-300`;
            default:
                return `${baseClasses} w-full`;
        }
    };

    return (
        <div className="flex-1 bg-gray-100 p-4">
            <div
                ref={canvasRef}
                className={getCanvasClasses()}
                onDrop={handleDrop}
                onDragOver={handleDragOver}
                onMouseLeave={onCloseQuickEdit}
            >
                <div className="min-h-full">
                    {pageStructure.length === 0 ? (
                        <div className="h-full flex items-center justify-center text-gray-400 min-h-96">
                            <div className="text-center">
                                <i className="fas fa-mouse-pointer text-6xl mb-4"></i>
                                <p className="text-xl">Kéo thả component vào đây để bắt đầu</p>
                            </div>
                        </div>
                    ) : (
                        pageStructure.map((component, index) => renderComponent(component, index))
                    )}
                </div>

                <QuickEditToolkit
                    element={quickEditElement}
                    position={quickEditPosition}
                    onEdit={() => onSelectElement(quickEditElement)}
                    onDuplicate={() => onDuplicateElement(quickEditElement)}
                    onDelete={() => onDeleteElement(quickEditElement)}
                    onMoveUp={() => onMoveElement(quickEditElement, 'up')}
                    onMoveDown={() => onMoveElement(quickEditElement, 'down')}
                    onClose={onCloseQuickEdit}
                />
            </div>
        </div>
    );
};
export default Canvas;
