export const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

export const renderStaticHTML = (pageData) => {
    console.log('Rendering static HTML with pageData:', JSON.stringify(pageData, null, 2));

    // Default canvas properties
    const canvasWidth = pageData.canvas?.width || '1200px';
    const canvasBackground = pageData.canvas?.background || '#ffffff';

    let html = `
<!DOCTYPE html>
<html lang="vi">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Landing Page</title>
    <style>
        :root {
            --primary: ${CSS_VARIABLES['--primary']};
            --primary-dark: ${CSS_VARIABLES['--primary-dark']};
            --primary-light: ${CSS_VARIABLES['--primary-light']};
            --secondary: ${CSS_VARIABLES['--secondary']};
            --accent: ${CSS_VARIABLES['--accent']};
            --success: ${CSS_VARIABLES['--success']};
            --warning: ${CSS_VARIABLES['--warning']};
            --danger: ${CSS_VARIABLES['--danger']};
            --dark: ${CSS_VARIABLES['--dark']};
            --light: ${CSS_VARIABLES['--light']};
            --white: ${CSS_VARIABLES['--white']};
            --text-primary: ${CSS_VARIABLES['--text-primary']};
            --text-secondary: ${CSS_VARIABLES['--text-secondary']};
            --text-light: ${CSS_VARIABLES['--text-light']};
            --text-inverse: ${CSS_VARIABLES['--text-inverse']};
            --border-color: ${CSS_VARIABLES['--border-color']};
            --border-radius: ${CSS_VARIABLES['--border-radius']};
            --shadow: ${CSS_VARIABLES['--shadow']};
        }
        body {
            font-family: 'Inter', Arial, sans-serif;
            margin: 0;
            padding: 0;
            background: ${canvasBackground};
        }
        .page-container {
            position: relative;
            width: ${canvasWidth};
            min-height: 100vh;
            margin: 0 auto;
            background: ${canvasBackground};
        }
        .ladi-section {
            width: 100%;
            max-width: 1200px;
            margin: 0 auto;
            position: relative;
            overflow: hidden;
        }
        .ladi-section-background {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 0;
        }
        .ladi-overlay {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 1;
            pointer-events: none;
        }
        .ladi-container {
            position: relative;
            z-index: 2;
            padding: 20px;
            min-height: 100%;
            box-sizing: border-box;
        }
        .lpb-canvas-element {
            position: absolute;
        }
        .lpb-child-element {
            position: absolute;
        }
        @media (max-width: 768px) {
            .page-container {
                width: 100%;
            }
            .ladi-section {
                width: 100%;
                min-width: auto;
            }
            .lpb-child-element:not([style*="position: absolute"]) {
                width: 100% !important;
            }
            .lpb-child-element {
                left: 0 !important;
                width: 100% !important;
            }
        }
        @media (max-width: 480px) {
            .lpb-child-element {
                left: 0 !important;
                width: 100% !important;
            }
        }
        /* Styles for new components */
        .ladi-card, .ladi-product, .ladi-testimonial, .ladi-box {
            padding: 16px;
            box-sizing: border-box;
        }
        .ladi-menu {
            display: flex;
            gap: 20px;
            list-style: none;
            padding: 0;
        }
        .ladi-menu a {
            color: inherit;
            text-decoration: none;
        }
        .ladi-grid {
            display: grid;
        }
        .ladi-list {
            list-style: none;
            padding: 0;
        }
        .ladi-video {
            max-width: 100%;
        }
        .ladi-icon {
            display: inline-block;
        }
        .ladi-gallery {
            display: grid;
            gap: 10px;
        }
        .ladi-carousel {
            overflow: hidden;
            position: relative;
        }
        .ladi-tabs {
            display: flex;
            flex-direction: column;
        }
        .ladi-tabs-nav {
            display: flex;
            gap: 10px;
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-tab {
            padding: 8px 16px;
            cursor: pointer;
        }
        .ladi-tab-content {
            padding: 16px;
        }
        .ladi-accordion-item {
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-accordion-title {
            padding: 8px;
            cursor: pointer;
        }
        .ladi-table {
            width: 100%;
            border-collapse: collapse;
        }
        .ladi-table th, .ladi-table td {
            border: 1px solid var(--border-color);
            padding: 8px;
            text-align: left;
        }
        .ladi-survey {
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .ladi-html {
            overflow: auto;
        }
        .ladi-collection {
            display: flex;
            flex-direction: column;
            gap: 20px;
        }
    </style>
</head>
<body>
    <div class="page-container">
    `;

    const renderElement = (element, isChild = false, parentType = '') => {
        const { id, type, componentData = {}, position = {}, size = {}, styles = {}, children = [] } = element;

        // Normalize styles and position
        const positionStyles = position.desktop ? `left: ${position.desktop.x || 0}px; top: ${position.desktop.y || 0}px;` : '';
        const sizeStyles = size ? `width: ${size.width || 200}px; height: ${size.height || 50}px;` : '';
        const cssStyles = styles ? Object.entries(styles).filter(([key]) => key !== 'position').map(([key, value]) => `${key}: ${value};`).join(' ') : '';
        const elementClass = isChild ? `lpb-child-element ladi-${type}` : `lpb-canvas-element ladi-${type}`;
        const elementPosition = isChild ? `position: absolute; ${positionStyles}` : (position.desktop ? `position: absolute; transform: translate(${position.desktop.x || 0}px, ${position.desktop.y || 0}px);` : '');

        let elementHtml = `<div id="${id}" class="${elementClass}" style="${elementPosition} ${sizeStyles} ${cssStyles}" data-type="${type}">`;

        switch (type) {
            case 'section':
                if (componentData.structure === 'ladi-standard') {
                    elementHtml += `
                        <div class="ladi-section" style="width: 100%; height: 100%; position: relative;">
                            <div class="ladi-section-background" style="background-color: ${componentData.backgroundColor || 'transparent'}; background-image: ${componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none'}; background-size: cover; background-position: center;"></div>
                            <div class="ladi-overlay" style="background-color: ${componentData.overlayColor || 'transparent'}; opacity: ${componentData.overlayOpacity || 0};"></div>
                            <div class="ladi-container" style="padding: ${componentData.padding || '20px'}; width: 100%; height: 100%; position: relative;">
                    `;
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div></div>`;
                } else {
                    elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '0'}; background: ${componentData.background || 'transparent'}; ${cssStyles}">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                }
                break;
            case 'container':
            case 'box':
                elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'transparent'}; ${cssStyles}">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'heading':
                const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                    ? componentData.level
                    : 'h2';
                elementHtml += `<${headingLevel} style="font-size: ${componentData.fontSize || '1.5rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '700'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Tiêu đề'}</${headingLevel}>`;
                break;
            case 'paragraph':
                elementHtml += `<p style="font-size: ${componentData.fontSize || '1rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '400'}; line-height: ${componentData.lineHeight || '1.6'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Đoạn văn'}</p>`;
                break;
            case 'button':
                elementHtml += `<button style="padding: ${componentData.padding || '8px 16px'}; border-radius: ${componentData.borderRadius || '8px'}; background: ${componentData.background || '#2563eb'}; color: ${componentData.color || '#fff'}; border: ${componentData.border || 'none'}; cursor: pointer; font-size: ${componentData.fontSize || '1rem'}; font-weight: ${componentData.fontWeight || '600'}; text-align: ${componentData.textAlign || 'center'}; ${cssStyles}">${componentData.content || 'Nút'}</button>`;
                break;
            case 'image':
                elementHtml += `<img src="${componentData.src || 'https://via.placeholder.com/150?text=Image'}" alt="${componentData.alt || 'Hình ảnh'}" style="max-width: ${componentData.maxWidth || '100%'}; height: ${componentData.height || 'auto'}; border-radius: ${componentData.borderRadius || '0'}; ${cssStyles}" />`;
                break;
            case 'form':
                elementHtml += `<form style="display: flex; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; ${cssStyles}">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h3 style="margin: ${componentData.titleMargin || '0'}; font-size: ${componentData.titleFontSize || '1.2rem'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (Array.isArray(componentData.fields) && componentData.fields.length > 0) {
                    componentData.fields.forEach((field) => {
                        elementHtml += `<input type="${field.type || 'text'}" placeholder="${field.placeholder || field.label || 'Nhập...'}" style="padding: ${field.padding || '8px'}; border-radius: ${field.borderRadius || '8px'}; border: ${field.border || '1px solid #ccc'}; font-size: ${field.fontSize || '1rem'}; width: 100%; box-sizing: border-box; ${cssStyles}" />`;
                    });
                }
                if (componentData.buttonText && !children.some(child => child?.type === 'button')) {
                    elementHtml += `<button type="submit" style="background: ${componentData.buttonBackground || '#2563eb'}; color: ${componentData.buttonColor || '#fff'}; padding: ${componentData.buttonPadding || '8px 16px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText || 'Gửi'}</button>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</form>`;
                break;
            case 'card':
                elementHtml += `<div class="ladi-card" style="padding: ${componentData.padding || '16px'}; background: ${componentData.background || '#ffffff'}; border-radius: ${componentData.borderRadius || '8px'}; box-shadow: ${componentData.boxShadow || '0 2px 8px rgba(0,0,0,0.1)'}; ${cssStyles}">`;
                if (componentData.title) {
                    elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.2rem'}; margin: ${componentData.titleMargin || '0 0 8px'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (componentData.content) {
                    elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'testimonial':
                elementHtml += `<div class="ladi-testimonial" style="padding: ${componentData.padding || '20px'}; background: ${componentData.background || '#f9fafb'}; border-radius: ${componentData.borderRadius || '8px'}; ${cssStyles}">`;
                if (componentData.quote) {
                    elementHtml += `<p style="font-size: ${componentData.quoteFontSize || '1rem'}; margin: ${componentData.quoteMargin || '0 0 8px'}; color: ${componentData.quoteColor || '#1f2937'}; font-style: italic;">"${componentData.quote}"</p>`;
                }
                if (componentData.author) {
