                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'gallery':
                elementHtml += `<div class="ladi-gallery" style="grid-template-columns: ${componentData.gridTemplateColumns || 'repeat(auto-fill, minmax(150px, 1fr))'}; gap: ${componentData.gap || '10px'}; ${cssStyles}">`;
                if (Array.isArray(componentData.images)) {
