                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'carousel':
                elementHtml += `<div class="ladi-carousel" style="${cssStyles}">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item) => {
                        elementHtml += `<div style="padding: ${componentData.itemPadding || '16px'}; background: ${componentData.itemBackground || '#ffffff'}; ${cssStyles}">`;
                        if (item.image) {
                            elementHtml += `<img src="${item.image}" alt="${item.title || 'Slide'}" style="max-width: 100%; height: auto; border-radius: ${componentData.imageBorderRadius || '8px'};" />`;
                        }
                        if (item.title) {
