                    elementHtml += `</tr></thead>`;
                }
                if (Array.isArray(componentData.rows)) {
                    elementHtml += `<tbody>`;
                    componentData.rows.forEach((row) => {
                        elementHtml += `<tr>`;
                        row.forEach((cell) => {
                            elementHtml += `<td style="padding: ${componentData.cellPadding || '8px'}; ${cssStyles}">${cell}</td>`;
