                    elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.2rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (Array.isArray(componentData.questions)) {
                    componentData.questions.forEach((question) => {
                        elementHtml += `<div style="margin: ${componentData.questionMargin || '8px 0'}; ${cssStyles}">`;
