import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import Canvas from '../components/create-page/Canvas';
import ComponentLibrary from '../components/create-page/ComponentLibrary';
import PropertiesPanel from '../components/create-page/PropertiesPanel';
import Toolbar from './create-page/Toolbar';
import api from '@landinghub/api';
import '../styles/CreateLanding.css';
import { useSearchParams } from 'react-router-dom';

// Hàm render HTML tĩnh từ pageData
const renderStaticHTML = (pageData) => {
    let html = `<!DOCTYPE html>
<html lang="vi">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Landing Page</title>
    <style>
        body { font-family: Arial, sans-serif; margin: 0; padding: 0; }
        .page-container { position: relative; width: ${pageData.canvas.width}px; min-height: 600px; margin: 0 auto; background: ${pageData.canvas.background}; }
    </style>
</head>
<body>
    <div class="page-container">
`;

    const renderElementRecursive = (element, isChild = false) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {}, children = [] } = element;
        const pos = position.desktop || { x: 0, y: 0 };
        const styleStr = `position: ${isChild ? 'relative' : 'absolute'}; left: ${pos.x}px; top: ${pos.y}px; width: ${size.width}px; height: ${size.height}px; ${Object.entries(styles).map(([k, v]) => `${k}: ${v};`).join(' ')}`;

        let content = '';
        switch (type) {
            case 'heading':
                content = `<h2 style="font-size: ${componentData.fontSize || '1.5rem'}; color: ${componentData.color || '#1f2937'}; margin: 0; padding: 0; ${styleStr}">${componentData.content || 'Tiêu đề'}</h2>`;
                break;
            case 'paragraph':
                content = `<p style="font-size: ${componentData.fontSize || '1rem'}; color: ${componentData.color || '#1f2937'}; margin: 0; padding: 0; ${styleStr}">${componentData.content || 'Đoạn văn'}</p>`;
                break;
            case 'button':
                content = `<button style="padding: 8px 16px; border-radius: 8px; background: ${componentData.background || '#2563eb'}; color: ${componentData.color || '#fff'}; border: none; cursor: pointer; ${styleStr}">${componentData.content || 'Nút'}</button>`;
                break;
            case 'image':
                content = `<img src="${componentData.src || 'https://via.placeholder.com/150'}" alt="${componentData.alt || 'Image'}" style="max-width: 100%; height: auto; ${styleStr}" />`;
                break;
            case 'form':
                content = `<form style="display: flex; flex-direction: column; gap: 10px; ${styleStr}">`;
                if (componentData.title && !children.some(child => child.type === 'heading')) {
                    content += `<h3 style="margin: 0; font-size: 1.2rem;">${componentData.title}</h3>`;
                }
                if (componentData.fields) {
                    content += componentData.fields.map((field, index) => `<input key="${index}" type="text" placeholder="${field}" style="padding: 8px; border-radius: 8px; border: 1px solid #ccc;" />`).join('');
                } else if (!children.some(child => child.type === 'input')) {
                    content += `<input type="text" placeholder="${componentData.placeholder || 'Input'}" style="padding: 8px; border-radius: 8px; border: 1px solid #ccc;" />`;
                }
                if (!children.some(child => child.type === 'button')) {
                    content += `<button type="submit" style="background: #2563eb; color: #fff; padding: 8px 16px; border-radius: 8px; border: none; cursor: pointer;">${componentData.buttonText || 'Submit'}</button>`;
                }
                children.forEach(child => {
                    content += renderElementRecursive(child, true);
                });
                content += `</form>`;
                break;
            case 'section':
            case 'container':
                content = `<div style="display: flex; flex-direction: column; gap: 10px; ${styleStr}">`;
                if (componentData.title && !children.some(child => child.type === 'heading')) {
                    content += `<h2 style="font-size: 1.8rem; margin: 0;">${componentData.title}</h2>`;
                }
                if (componentData.subtitle && !children.some(child => child.type === 'paragraph')) {
                    content += `<p style="font-size: 1.2rem; margin: 0;">${componentData.subtitle}</p>`;
                }
                if (componentData.content && !children.some(child => child.type === 'paragraph')) {
                    content += `<p style="font-size: 1rem; margin: 0;">${componentData.content}</p>`;
                }
                if (componentData.features && !children.some(child => child.type === 'list')) {
                    content += `<ul style="list-style: none; padding: 0; margin: 0; display: flex; flex-direction: column; gap: 8px;">${componentData.features.map((feature, index) => `<li key="${index}" style="font-size: 1rem;">${feature}</li>`).join('')}</ul>`;
                }
                if (componentData.ctaText && !children.some(child => child.type === 'button')) {
                    content += `<button style="padding: 10px 20px; border-radius: 8px; background: #2563eb; color: #fff; border: none; cursor: pointer;">${componentData.ctaText}</button>`;
                }
                children.forEach(child => {
                    content += renderElementRecursive(child, true);
                });
                content += `</div>`;
                break;
            case 'divider':
                content = `<hr style="border: none; background: #e5e7eb; height: 2px; ${styleStr}" />`;
                break;
            case 'spacer':
                content = `<div style="background: transparent; ${styleStr}"></div>`;
                break;
            case 'popup':
            case 'modal':
                content = `<div style="display: flex; flex-direction: column; gap: 10px; text-align: center; ${styleStr}">`;
                if (componentData.title && !children.some(child => child.type === 'heading')) {
                    content += `<h3 style="font-size: 1.5rem; margin: 0;">${componentData.title}</h3>`;
                }
                if (componentData.content && !children.some(child => child.type === 'paragraph')) {
                    content += `<p style="font-size: 1rem; margin: 0;">${componentData.content}</p>`;
                }
                if (componentData.buttonText && !children.some(child => child.type === 'button')) {
                    content += `<button style="padding: 10px 20px; border-radius: 8px; background: #2563eb; color: #fff; border: none; cursor: pointer;">${componentData.buttonText}</button>`;
                }
                children.forEach(child => {
                    content += renderElementRecursive(child, true);
                });
                content += `</div>`;
                break;
            default:
                content = `<div style="color: #ff0000; ${styleStr}">Không xác định: ${type}</div>`;
                break;
        }
        return content;
    };

    pageData.elements.forEach(element => {
        html += renderElementRecursive(element);
    });

    html += `    </div>\n</body>\n</html>`;
    return html;
};

const CreateLanding = () => {
    const [searchParams] = useSearchParams();
    const pageId = searchParams.get('id');
    useEffect(() => {
        if (!pageId) {
            console.warn('No pageId found in URL. User should create page from dashboard first.');
            toast.warning('Vui lòng tạo trang mới từ dashboard trước khi thiết kế.');
        }
    }, [pageId]);
    const [pageData, setPageData] = useState({
        canvas: { width: 1200, background: '#ffffff' },
        elements: [],
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [gridSize, setGridSize] = useState(10);
    const [showGrid, setShowGrid] = useState(true);
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);

    const handleSelectElement = (ids, append = false) => {
        if (Array.isArray(ids)) {
            setSelectedIds(append ? [...selectedIds, ...ids] : ids);
        } else {
            setSelectedIds(append ? [...selectedIds, ids] : [ids]);
        }
        setSelectedChildId(null);
    };

    const handleSelectChild = (parentId, childId) => {
        if (!parentId) {
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        if (!pageData.elements.find((el) => el.id === parentId)) {
            console.warn(`Parent element with id ${parentId} not found`);
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        setSelectedIds([parentId]);
        setSelectedChildId(childId);
    };

    const handleAddElement = (element) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: [...prev.elements, element],
            };
            setHistory([...history.slice(0, historyIndex + 1), prev]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleUpdatePosition = (id, position, mode) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id
                        ? {
                            ...el,
                            position: mode === 'replace' ? position : { ...el.position, ...position },
                        }
                        : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), prev]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleUpdateSize = (id, size) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, size } : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), prev]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleDeleteElement = (id) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.filter((el) => el.id !== id),
            };
            setHistory([...history.slice(0, historyIndex + 1), prev]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds((prev) => prev.filter((selId) => selId !== id));
        setSelectedChildId(null);
    };

    const handleEditElement = (id) => {
        console.log('Edit element:', id);
    };

    const handleDuplicateElement = (id) => {
        const element = pageData.elements.find((el) => el.id === id);
        if (element) {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    ...element.position,
                    desktop: {
                        x: (element.position.desktop?.x || 0) + 10,
                        y: (element.position.desktop?.y || 0) + 10,
                    },
                },
            };
            setPageData((prev) => {
                const newPageData = {
                    ...prev,
                    elements: [...prev.elements, newElement],
                };
                setHistory([...history.slice(0, historyIndex + 1), prev]);
                setHistoryIndex(historyIndex + 1);
                return newPageData;
            });
        }
    };

    const handleMoveElementUp = (id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index <= 0) return prev;
            const newElements = [...prev.elements];
            [newElements[index - 1], newElements[index]] = [newElements[index], newElements[index - 1]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), prev]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleMoveElementDown = (id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index >= prev.elements.length - 1) return prev;
            const newElements = [...prev.elements];
            [newElements[index], newElements[index + 1]] = [newElements[index + 1], newElements[index]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), prev]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleGroupElements = (ids) => {
        console.log('Group elements:', ids);
    };

    const handleUpdateChildren = (parentId, dragIndex, dropIndex) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        const newChildren = [...(el.children || [])];
                        if (dragIndex < 0 || dragIndex >= newChildren.length || dropIndex < 0 || dropIndex >= newChildren.length) {
                            console.warn('Invalid dragIndex or dropIndex:', { dragIndex, dropIndex, childrenLength: newChildren.length });
                            return el;
                        }
                        const [movedChild] = newChildren.splice(dragIndex, 1);
                        newChildren.splice(dropIndex, 0, movedChild);
                        return { ...el, children: newChildren };
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), prev]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleUndo = () => {
        if (historyIndex >= 0) {
            setPageData(history[historyIndex]);
            setHistoryIndex(historyIndex - 1);
        }
    };

    const handleRedo = () => {
        if (historyIndex < history.length - 1) {
            setPageData(history[historyIndex + 1]);
            setHistoryIndex(historyIndex + 1);
        }
    };

    const handleSave = async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang. Vui lòng tạo trang mới từ dashboard.');
            return;
        }

        try {
            const htmlContent = renderStaticHTML(pageData);
            const response = await api.put(`/api/pages/${pageId}`, {
                html: htmlContent
            });

            if (response.data) {
                toast.success('Lưu trang thành công!');
            } else {
                toast.error('Lưu trang thất bại: ' + (response.data?.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi lưu trang:', error);
            toast.error('Lỗi khi lưu trang: ' + (error.response?.data?.error || error.message || 'Lỗi không xác định'));
        }
    };
    const handlePreview = () => {
        const htmlContent = renderStaticHTML(pageData);
        const newWindow = window.open('', '_blank');
        newWindow.document.write(htmlContent);
        newWindow.document.close();
    };

    const handleGenerateCode = async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang để triển khai.');
            return;
        }

        try {
            // Save trước khi publish
            await handleSave();

            // Publish
            const response = await api.post(`/api/pages/${pageId}/publish`);
            if (response.data.url) { // Sửa format response
                toast.success(`Triển khai thành công! Truy cập tại: ${response.data.url}`);
            } else {
                toast.error('Triển khai thất bại: ' + (response.data?.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi triển khai:', error);
            toast.error('Lỗi khi triển khai trang: ' + (error.response?.data?.error || error.message || 'Lỗi không xác định'));
        }
    };

    const selectedElement = (() => {
        if (selectedChildId) {
            const parent = pageData.elements.find((el) => el.id === selectedIds[0]);
            if (!parent || !parent.children) return null;
            const child = parent.children.find((child) => child.id === selectedChildId);
            return child ? { json: child, isChild: true } : null;
        }
        const element = pageData.elements.find((el) => el.id === selectedIds[0]);
        return element ? { json: element, isChild: false } : null;
    })();

    return (
        <DndProvider backend={HTML5Backend}>
            <Toolbar
                onSave={handleSave}
                onPreview={handlePreview}
                onGenerateCode={handleSave}
                viewMode={viewMode}
                onViewModeChange={setViewMode}
                onUndo={handleUndo}
                onRedo={handleRedo}
                canUndo={historyIndex >= 0}
                canRedo={historyIndex < history.length - 1}
            />
            <div className="lpb-container">
                <ComponentLibrary isCollapsed={false} onToggle={() => {}} />
                <Canvas
                    pageData={pageData}
                    selectedIds={selectedIds}
                    onSelectElement={handleSelectElement}
                    onUpdatePosition={handleUpdatePosition}
                    onUpdateSize={handleUpdateSize}
                    onDeleteElement={handleDeleteElement}
                    onAddElement={handleAddElement}
                    onGroupElements={handleGroupElements}
                    viewMode={viewMode}
                    zoomLevel={zoomLevel}
                    gridSize={gridSize}
                    showGrid={showGrid}
                    onEditElement={handleEditElement}
                    onDuplicateElement={handleDuplicateElement}
                    onMoveElementUp={handleMoveElementUp}
                    onMoveElementDown={handleMoveElementDown}
                    onSelectChild={handleSelectChild}
                    selectedChildId={selectedChildId}
                    onUpdateChildren={handleUpdateChildren}
                />
                <PropertiesPanel
                    selectedElement={selectedElement}
                    onUpdateElement={(updatedElement) => {
                        setPageData((prev) => {
                            const newPageData = {
                                ...prev,
                                elements: prev.elements.map((el) =>
                                    el.id === selectedIds[0]
                                        ? updatedElement.isChild
                                            ? {
                                                ...el,
                                                children: el.children.map((child) =>
                                                    child.id === selectedChildId
                                                        ? { ...child, ...updatedElement.json }
                                                        : child
                                                ),
                                            }
                                            : { ...el, ...updatedElement.json }
                                        : el
                                ),
                            };
                            setHistory([...history.slice(0, historyIndex + 1), prev]);
                            setHistoryIndex(historyIndex + 1);
                            return newPageData;
                        });
                    }}
                    isCollapsed={false}
                    onToggle={() => {}}
                />
            </div>
        </DndProvider>
    );
};

export default CreateLanding;
