// apps/web/src/components/Login.js
import React, { useState } from 'react';
import api from '../../../../packages/utils/api';
import { useGoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await api.post('/api/auth/login', { email, password });
            localStorage.setItem('token', res.data.token);
            navigate('/dashboard');
        } catch (err) {
            console.error(err.response?.data?.msg || 'Login failed');
        }
    };

    const handleGoogleLogin = useGoogleLogin({
        onSuccess: async (tokenResponse) => {
            try {
                const res = await api.post('/api/auth/google/callback', { token: tokenResponse.access_token });
                localStorage.setItem('token', res.data.token);
                navigate('/dashboard');
            } catch (err) {
                console.error('Google login error:', err.response?.data?.msg || err.message);
            }
        },
        onError: (error) => console.error('Google login failed:', error),
        flow: 'implicit',
        scope: 'profile email',
    });

    return (
        <div style={{ padding: '20px', maxWidth: '400px', margin: 'auto' }}>
            <h2>Đăng Nhập</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                    required
                    style={{ display: 'block', margin: '10px 0', padding: '8px' }}
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Mật khẩu"
                    required
                    style={{ display: 'block', margin: '10px 0', padding: '8px' }}
                />
                <button type="submit" style={{ margin: '10px 0', padding: '8px 16px' }}>Đăng Nhập</button>
            </form>
            <button onClick={handleGoogleLogin} style={{ marginTop: '10px', padding: '8px 16px' }}>
                Đăng Nhập Với Google
            </button>
        </div>
    );
};

export default Login;
