    '--white': '#ffffff',
    '--border-radius': '4px',
    '--shadow': '0 2px 8px rgba(0, 0, 0, 0.15)',
    '--primary-color': '#2563eb',
};

// Utility to parse HTML string to page data structure
const parseHTMLToPageData = (html) => {
    try {
        const parser = new DOMParser();
        const doc = parser.parseFromString(html, 'text/html');
        const canvasElement = doc.querySelector('.lpb-canvas');
        if (!canvasElement) {
            return {
                canvas: {
                    width: parseInt(CSS_VARIABLES['--canvas-desktop']),
                    background: CSS_VARIABLES['--white'],
                },
                elements: [],
                visiblePopups: [],
            };
        }

        const elements = [];
        const visiblePopups = [];
        const canvasStyles = window.getComputedStyle(canvasElement);
        const canvas = {
            width: parseInt(canvasStyles.width) || parseInt(CSS_VARIABLES['--canvas-desktop']),
            height: parseInt(canvasStyles.height) || 1000,
            background: canvasStyles.backgroundColor || CSS_VARIABLES['--white'],
        };

        const parseElement = (el, parentId = null) => {
            const element = {
                id: el.id || `element-${Date.now()}`,
                type: el.dataset.type || 'section',
                componentData: JSON.parse(el.dataset.componentData || '{}'),
                position: {
                    desktop: {
                        x: parseFloat(el.style.left) || 0,
                        y: parseFloat(el.style.top) || 0,
                    },
                    tablet: { x: 0, y: 0 },
                    mobile: { x: 0, y: 0 },
                },
                size: {
                    width: parseInt(el.style.width) || el.offsetWidth || 1200,
                    height: parseInt(el.style.height) || el.offsetHeight || 400,
                },
                styles: {
                    backgroundColor: el.style.backgroundColor || '',
                    borderRadius: el.style.borderRadius || '',
                    boxShadow: el.style.boxShadow || '',
                    margin: el.style.margin || '0',
                    padding: el.style.padding || '0',
                    zIndex: parseInt(el.style.zIndex) || 1,
                },
                visible: el.style.display !== 'none',
                locked: el.dataset.locked === 'true',
                children: [],
            };

            if (el.dataset.type === 'popup' || el.dataset.type === 'modal') {
                if (element.visible) visiblePopups.push(element.id);
            }

            const childElements = el.querySelectorAll(':scope > [data-type]');
            childElements.forEach((child) => {
                element.children.push(parseElement(child, element.id));
            });

            return element;
        };

        const rootElements = doc.querySelectorAll('.lpb-canvas > [data-type]');
        rootElements.forEach((el) => {
            elements.push(parseElement(el));
        });

        return { canvas, elements, visiblePopups };
    } catch (error) {
        console.error('Error parsing HTML to page data:', error);
        return {
            canvas: {
                width: parseInt(CSS_VARIABLES['--canvas-desktop']),
                background: CSS_VARIABLES['--white'],
            },
            elements: [],
            visiblePopups: [],
        };
    }
};

// Utility to generate static HTML from page data
const renderStaticHTML = (pageData) => {
    try {
        const { canvas, elements, visiblePopups } = pageData;
        const canvasWidth = canvas.width || parseInt(CSS_VARIABLES['--canvas-desktop']);
        const canvasHeight = canvas.height || 1000;

        // Calculate canvas height based on sections and popups
        const calculatedHeight = elements
            .filter((el) => el.type === 'section')
            .reduce((maxY, el) => {
                const y = el.position?.desktop?.y || 0;
                const height = el.size?.height || 400;
                return Math.max(maxY, y + height);
            }, canvasHeight);

        // Base HTML structure
        let html = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Landing Page</title>
    <style>
        body { margin: 0; padding: 0; font-family: Arial, sans-serif; }
        .lpb-canvas { 
            position: relative; 
            width: ${canvasWidth}px; 
            height: ${calculatedHeight}px; 
            background: ${canvas.background || CSS_VARIABLES['--white']}; 
            margin: 0 auto; 
            overflow: hidden; 
        }
        .lpb-element { position: absolute; }
        .lpb-child-element { position: absolute; }
        .lpb-section { width: 100%; }
        .lpb-popup, .lpb-modal { 
            display: none; 
            background: ${CSS_VARIABLES['--white']}; 
            border-radius: ${CSS_VARIABLES['--border-radius']}; 
            box-shadow: ${CSS_VARIABLES['--shadow']}; 
        }
        .lpb-popup.visible, .lpb-modal.visible { display: block; }
        .lpb-heading { font-size: 2rem; font-weight: bold; }
        .lpb-paragraph { font-size: 1rem; line-height: 1.5; }
        .lpb-button { 
            padding: 10px 20px; 
            background: ${CSS_VARIABLES['--primary-color']}; 
            color: white; 
            border: none; 
            border-radius: 4px; 
            cursor: pointer; 
        }
        .lpb-image { object-fit: cover; }
        .lpb-icon { font-size: 24px; }
        .lpb-gallery { display: flex; flex-wrap: wrap; gap: 10px; }
