        // Render elements
        elements.forEach((element) => {
            const { id, type, position, size, styles, componentData, children, visible, locked } = element;
            const isPopup = type === 'popup' || type === 'modal';
            const isVisible = visible && (!isPopup || visiblePopups.includes(id));
