                        ? `onclick="window.location.href='${action.url}'"`
                        : '';
                content = `<button class="lpb-button" ${actionAttr}>${componentData.text || 'Button'}</button>`;
            } else if (type === 'image') {
                content = `<img src="${componentData.src || 'https://via.placeholder.com/150'}" alt="${componentData.alt || 'Image'}" class="lpb-image">`;
