                        top: ${child.position.desktop?.y || 0}px;
                        width: ${child.size.width}px;
                        height: ${child.size.height}px;
                        ${child.styles.backgroundColor ? `background-color: ${child.styles.backgroundColor};` : ''}
                        ${child.styles.borderRadius ? `border-radius: ${child.styles.borderRadius};` : ''}
                        ${child.styles.boxShadow ? `box-shadow: ${child.styles.boxShadow};` : ''}
                        ${child.styles.margin ? `margin: ${child.styles.margin};` : ''}
                        ${child.styles.padding ? `padding: ${child.styles.padding};` : ''}
                        z-index: ${child.styles.zIndex || 2};
                        ${child.locked ? 'pointer-events: none;' : ''}
                    `.trim();

