// F:\landinghub-iconic\apps\web\src\pages\Pages.js
import React, { useState, useEffect } from 'react';
import api from '@landinghub/api';

const Pages = () => {
    const [pages, setPages] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await api.get('/api/pages');
                setPages(res.data);
            } catch (err) {
                console.error('Error fetching pages:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <div>Đang tải...</div>;

    return (
        <div className="page-content">
            <h1>Quản lý Landing Page</h1>
            <table>
                <thead>
                <tr>
                    <th>Title</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {pages.map((page) => (
                    <tr key={page._id}>
                        <td>{page.title}</td>
                        <td>{page.status || 'Chưa triển khai'}</td>
                        <td>
                            <button onClick={() => alert(`Xem trước ${page.title}`)}>Xem trước</button>
                            <button onClick={() => alert(`Triển khai ${page.title}`)}>Triển khai</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <button onClick={() => alert('Tạo Landing Page mới')}>Tạo mới</button>
        </div>
    );
};

export default React.memo(Pages);
