import React, { useState } from 'react';
import { ChevronRight, ChevronLeft, MousePointer, Type, Zap, Sparkles, Palette, Box, Sun, Settings, Maximize } from 'lucide-react';
import '../../../styles/ElementPropertiesPanel.css';

const FONT_PRESETS = [
  { name: 'Arial', value: 'Arial, sans-serif' },
  { name: 'Helvetica', value: 'Helvetica, sans-serif' },
  { name: 'Roboto', value: 'Roboto, sans-serif' },
  { name: 'Open Sans', value: 'Open Sans, sans-serif' },
  { name: 'Poppins', value: 'Poppins, sans-serif' },
  { name: 'Montserrat', value: 'Montserrat, sans-serif' },
  { name: 'Inter', value: 'Inter, sans-serif' },
];

const FONT_WEIGHT_PRESETS = [
  { name: 'Light (300)', value: '300' },
  { name: 'Regular (400)', value: '400' },
  { name: 'Medium (500)', value: '500' },
  { name: 'Semibold (600)', value: '600' },
  { name: 'Bold (700)', value: '700' },
  { name: 'Extra Bold (800)', value: '800' },
];

const COLOR_PRESETS = ['#000000', '#ffffff', '#f3f4f6', '#1f2937', '#374151', '#667eea', '#764ba2', '#2563eb', '#3b82f6', '#06b6d4', '#10b981', '#22c55e', '#84cc16', '#eab308', '#f59e0b', '#ef4444', '#dc2626', '#ec4899', '#a855f7', '#8b5cf6'];

const GRADIENT_PRESETS = [
  { name: 'Không có', value: '', preview: '#2563eb' },
  { name: 'Tím - Hồng', value: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', preview: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' },
  { name: 'Xanh dương', value: 'linear-gradient(135deg, #2563eb 0%, #06b6d4 100%)', preview: 'linear-gradient(135deg, #2563eb 0%, #06b6d4 100%)' },
  { name: 'Xanh lá', value: 'linear-gradient(135deg, #10b981 0%, #84cc16 100%)', preview: 'linear-gradient(135deg, #10b981 0%, #84cc16 100%)' },
  { name: 'Cam - Vàng', value: 'linear-gradient(135deg, #f59e0b 0%, #eab308 100%)', preview: 'linear-gradient(135deg, #f59e0b 0%, #eab308 100%)' },
  { name: 'Đỏ - Hồng', value: 'linear-gradient(135deg, #ef4444 0%, #ec4899 100%)', preview: 'linear-gradient(135deg, #ef4444 0%, #ec4899 100%)' },
  { name: 'Cầu vồng', value: 'linear-gradient(90deg, #667eea, #764ba2, #f093fb, #4facfe)', preview: 'linear-gradient(90deg, #667eea, #764ba2, #f093fb, #4facfe)' },
  { name: 'Đen xám', value: 'linear-gradient(135deg, #1f2937 0%, #4b5563 100%)', preview: 'linear-gradient(135deg, #1f2937 0%, #4b5563 100%)' },
  { name: 'Vàng gold', value: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)', preview: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)' },
];

const ANIMATION_PRESETS = [
  { name: 'Không chọn', value: 'none' },
  { name: 'bounce', value: 'bounce' },
  { name: 'pulse', value: 'pulse' },
  { name: 'shake', value: 'shake' },
  { name: 'fadeIn', value: 'fadeIn' },
  { name: 'zoomIn', value: 'zoomIn' },
  { name: 'slideInUp', value: 'slideInUp' },
];

const EVENT_TYPES = [
  { name: 'Không chọn', value: 'none' },
  { name: 'Mở URL', value: 'navigate' },
  { name: 'Gửi Form', value: 'submitForm' },
  { name: 'Gọi API', value: 'triggerApi' },
  { name: 'Mở Popup', value: 'openPopup' },
  { name: 'Đóng Popup', value: 'closePopup' },
  { name: 'Cuộn đến Section', value: 'scrollToSection' },
];

const ButtonPropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle, allElements = [] }) => {
  const [activeTab, setActiveTab] = useState('design');

  if (isCollapsed) {
    return (
        <div style={{
          width: '48px',
          height: '100%',
          background: '#ffffff',
          borderLeft: '1px solid #e5e7eb',
          display: 'flex',
          alignItems: 'center',
          padding: '12px 0'
        }}>
          <button
              onClick={onToggle}
              style={{
                width: '32px',
                height: '32px',
                border: 'none',
                background: 'transparent',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '6px',
                transition: 'background 0.2s'
              }}
              onMouseEnter={(e) => e.currentTarget.style.background = '#f3f4f6'}
              onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
          >
            <ChevronLeft size={18} />
          </button>
        </div>
    );
  }

  if (!selectedElement || !selectedElement.json) {
    return (
        <div style={{
          width: '320px',
          height: '100%',
          background: '#ffffff',
          borderLeft: '1px solid #e5e7eb',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <div style={{
            padding: '16px',
            borderBottom: '1px solid #e5e7eb',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <Type size={16} color="#2563eb" />
              <h3 style={{ margin: 0, fontSize: '14px', fontWeight: '600' }}>Thuộc tính Button</h3>
            </div>
            <button
                onClick={onToggle}
                style={{
                  border: 'none',
                  background: 'transparent',
                  cursor: 'pointer',
                  padding: '4px',
                  display: 'flex',
                  alignItems: 'center'
                }}
            >
              <ChevronRight size={18} />
            </button>
          </div>
          <div style={{
            flex: 1,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#9ca3af',
            padding: '40px'
          }}>
            <MousePointer size={48} strokeWidth={1.5} />
            <p style={{ margin: '16px 0 0', fontSize: '14px' }}>Chọn một button để chỉnh sửa</p>
          </div>
        </div>
    );
  }

  const { type, componentData = {}, styles = {}, size = {} } = selectedElement.json;

  if (type !== 'button') {
    return (
        <div style={{
          width: '320px',
          height: '100%',
          background: '#ffffff',
          borderLeft: '1px solid #e5e7eb',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <div style={{
            padding: '16px',
            borderBottom: '1px solid #e5e7eb',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <Type size={16} color="#2563eb" />
              <h3 style={{ margin: 0, fontSize: '14px', fontWeight: '600' }}>Không hỗ trợ</h3>
            </div>
            <button
                onClick={onToggle}
                style={{
                  border: 'none',
                  background: 'transparent',
                  cursor: 'pointer',
                  padding: '4px',
                  display: 'flex',
                  alignItems: 'center'
                }}
            >
              <ChevronRight size={18} />
            </button>
          </div>
        </div>
    );
  }

  const popupElements = allElements.filter(el => el.type === 'popup');
  const sectionElements = allElements.filter(el => el.type === 'section');

  const handleStyleChange = (property, value) => {
    onUpdateElement({ ...selectedElement, json: { ...selectedElement.json, styles: { ...selectedElement.json.styles, [property]: value } } });
  };

  const handleComponentDataChange = (key, value) => {
    onUpdateElement({ ...selectedElement, json: { ...selectedElement.json, componentData: { ...selectedElement.json.componentData, [key]: value } } });
  };

  const handleEventChange = (eventType, property, value) => {
    const currentEvents = componentData.events || {};
    onUpdateElement({ ...selectedElement, json: { ...selectedElement.json, componentData: { ...selectedElement.json.componentData, events: { ...currentEvents, [eventType]: { ...(currentEvents[eventType] || {}), [property]: value } } } } });
  };

  const handleSizeChange = (dimension, value) => {
    const parsedValue = parseInt(value);
    if (isNaN(parsedValue) || parsedValue < 0) {
      return;
    }
    onUpdateElement({ ...selectedElement, json: { ...selectedElement.json, size: { ...selectedElement.json.size, [dimension]: parsedValue } } });
  };

  const applyGradient = (gradient) => {
    handleStyleChange('background', gradient || '#2563eb');
  };

  const onClickEvent = componentData.events?.onClick || {};
  const onHoverEvent = componentData.events?.onHover || {};

  return (
      <div style={{
        width: '320px',
        height: '100%',
        background: '#ffffff',
        borderLeft: '1px solid #e5e7eb',
        display: 'flex',
        flexDirection: 'column',
        fontFamily: 'system-ui, -apple-system, sans-serif'
      }}>
        <div style={{
          padding: '16px',
          borderBottom: '1px solid #e5e7eb',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          <input
              type="text"
              value={componentData.content || ''}
              onChange={(e) => handleComponentDataChange('content', e.target.value)}
              placeholder="Nội dung nút"
              style={{
                flex: 1,
                padding: '0 12px',
                fontSize: '14px',
                fontWeight: '600',
                border: 'none',
                outline: 'none'
              }}
          />
          <button
              onClick={onToggle}
              style={{
                border: 'none',
                background: 'transparent',
                cursor: 'pointer',
                padding: '4px',
                display: 'flex',
                alignItems: 'center'
              }}
          >
            <ChevronRight size={18} />
          </button>
        </div>

        <div style={{
          display: 'flex',
          borderBottom: '1px solid #e5e7eb'
        }}>
          <button
              onClick={() => setActiveTab('design')}
              style={{
                flex: 1,
                padding: '12px',
                fontSize: '13px',
                fontWeight: activeTab === 'design' ? '600' : '400',
                borderBottom: activeTab === 'design' ? '2px solid #2563eb' : 'none',
                background: 'transparent',
                cursor: 'pointer',
                color: activeTab === 'design' ? '#2563eb' : '#374151',
                transition: 'all 0.2s'
              }}
              onMouseEnter={(e) => {
                if (activeTab !== 'design') e.currentTarget.style.background = '#f3f4f6';
              }}
              onMouseLeave={(e) => {
                if (activeTab !== 'design') e.currentTarget.style.background = 'transparent';
              }}
          >
            Thiết kế
          </button>
          <button
              onClick={() => setActiveTab('events')}
              style={{
                flex: 1,
                padding: '12px',
                fontSize: '13px',
                fontWeight: activeTab === 'events' ? '600' : '400',
                borderBottom: activeTab === 'events' ? '2px solid #2563eb' : 'none',
                background: 'transparent',
                cursor: 'pointer',
                color: activeTab === 'events' ? '#2563eb' : '#374151',
                transition: 'all 0.2s'
              }}
              onMouseEnter={(e) => {
                if (activeTab !== 'events') e.currentTarget.style.background = '#f3f4f6';
              }}
              onMouseLeave={(e) => {
                if (activeTab !== 'events') e.currentTarget.style.background = 'transparent';
              }}
          >
            Sự kiện
          </button>
          <button
              onClick={() => setActiveTab('animation')}
              style={{
                flex: 1,
                padding: '12px',
                fontSize: '13px',
                fontWeight: activeTab === 'animation' ? '600' : '400',
                borderBottom: activeTab === 'animation' ? '2px solid #2563eb' : 'none',
                background: 'transparent',
                cursor: 'pointer',
                color: activeTab === 'animation' ? '#2563eb' : '#374151',
                transition: 'all 0.2s'
              }}
              onMouseEnter={(e) => {
                if (activeTab !== 'animation') e.currentTarget.style.background = '#f3f4f6';
              }}
              onMouseLeave={(e) => {
                if (activeTab !== 'animation') e.currentTarget.style.background = 'transparent';
              }}
          >
            Hiệu ứng
          </button>
        </div>

        {activeTab === 'design' && (
            <div style={{ padding: '16px', overflowY: 'auto', flex: 1 }}>
              <div style={{ marginBottom: '24px' }}>
                <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
                  <Type size={14} />
                  Nội dung
                </h4>
                <div style={{ marginBottom: '12px' }}>
                  <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                    Text hiển thị
                  </label>
                  <textarea
                      value={componentData.content || ''}
                      onChange={(e) => handleComponentDataChange('content', e.target.value)}
                      placeholder="Nhập nội dung nút..."
                      rows="2"
                      style={{
                        width: '100%',
                        padding: '8px 12px',
                        fontSize: '13px',
                        border: '1px solid #e5e7eb',
                        borderRadius: '6px',
                        resize: 'vertical',
                        fontFamily: styles.fontFamily || 'Arial, sans-serif',
                        outline: 'none'
                      }}
                      onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                      onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                  />
                </div>
                <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
                  {['Xem thêm →', 'Đăng ký ngay', 'Liên hệ'].map(text => (
                      <button
                          key={text}
                          onClick={() => handleComponentDataChange('content', text)}
                          style={{
                            padding: '6px 12px',
                            fontSize: '12px',
                            background: '#f3f4f6',
                            border: '1px solid #d1d5db',
                            borderRadius: '6px',
                            cursor: 'pointer',
                            transition: 'all 0.2s'
                          }}
                      >
                        {text}
                      </button>
                  ))}
                </div>
              </div>

              <div style={{ marginBottom: '24px' }}>
                <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
                  <Type size={14} />
                  Kiểu chữ
                </h4>
                <div style={{ display: 'grid', gap: '16px' }}>
                  <div>
                    <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                      Font chữ
                    </label>
                    <select
                        value={styles.fontFamily || 'Arial, sans-serif'}
                        onChange={(e) => handleStyleChange('fontFamily', e.target.value)}
                        style={{
                          width: '100%',
                          padding: '8px 12px',
                          fontSize: '13px',
                          border: '1px solid #e5e7eb',
                          borderRadius: '6px',
                          background: '#fff',
                          cursor: 'pointer'
                        }}
                    >
                      {FONT_PRESETS.map(font => (<option key={font.value} value={font.value}>{font.name}</option>))}
                    </select>
                  </div>
                  <div>
                    <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                      Cỡ chữ
                    </label>
                    <div style={{ position: 'relative' }}>
                      <input
                          type="number"
                          value={parseInt(styles.fontSize) || 16}
                          onChange={(e) => handleStyleChange('fontSize', e.target.value + 'px')}
                          style={{
                            width: '100%',
                            padding: '8px 32px 8px 12px',
                            fontSize: '13px',
                            border: '1px solid #e5e7eb',
                            borderRadius: '6px',
                            boxSizing: 'border-box',
                            outline: 'none'
                          }}
                      />
                      <span style={{
                        position: 'absolute',
                        right: '12px',
                        top: '50%',
                        transform: 'translateY(-50%)',
                        fontSize: '12px',
                        color: '#9ca3af'
                      }}>px</span>
                    </div>
                  </div>
                  <div>
                    <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                      Độ đậm
                    </label>
                    <select
                        value={styles.fontWeight || '600'}
                        onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                        style={{
                          width: '100%',
                          padding: '8px 12px',
                          fontSize: '13px',
                          border: '1px solid #e5e7eb',
                          borderRadius: '6px',
                          background: '#fff',
                          cursor: 'pointer'
                        }}
                    >
                      {FONT_WEIGHT_PRESETS.map(weight => (<option key={weight.value} value={weight.value}>{weight.name}</option>))}
                    </select>
                  </div>
                  <div>
