                      <input
                          type="number"
                          value={size.height || 50}
                          onChange={(e) => handleSizeChange('height', e.target.value)}
                          style={{
                            width: '100%',
                            padding: '8px 32px 8px 12px',
                            fontSize: '13px',
                            border: '1px solid #e5e7eb',
                            borderRadius: '6px',
                            boxSizing: 'border-box',
                            outline: 'none'
                          }}
                      />
                      <span style={{
                        position: 'absolute',
                        right: '12px',
                        top: '50%',
                        transform: 'translateY(-50%)',
