                        </label>
                        <select
                            value={onClickEvent.popupId || ''}
                            onChange={(e) => handleEventChange('onClick', 'popupId', e.target.value)}
                            style={{
                              width: '100%',
                              padding: '8px 12px',
                              fontSize: '13px',
                              border: '1px solid #e5e7eb',
                              borderRadius: '6px',
                              background: '#fff',
                              cursor: 'pointer'
                            }}
                        >
                          <option value="">-- Chọn popup --</option>
                          {popupElements.map(popup => (<option key={popup.id} value={popup.id}>{popup.componentData?.title || popup.id}</option>))}
                        </select>
                      </div>
                  )}
                  {onClickEvent.type === 'scrollToSection' && (
