                          <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                            Chọn Section
                          </label>
                          <select
                              value={onClickEvent.sectionId || ''}
                              onChange={(e) => handleEventChange('onClick', 'sectionId', e.target.value)}
