                          step="100"
                          value={componentData.animation?.duration || 1000}
                          onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, duration: parseInt(e.target.value) })}
                          style={{ flex: 1 }}
                      />
                      <span style={{ fontSize: '12px', color: '#6b7280', minWidth: '48px' }}>
                                        {componentData.animation?.duration || 1000}ms
                                    </span>
                    </div>
                  </div>
                  <div>
                    <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                      Độ trễ (ms)
                    </label>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                      <input
                          type="range"
                          min="0"
                          max="3000"
                          step="100"
                          value={componentData.animation?.delay || 0}
                          onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, delay: parseInt(e.target.value) })}
                          style={{ flex: 1 }}
                      />
                      <span style={{ fontSize: '12px', color: '#6b7280', minWidth: '48px' }}>
                                        {componentData.animation?.delay || 0}ms
                                    </span>
                    </div>
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                    <input
                        type="checkbox"
                        id="repeat-animation"
                        checked={componentData.animation?.repeat || false}
                        onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, repeat: e.target.checked })}
                        style={{ cursor: 'pointer' }}
                    />
                    <label htmlFor="repeat-animation" style={{ fontSize: '12px', color: '#374151', cursor: 'pointer' }}>
                      Lặp lại hiệu ứng
                    </label>
                  </div>
                </div>
              </div>
            </div>
        )}
      </div>
  );
};

export default ButtonPropertiesPanel;
