import React, { useState, useMemo, useCallback } from 'react';
import { useDrag } from 'react-dnd';
import { toast } from 'react-toastify';
import { EXTENDED_LIBRARY } from '../../constants/ExtendedLibrary';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    ELEMENT: 'element',
};

const ComponentLibrary = ({ isCollapsed, onToggle }) => {
    const [activeTab, setActiveTab] = useState('sections');
    const [searchTerm, setSearchTerm] = useState('');
    const [activeCategory, setActiveCategory] = useState(null);
    const [activeSubcategory, setActiveSubcategory] = useState(null);

    const categories = useMemo(() => {
        return {
            sections: EXTENDED_LIBRARY.sections.subCategories || [],
            elements: EXTENDED_LIBRARY.elements.subCategories || [],
            popups: EXTENDED_LIBRARY.popups.subCategories || [],
            templates: EXTENDED_LIBRARY.templates.subCategories || [],
        };
    }, []);

    const filteredItems = useMemo(() => {
        const currentCategory = categories[activeTab] || [];
        let items = [];

        if (activeSubcategory) {
            const subcategory = currentCategory.find((cat) => cat.id === activeSubcategory);
            items = subcategory ? subcategory.templates || [] : [];
        } else if (activeCategory) {
            const category = currentCategory.find((cat) => cat.id === activeCategory);
            items = category ? category.templates || [] : [];
        } else {
            // Lấy tất cả templates từ tất cả categories
            items = currentCategory.flatMap((cat) => cat.templates || []);
        }

        if (!searchTerm) return items;
        return items.filter((item) =>
            item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            item.description.toLowerCase().includes(searchTerm.toLowerCase())
        );
    }, [activeTab, activeCategory, activeSubcategory, searchTerm, categories]);

    const ComponentItem = ({ item }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.ELEMENT,
            item: {
                id: item.id,
                json: {
                    type: item.json.type,
                    componentData: item.json.componentData || {},
                    size: item.json.size || { width: 200, height: 50 },
                    styles: item.json.styles || {},
                    children: item.json.children || [],
                },
            },
            begin: () => {
                console.log('Dragging item:', item.name, 'Type:', item.json.type);
                console.log('Item data:', item.json);
            },
            end: (item, monitor) => {
                const dropResult = monitor.getDropResult();
                if (dropResult) {
                    console.log('Drop successful for:', item.id);
                } else {
                    console.log('Drop cancelled for:', item.id);
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [item]);

        return (
            <div
                ref={drag}
                className={`lpb-template-item ${isDragging ? 'lpb-template-item-dragging' : ''}`}
                title={item.description}
            >
                {/* Sửa icon để không bị trùng lặp class */}
                <div className="lpb-template-item-icon">
                    <i className={item.icon || 'fas fa-cube'}></i>
                </div>
                <div className="lpb-template-item-content">
                    <span className="lpb-template-item-name">{item.name}</span>
                    <div className="lpb-template-item-level">{item.level || 'basic'}</div>
                </div>
                <div className="lpb-template-tooltip">{item.description}</div>
            </div>
        );
    };

    const handleTabClick = useCallback((tab) => {
        setActiveTab(tab);
        setActiveCategory(null);
        setActiveSubcategory(null);
        setSearchTerm('');
        console.log('Switched to tab:', tab, 'Available items:', categories[tab]?.length);
    }, [categories]);

    const handleCategoryClick = useCallback((categoryId) => {
        const newActiveCategory = activeCategory === categoryId ? null : categoryId;
        setActiveCategory(newActiveCategory);
        setActiveSubcategory(null);
        console.log('Category clicked:', categoryId, 'Active:', newActiveCategory);
    }, [activeCategory]);

    const handleSubcategoryClick = useCallback((subcategoryId) => {
        const newActiveSubcategory = activeSubcategory === subcategoryId ? null : subcategoryId;
        setActiveSubcategory(newActiveSubcategory);
        console.log('Subcategory clicked:', subcategoryId, 'Active:', newActiveSubcategory);
    }, [activeSubcategory]);

    // Debug log để kiểm tra data
    React.useEffect(() => {
        console.log('EXTENDED_LIBRARY loaded:', EXTENDED_LIBRARY);
        console.log('Categories:', categories);
        console.log('Filtered items:', filteredItems);
    }, [categories, filteredItems]);

    if (isCollapsed) {
        return (
            <div className="lpb-sidebar-left lpb-sidebar-collapsed">
                <div className="lpb-component-library">
                    <div className="lpb-component-library-header">
                        <button onClick={onToggle} className="lpb-component-library-toggle">
                            <i className="fas fa-chevron-right"></i>
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="lpb-sidebar-left">
            <div className="lpb-component-library">
                <div className="lpb-component-library-header">
                    <h2 className="lpb-component-library-title">Thư viện</h2>
                    <button onClick={onToggle} className="lpb-component-library-toggle">
                        <i className="fas fa-chevron-left"></i>
                    </button>
                </div>

                <div className="lpb-component-library-search">
                    <div className="lpb-search-input-wrapper">
                        <i className="fas fa-search lpb-search-icon"></i>
                        <input
                            type="text"
                            className="lpb-search-input"
                            placeholder="Tìm kiếm components..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        {searchTerm && (
                            <button
                                className="lpb-search-clear"
                                onClick={() => setSearchTerm('')}
                            >
                                <i className="fas fa-times"></i>
                            </button>
                        )}
                    </div>
                </div>

                <div className="lpb-component-library-tabs">
                    {Object.keys(categories).map((tab) => (
                        <button
                            key={tab}
                            className={`lpb-component-library-tab ${activeTab === tab ? 'active' : ''}`}
                            onClick={() => handleTabClick(tab)}
                        >
                            <i className={EXTENDED_LIBRARY[tab]?.icon || 'fas fa-cube'}></i>
                            <span>{EXTENDED_LIBRARY[tab]?.name || tab.charAt(0).toUpperCase() + tab.slice(1)}</span>
                        </button>
                    ))}
                </div>

                <div className="lpb-component-library-content">
                    {searchTerm ? (
                        // Hiển thị kết quả search
                        <div className="lpb-search-results">
                            <div className="lpb-search-results-header">
                                <span>Kết quả tìm kiếm: "{searchTerm}" ({filteredItems.length} items)</span>
                            </div>
                            <div className="lpb-component-library-template-list">
                                {filteredItems.length > 0 ? (
                                    filteredItems.map((item) => (
                                        <ComponentItem key={item.id} item={item} />
                                    ))
                                ) : (
                                    <div className="lpb-no-results">
                                        <i className="fas fa-search"></i>
                                        <p>Không tìm thấy component nào</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    ) : (
                        // Hiển thị categories và subcategories
                        <div className="lpb-component-library-wrapper">
                            {categories[activeTab]?.length > 0 ? (
                                categories[activeTab].map((category) => (
                                    <div key={category.id} className="lpb-component-library-category">
                                        <button
                                            className={`lpb-component-library-category-btn ${
                                                activeCategory === category.id ? 'lpb-component-library-category-btn-active' : ''
                                            }`}
                                            onClick={() => handleCategoryClick(category.id)}
                                        >
                                            <i className="fas fa-folder"></i>
                                            <span>{category.name}</span>
                                            <i className={`fas fa-chevron-${activeCategory === category.id ? 'down' : 'right'} lpb-category-chevron`}></i>
                                        </button>

                                        {activeCategory === category.id && (
                                            <div className="lpb-category-content">
                                                {category.subCategories && category.subCategories.length > 0 && (
                                                    <div className="lpb-component-library-subcategory-nav">
                                                        {category.subCategories.map((subcategory) => (
                                                            <button
                                                                key={subcategory.id}
                                                                className={`lpb-component-library-subcategory-btn ${
                                                                    activeSubcategory === subcategory.id ? 'lpb-component-library-subcategory-btn-active' : ''
                                                                }`}
                                                                onClick={() => handleSubcategoryClick(subcategory.id)}
                                                            >
                                                                <span>{subcategory.name}</span>
                                                            </button>
                                                        ))}
                                                    </div>
                                                )}

                                                <div className="lpb-component-library-template-list">
                                                    {filteredItems.length > 0 ? (
                                                        filteredItems.map((item) => (
                                                            <ComponentItem key={item.id} item={item} />
                                                        ))
                                                    ) : (
                                                        <div className="lpb-no-items">
                                                            <i className="fas fa-box-open"></i>
                                                            <p>Chưa có component nào</p>
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                ))
                            ) : (
                                <div className="lpb-empty-category">
                                    <i className="fas fa-folder-open"></i>
                                    <p>Không có category nào trong tab này</p>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                {/* Stats footer */}
                <div className="lpb-component-library-footer">
                    <div className="lpb-stats">
                        <span className="lpb-stats-item">
                            <i className="fas fa-layer-group"></i>
                            {categories[activeTab]?.length || 0} categories
                        </span>
                        <span className="lpb-stats-item">
                            <i className="fas fa-cube"></i>
                            {filteredItems.length} items
                        </span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ComponentLibrary;
