export const EXTENDED_LIBRARY = {
    sections: {
        name: 'Sections',
        subCategories: [
            {
                id: 'standard-sections',
                name: 'Standard Sections',
                templates: [
                    {
                        id: 'hero-section',
                        name: 'Hero Section',
                        icon: 'fa-layer-group',
                        description: 'A prominent hero section for landing pages',
                        json: {
                            type: 'section',
                            componentData: {
                                structure: 'ladi-standard',
                                title: 'Welcome to Our Site',
                                backgroundColor: '#ffffff',
                            },
                            size: { width: 420, height: 574 },
                            styles: { color: '#ffffff', textAlign: 'center' },
                            children: [],
                        },
                    },
                ],
            },
        ],
    },
    elements: {
        name: 'Elements',
        subCategories: [
            {
                id: 'basic-elements',
                name: 'Basic Elements',
                templates: [
                    {
                        id: 'heading-1',
                        name: 'Heading',
                        icon: 'fa-heading',
                        description: 'A customizable heading',
                        json: {
                            type: 'heading',
                            componentData: { content: 'New Heading' },
                            size: { width: 200, height: 50 },
                            styles: { fontSize: '1.5rem', color: '#1f2937' },
                        },
                    },
                    {
                        id: 'paragraph-1',
                        name: 'Paragraph',
                        icon: 'fa-paragraph',
                        description: 'A customizable paragraph',
                        json: {
                            type: 'paragraph',
                            componentData: { content: 'This is a paragraph' },
                            size: { width: 200, height: 50 },
                            styles: { fontSize: '1rem', color: '#1f2937' },
                        },
                    },
                    {
                        id: 'button-1',
                        name: 'Button',
                        icon: 'fa-square',
                        description: 'A customizable button',
                        json: {
                            type: 'button',
                            componentData: { content: 'Click Me' },
                            size: { width: 120, height: 40 },
                            styles: { background: '#2563eb', color: '#fff', borderRadius: '8px' },
                        },
                    },
                ],
            },
        ],
    },
    popups: {
        name: 'Popups',
        subCategories: [
            {
                id: 'basic-popups',
                name: 'Basic Popups',
                templates: [
                    {
                        id: 'popup-1',
                        name: 'Basic Popup',
                        icon: 'fa-window-restore',
                        description: 'A simple popup',
                        json: {
                            type: 'popup',
                            componentData: { title: 'Popup Title', content: 'Popup Content' },
                            size: { width: 300, height: 200 },
                            styles: { backgroundColor: '#ffffff' },
                        },
                    },
                ],
            },
        ],
    },
    templates: {
        name: 'Templates',
        subCategories: [],
    },
};
