// F:\landinghub-iconic\apps\web\src\components\Login.js
import React, { useState } from 'react';
import api from '@landinghub/api';
import { useGoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const [form, setForm] = useState({ email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };
    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);
        try {
            const res = await api.post('/api/auth/login', form);
            localStorage.setItem('token', res.data.token);
            navigate('/dashboard');
        } catch (err) {
            setError(err.response?.data?.msg || 'Đăng nhập thất bại');
        } finally {
            setLoading(false);
        }
    };

    const loginWithGoogle = useGoogleLogin({
        onSuccess: async (tokenResponse) => {
            try {
                // tokenResponse có access_token, dùng để gọi API Google lấy id_token
                const userInfo = await axios.get(
                    `https://www.googleapis.com/oauth2/v3/userinfo?access_token=${tokenResponse.access_token}`
                );

                console.log("Google user info:", userInfo.data);

                const res = await axios.post("http://localhost:5000/api/auth/google/callback", {
                    email: userInfo.data.email,
                    name: userInfo.data.name,
                    googleId: userInfo.data.sub, // sub là unique Google ID
                });

                localStorage.setItem("token", res.data.token);
                navigate('/dashboard');
            } catch (err) {
                console.error("Google login error:", err.response?.data || err.message);
                setError("Đăng nhập Google thất bại");
            }
        },
        onError: () => {
            setError("Google Login Failed");
        },
    });



    return (
        <div style={{ padding: 24, maxWidth: 400, margin: 'auto' }}>
            <h2 style={{ textAlign: 'center', marginBottom: 20 }}>Đăng Nhập</h2>

            {error && (
                <div style={{ color: 'red', marginBottom: 12, textAlign: 'center' }}>
                    {error}
                </div>
            )}

            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                    placeholder="Email"
                    required
                    style={{ width: '100%', marginBottom: 12, padding: 10 }}
                />
                <input
                    type="password"
                    name="password"
                    value={form.password}
                    onChange={handleChange}
                    placeholder="Mật khẩu"
                    required
                    autoComplete="current-password"
                    style={{ width: '100%', marginBottom: 12, padding: 10 }}
                />
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        width: '100%',
                        padding: 12,
                        backgroundColor: '#1976d2',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                    }}
                >
                    {loading ? 'Đang đăng nhập...' : 'Đăng Nhập'}
                </button>
            </form>

            <hr style={{ margin: '20px 0' }} />

            <button
                onClick={() => loginWithGoogle()}
                style={{
                    width: '100%',
                    padding: 12,
                    backgroundColor: '#db4437',
                    color: '#fff',
                    border: 'none',
                    cursor: 'pointer',
                }}
            >
                Đăng Nhập Với Google
            </button>
        </div>
    );
};

export default Login;
