import React, { useRef, useCallback, useEffect } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import { X, Eye, EyeOff } from 'lucide-react';
import { ItemTypes, getCanvasPosition, snapToGrid, getElementBounds, renderComponentContent } from './helpers';
import SelectionOverlay from './SelectionOverlay';
import '../../styles/Element.css';

export const ChildElement = ({
  child,
  parentId,
  isSelected,
  onSelectChild,
  onUpdateChildPosition,
  onMoveChild,
  viewMode,
  zoomLevel,
  gridSize,
  showGrid,
  setDragPreview,
  onDeleteChild,
}) => {
  const childRef = useRef(null);

  const [{ isDragging }, drag, preview] = useDrag(() => ({
    type: ItemTypes.CHILD_ELEMENT,
    item: { childId: child.id, parentId },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  const [{ isOver }, drop] = useDrop(() => ({
    accept: [ItemTypes.CHILD_ELEMENT],
    hover: (item, monitor) => {
      const clientOffset = monitor.getClientOffset();
      if (!clientOffset || !childRef.current) return;

      const pos = getCanvasPosition(clientOffset.x, clientOffset.y, childRef.current, zoomLevel);
      const snapPoints = [];
      const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

      setDragPreview({
        id: item.childId,
        x: snapped.x,
        y: snapped.y,
        size: child.size || { width: 200, height: 50 },
        type: child.type,
        componentData: child.componentData,
        styles: child.styles,
      });
    },
    drop: (item, monitor) => {
      const clientOffset = monitor.getClientOffset();
      if (!clientOffset || !childRef.current) return { moved: false };

      const pos = getCanvasPosition(clientOffset.x, clientOffset.y, childRef.current, zoomLevel);
      const snapPoints = [];
      const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

      if (item.childId !== child.id) {
        onMoveChild(item.parentId, item.childId, parentId, snapped);
        return { moved: true, newPosition: snapped };
      }
      return { moved: false };
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  useEffect(() => {
    preview(getEmptyImage(), { captureDraggingState: true });
  }, [preview]);

  const handleSelectChild = useCallback(
    (e) => {
      e.stopPropagation();
      onSelectChild(parentId, child.id);
    },
    [onSelectChild, parentId, child.id]
  );

  const handleMouseDown = useCallback(
    (e) => {
      e.stopPropagation();
      if (child.locked) return;

      handleSelectChild(e);
      const startX = e.clientX;
      const startY = e.clientY;
      const initialPos = child.position[viewMode] || { x: 0, y: 0 };

      const handleMouseMove = (moveEvent) => {
        const deltaX = (moveEvent.clientX - startX) / (zoomLevel / 100);
        const deltaY = (moveEvent.clientY - startY) / (zoomLevel / 100);
        const newPos = {
          x: Math.max(0, initialPos.x + deltaX),
          y: Math.max(0, initialPos.y + deltaY),
        };
        const snapped = snapToGrid(newPos.x, newPos.y, showGrid ? gridSize : Infinity, []);
        onUpdateChildPosition(parentId, child.id, { [viewMode]: snapped }, 'absolute');
      };

      const handleMouseUp = () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };

      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    },
    [child, viewMode, zoomLevel, gridSize, showGrid, onUpdateChildPosition, parentId, handleSelectChild]
  );

  const style = {
    position: 'absolute',
    transform: `translate(${child.position[viewMode]?.x || 0}px, ${child.position[viewMode]?.y || 0}px)`,
    width: child.size?.width || 200,
    height: child.size?.height || 50,
    opacity: isDragging ? 0.5 : child.locked ? 0.7 : 1,
    cursor: child.locked ? 'not-allowed' : 'move',
    zIndex: child.type === 'popup' ? 10000 : 1,
    ...child.styles,
  };

  return (
    <div
      ref={(node) => {
        childRef.current = node;
        drag(drop(node));
      }}
      className={`lpb-child-element ${isSelected ? 'lpb-selected' : ''} ${isOver ? 'lpb-over' : ''} lpb-element-${child.type}`}
      style={style}
      onClick={handleSelectChild}
      onMouseDown={handleMouseDown}
    >
      {renderComponentContent(child.type, child.componentData, child.styles)}
      {isSelected && (
        <SelectionOverlay
          element={child}
          onDelete={() => onDeleteChild(parentId, child.id)}
          isChild
        />
      )}
    </div>
  );
};

export const Element = ({
  element,
  isSelected,
  onSelectElement,
  onUpdatePosition,
  viewMode,
  zoomLevel,
  gridSize,
  onContextMenu,
  canvasBounds,
  canvasRef,
  onSelectChild,
  selectedChildId,
  onAddChild,
  onUpdateChildPosition,
  onMoveChild,
  showGrid,
  setDragPreview,
  visiblePopups,
  onClosePopup,
  onSaveTemplate,
  onToggleVisibility,
  onEditElement,
  onDuplicateElement,
  onMoveElementUp,
  onMoveElementDown,
  onDeleteElement,
  onDeleteChild,
}) => {
  const elementRef = useRef(null);
  const [{ isDragging }, drag, preview] = useDrag(() => ({
    type: ItemTypes.ELEMENT,
    item: { id: element.id, type: element.type },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
    canDrag: () => !element.locked && element.type !== 'section' && element.type !== 'popup',
  }));

  const [{ isOver }, drop] = useDrop(() => ({
    accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
    hover: (item, monitor) => {
      const clientOffset = monitor.getClientOffset();
      if (!clientOffset || !elementRef.current) return;

      const pos = getCanvasPosition(clientOffset.x, clientOffset.y, elementRef.current, zoomLevel);
      const snapPoints = [];
      const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

      if (monitor.getItemType() === ItemTypes.ELEMENT && item.id !== element.id) {
        setDragPreview({
          id: item.id,
          x: snapped.x,
          y: snapped.y,
          size: element.size || { width: 200, height: 50 },
          type: element.type,
          componentData: element.componentData,
          styles: element.styles,
        });
      } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
        setDragPreview({
          id: item.childId,
          x: snapped.x,
          y: snapped.y,
          size: element.children?.find((child) => child.id === item.childId)?.size || { width: 200, height: 50 },
          type: element.children?.find((child) => child.id === item.childId)?.type,
          componentData: element.children?.find((child) => child.id === item.childId)?.componentData,
          styles: element.children?.find((child) => child.id === item.childId)?.styles,
        });
      }
    },
    drop: (item, monitor) => {
      const clientOffset = monitor.getClientOffset();
      if (!clientOffset || !elementRef.current) return { moved: false };

      const pos = getCanvasPosition(clientOffset.x, clientOffset.y, elementRef.current, zoomLevel);
      const snapPoints = [];
      const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

      if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
        onMoveChild(item.parentId, item.childId, element.id, snapped);
        return { moved: true, parentId: element.id, newPosition: snapped };
      } else if (monitor.getItemType() === ItemTypes.ELEMENT && item.json) {
        const newChild = {
          id: `${item.json.type}-${Date.now()}`,
          type: item.json.type,
          componentData: { ...item.json.componentData, title: item.json.componentData?.title || 'Thành phần mới' },
          position: { [viewMode]: snapped },
          size: item.json.size || { width: 200, height: 50 },
          styles: item.json.styles || {},
          children: item.json.children || [],
        };
        onAddChild(element.id, newChild);
        toast.success(`Đã thêm ${item.json.componentData?.title || 'thành phần'} vào section!`, {
          position: 'bottom-right',
          autoClose: 2000,
        });
        return { moved: true, parentId: element.id, newPosition: snapped };
      }
      return { moved: false };
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  useEffect(() => {
    preview(getEmptyImage(), { captureDraggingState: true });
  }, [preview]);

  const handleSelect = useCallback(
    (e) => {
      e.stopPropagation();
      onSelectElement([element.id], e.ctrlKey);
    },
    [onSelectElement, element.id]
  );

  const handleMouseDown = useCallback(
    (e) => {
      e.stopPropagation();
      if (element.locked || element.type === 'section' || element.type === 'popup') return;

      handleSelect(e);
      const startX = e.clientX;
      const startY = e.clientY;
      const initialPos = element.position[viewMode] || { x: 0, y: 0 };

      const handleMouseMove = (moveEvent) => {
        const deltaX = (moveEvent.clientX - startX) / (zoomLevel / 100);
        const deltaY = (moveEvent.clientY - startY) / (zoomLevel / 100);
        const newPos = {
          x: Math.max(0, initialPos.x + deltaX),
          y: Math.max(0, initialPos.y + deltaY),
        };
        const snapped = snapToGrid(newPos.x, newPos.y, showGrid ? gridSize : Infinity, []);
        onUpdatePosition(element.id, { [viewMode]: snapped }, 'absolute');
      };

      const handleMouseUp = () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };

      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    },
    [element, viewMode, zoomLevel, gridSize, showGrid, onUpdatePosition, handleSelect]
  );

  const handleContextMenu = useCallback(
    (e) => {
      e.preventDefault();
      e.stopPropagation();
      const bounds = elementRef.current.getBoundingClientRect();
      onContextMenu(element.id, { x: e.clientX - bounds.left, y: e.clientY - bounds.top });
    },
    [onContextMenu, element.id]
  );

  const style = {
    position: element.type === 'section' || element.type === 'popup' ? 'absolute' : 'absolute',
    transform: `translate(${element.position[viewMode]?.x || 0}px, ${element.position[viewMode]?.y || 0}px)`,
    width: element.size?.width || (viewMode === 'mobile' ? 375 : 1200),
    height: element.size?.height || 400,
    opacity: isDragging ? 0.5 : element.locked ? 0.7 : element.visible !== false ? 1 : 0,
    cursor: element.locked ? 'not-allowed' : element.type === 'section' || element.type === 'popup' ? 'default' : 'move',
    zIndex: element.type === 'popup' ? 10000 : element.type === 'section' ? 1 : 10,
    ...element.styles,
  };

  return (
    <div
      ref={(node) => {
        elementRef.current = node;
        if (element.type !== 'section' && element.type !== 'popup') {
          drag(drop(node));
        } else {
          drop(node);
        }
      }}
      className={`lpb-element ${isSelected ? 'lpb-selected' : ''} ${isOver ? 'lpb-over' : ''} lpb-element-${element.type}`}
      style={style}
      onClick={handleSelect}
      onMouseDown={element.type === 'section' || element.type === 'popup' ? handleSelect : handleMouseDown}
      onContextMenu={handleContextMenu}
    >
      {element.type === 'popup' && visiblePopups.includes(element.id) && (
        <button
          onClick={(e) => onClosePopup(element.id, e)}
          style={{
            position: 'absolute',
            top: 10,
            right: 10,
            background: '#fff',
            border: '1px solid #e5e7eb',
            borderRadius: '50%',
            width: 24,
            height: 24,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            zIndex: 10001,
          }}
        >
          <X size={16} />
        </button>
      )}
      {renderComponentContent(element.type, element.componentData, element.styles)}
      {element.children?.map((child) => (
        <ChildElement
          key={child.id}
          child={child}
          parentId={element.id}
          isSelected={selectedChildId === child.id}
          onSelectChild={onSelectChild}
          onUpdateChildPosition={onUpdateChildPosition}
          onMoveChild={onMoveChild}
          viewMode={viewMode}
          zoomLevel={zoomLevel}
          gridSize={gridSize}
          showGrid={showGrid}
          setDragPreview={setDragPreview}
          onDeleteChild={onDeleteChild}
        />
      ))}
      {isSelected && (
        <SelectionOverlay
          element={element}
          onEdit={onEditElement}
          onDuplicate={onDuplicateElement}
          onMoveUp={onMoveElementUp}
          onMoveDown={onMoveElementDown}
          onDelete={() => onDeleteElement(element.id)}
          onSaveTemplate={() => onSaveTemplate(element)}
          onToggleVisibility={() => onToggleVisibility(element.id)}
          isVisible={element.visible !== false}
        />
      )}
    </div>
  );
};
