import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ role }) => {
    const [isCompact, setIsCompact] = useState(false);
    const location = useLocation();

    const userMenu = [
        { name: 'T·ªïng quan', path: '/dashboard', icon: 'üè†' },
        { name: 'Qu·∫£n l√Ω Landing Page', path: '/pages', icon: 'üìÑ' },
        { name: 'Qu·∫£n l√Ω Leads', path: '/leads', icon: 'üë•' },
        { name: 'Thanh to√°n & Premium', path: '/payments', icon: 'üí∞' },
        { name: 'B√°o c√°o', path: '/reports', icon: 'üìä' },
        { name: 'Qu·∫£ng c√°o & Marketing', path: '/marketing', icon: 'üìß' },
        { name: 'T∆∞∆°ng t√°c Kh√°ch h√†ng', path: '/chatbot', icon: 'üí¨' },
    ];

    const adminMenu = [
        { name: 'T·ªïng quan H·ªá th·ªëng', path: '/dashboard', icon: 'üè†' },
        { name: 'Qu·∫£n l√Ω T√†i kho·∫£n', path: '/users', icon: 'üë§' },
        { name: 'Ki·ªÉm tra Thanh to√°n', path: '/payments', icon: 'üí∞' },
        { name: 'Qu·∫£n l√Ω M·∫´u Marketplace', path: '/templates', icon: 'üìã' },
        { name: 'B√°o c√°o H·ªá th·ªëng', path: '/reports', icon: 'üìä' },
    ];

    const menu = role === 'admin' ? adminMenu : userMenu;

    return (
        <aside className={`sidebar ${isCompact ? 'compact' : ''}`}>
            <button className="sidebar-toggle" onClick={() => setIsCompact(!isCompact)}>
                {isCompact ? '‚û°Ô∏è' : '‚¨ÖÔ∏è'}
            </button>
            {menu.map((item) => {
                const isActive = location.pathname === item.path;
                return (
                    <Link
                        key={item.name}
                        to={item.path}
                        className={`sidebar-item ${isActive ? 'active' : ''}`}
                        title={isCompact ? item.name : ''}
                    >
                        <span className="sidebar-icon">{item.icon}</span>
                        {!isCompact && <span className="sidebar-text">{item.name}</span>}
                    </Link>
                );
            })}
        </aside>
    );
};

export default React.memo(Sidebar);
