import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/Marketplace.css'; // Import file CSS đã cập nhật
import DogLoader from '../components/Loader';
import { Search, Eye, Star } from 'lucide-react'; // Bỏ Heart, ShoppingCart vì đã tinh gọn

const Marketplace = () => {
    // Toàn bộ state và logic của bạn được giữ nguyên
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [pages, setPages] = useState([]);
    const [error, setError] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('all');
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('newest');
    const [priceRange, setPriceRange] = useState({ min: '', max: '' });
    const [hasMore, setHasMore] = useState(true);
    const [page, setPage] = useState(1);
    const [featuredPages, setFeaturedPages] = useState([]);
    const [bestsellers, setBestsellers] = useState([]);
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const categories = [
        { value: 'all', label: 'Tất cả' },
        { value: 'Thương mại điện tử', label: 'Thương mại điện tử' },
        { value: 'Landing Page', label: 'Landing Page' },
        // ... các categories khác
    ];

    const sortOptions = [
        { value: 'newest', label: 'Mới nhất' },
        { value: 'price_low', label: 'Giá thấp đến cao' },
        // ... các sort options khác
    ];

    useEffect(() => {
        const initializeAuth = async () => {
            // ... logic xác thực giữ nguyên
        };
        if (user?.role) {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 600, once: true, offset: 100 });
    }, []);

    useEffect(() => {
        if (userRole) {
            loadFeaturedPages();
            setPages([]);
            setPage(1);
            setHasMore(true);
            loadPages(1);
        }
    }, [userRole, selectedCategory, sortBy]);

    const loadFeaturedPages = async () => {
        // ... logic load featured pages giữ nguyên
    };

    const loadPages = async (pageNum = 1) => {
        // ... logic load pages giữ nguyên
    };

    const loadMore = () => {
        // ... logic load more giữ nguyên
    };

    const handleSearch = (e) => {
        e.preventDefault();
        setPages([]);
        setPage(1);
        setHasMore(true);
        loadPages(1);
    };

    const handleViewDetail = (pageId) => {
        navigate(`/marketplace/${pageId}`);
    };

    const formatPrice = (price) => {
        if (price === 0) return 'Miễn phí';
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(price);
    };

    const calculateDiscount = (price, originalPrice) => {
        if (!originalPrice || originalPrice <= price) return 0;
        return Math.round(((originalPrice - price) / originalPrice) * 100);
    };

    const isNewProduct = (createdAt) => {
        const createdDate = new Date(createdAt);
        const now = new Date();
        const diffTime = Math.abs(now - createdDate);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        return diffDays <= 7;
    };

    if (loading && page === 1) {
        return <DogLoader />;
    }

    return (
        <div className="marketplace-container">
            <div className="marketplace-main">
                <Header />
                <div className="marketplace-content">
                    <Sidebar userRole={userRole} />
                    <div className="marketplace-noidung">
                        {/* Hero Section và Filters giữ nguyên */}
                        <div className="marketplace-hero" data-aos="fade-down">
                            {/* ... */}
                        </div>
                        <div className="marketplace-filters" data-aos="fade-up">
                            {/* ... */}
                        </div>

                        {/* Featured Pages giữ nguyên */}
                        {featuredPages.length > 0 && (
                            <div className="featured-section" data-aos="fade-up">
                                {/* ... */}
                            </div>
                        )}

                        {error && <div className="error-message">{error}</div>}

                        {/* Main Grid Section */}
                        <div className="marketplace-grid-section" data-aos="fade-up">
                            <h2>Tất cả Landing Page</h2>
                            <InfiniteScroll
                                dataLength={pages.length}
                                next={loadMore}
                                hasMore={hasMore}
                                loader={<div className="loader">Đang tải...</div>}
                                endMessage={
                                    pages.length > 0 && (
                                        <p style={{ textAlign: 'center', marginTop: '20px', color: '#666' }}>
                                            Đã hiển thị tất cả landing page
                                        </p>
                                    )
                                }
                                className="marketplace-grid" // Áp dụng class grid trực tiếp vào InfiniteScroll
                            >
                                {/* ================================================= */}
                                {/* === BẮT ĐẦU PHẦN JSX MỚI CHO CARD (THAY THẾ) === */}
                                {/* ================================================= */}
                                {pages.map((page) => (
                                    <div
                                        key={page._id}
                                        className="marketplace-card"
                                        data-aos="zoom-in"
                                        onClick={() => handleViewDetail(page._id)}
                                    >
                                        <div className="card-image">
                                            <img
                                                src={page.main_screenshot || '/placeholder.png'}
                                                alt={page.title}
                                                className="card-img"
                                                loading="lazy"
                                            />

                                            <div className="card-badge-container">
                                                {isNewProduct(page.created_at) && (
                                                    <div className="card-badge new-badge">Mới</div>
                                                )}
                                                {page.is_bestseller && (
                                                    <div className="card-badge bestseller-badge">Bán chạy</div>
                                                )}
                                                {calculateDiscount(page.price, page.original_price) > 0 && (
                                                    <div className="card-badge discount-badge">
                                                        -{calculateDiscount(page.price, page.original_price)}%
                                                    </div>
                                                )}
                                            </div>

                                            <div className="image-overlay">
                                                <button
                                                    className="view-detail-btn-overlay"
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        handleViewDetail(page._id);
                                                    }}
                                                >
                                                    Xem chi tiết
                                                </button>
                                            </div>
                                        </div>

                                        <div className="card-content">
                                            <div className="card-category">{page.category}</div>
                                            <h3 className="card-title" title={page.title}>{page.title}</h3>

                                            <div className="card-info-footer">
                                                <div className="card-meta">
                                                    <span><Eye size={14} /> {page.views || 0}</span>
                                                    <span><Star size={14} /> {page.rating ? page.rating.toFixed(1) : '0.0'}</span>
                                                </div>
                                                <div className="card-price">
                                                    <span className="current-price">{formatPrice(page.price)}</span>
                                                    {page.original_price && page.original_price > page.price && (
                                                        <span className="original-price">{formatPrice(page.original_price)}</span>
                                                    )}
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                                {/* ================================================= */}
                                {/* === KẾT THÚC PHẦN JSX MỚI CHO CARD             === */}
                                {/* ================================================= */}
                            </InfiniteScroll>
                            {pages.length === 0 && !loading && (
                                <div className="empty-state">
                                    <p>Không tồn tại landing page nào</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Marketplace;
