import React from 'react';

const PropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle }) => {
    if (isCollapsed) {
        return (
            <div className="w-12 bg-white border-l border-gray-200 h-full flex flex-col items-center py-2">
                <button onClick={onToggle} className="p-2 hover:bg-gray-100 rounded">
                    <i className="fas fa-chevron-left"></i>
                </button>
            </div>
        );
    }

    if (!selectedElement) {
        return (
            <div className="w-80 bg-white border-l border-gray-200 p-4">
                <div className="flex items-center justify-between border-b pb-4 mb-4">
                    <h3 className="font-semibold text-gray-800">Thuộc tính</h3>
                    <button onClick={onToggle} className="p-1 hover:bg-gray-100 rounded">
                        <i className="fas fa-chevron-right"></i>
                    </button>
                </div>
                <div className="text-center text-gray-500">
                    <i className="fas fa-mouse-pointer text-4xl mb-4"></i>
                    <p>Chọn một element để chỉnh sửa thuộc tính</p>
                </div>
            </div>
        );
    }

    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleContentChange = (field, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                content: {
                    ...selectedElement.json.content,
                    [field]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleBackgroundChange = (value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                background: { value },
            },
        };
        onUpdateElement(updated);
    };

    return (
        <div className="w-80 bg-white border-l border-gray-200 h-full overflow-y-auto">
            <div className="p-4 border-b flex items-center justify-between">
                <h3 className="font-semibold text-gray-800">Thuộc tính</h3>
                <button onClick={onToggle} className="p-1 hover:bg-gray-100 rounded">
                    <i className="fas fa-chevron-right"></i>
                </button>
            </div>

            <div className="p-4 space-y-6">
                {/* Content Section - Enhanced for different types */}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Nội dung</label>
                    {selectedElement.json.type === 'heading' || selectedElement.json.type === 'paragraph' || selectedElement.json.type === 'button' ? (
                        <textarea
                            value={selectedElement.json.content}
                            onChange={(e) => handleContentChange('text', e.target.value)} // Use field for complex content
                            className="w-full p-2 border border-gray-300 rounded-md resize-none"
                            rows={3}
                        />
                    ) : selectedElement.json.type === 'section' && selectedElement.json.layout === 'hero' ? (
                        <>
                            <input
                                type="text"
                                value={selectedElement.json.content.title || ''}
                                onChange={(e) => handleContentChange('title', e.target.value)}
                                placeholder="Title"
                                className="w-full p-2 border border-gray-300 rounded-md mb-2"
                            />
                            <textarea
                                value={selectedElement.json.content.subtitle || ''}
                                onChange={(e) => handleContentChange('subtitle', e.target.value)}
                                placeholder="Subtitle"
                                className="w-full p-2 border border-gray-300 rounded-md resize-none"
                                rows={2}
                            />
                            {/* Add more fields for buttons if needed */}
                        </>
                    ) : null}
                </div>

                {/* Typography - Applicable to text-based elements */}
                <div>
                    <h4 className="font-medium text-gray-700 mb-3">Typography</h4>
                    <div className="space-y-3">
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Font Size</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.fontSize || '16px'}
                                onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            />
                        </div>
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Font Weight</label>
                            <select
                                value={selectedElement.json.styles?.fontWeight || 'normal'}
                                onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            >
                                <option value="normal">Normal</option>
                                <option value="bold">Bold</option>
                                <option value="600">Semi Bold</option>
                                <option value="300">Light</option>
                            </select>
                        </div>
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Color</label>
                            <input
                                type="color"
                                value={selectedElement.json.styles?.color || '#000000'}
                                onChange={(e) => handleStyleChange('color', e.target.value)}
                                className="w-full h-10 border border-gray-300 rounded-md"
                            />
                        </div>
                        {/* Add more typography options like fontFamily, lineHeight, etc. */}
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Font Family</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.fontFamily || 'Arial'}
                                onChange={(e) => handleStyleChange('fontFamily', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            />
                        </div>
                    </div>
                </div>

                {/* Background for buttons and sections */}
                {(selectedElement.json.type === 'button' || selectedElement.json.type === 'section') && (
                    <div>
                        <h4 className="font-medium text-gray-700 mb-3">Background</h4>
                        <input
                            type="color"
                            value={selectedElement.json.styles?.backgroundColor || selectedElement.json.background?.value || '#007bff'}
                            onChange={(e) => selectedElement.json.type === 'section' ? handleBackgroundChange(e.target.value) : handleStyleChange('backgroundColor', e.target.value)}
                            className="w-full h-10 border border-gray-300 rounded-md"
                        />
                    </div>
                )}

                {/* Add more sections for other properties like padding, margin, border, etc. */}
                <div>
                    <h4 className="font-medium text-gray-700 mb-3">Spacing</h4>
                    <div className="space-y-3">
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Padding</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.padding || '0px'}
                                onChange={(e) => handleStyleChange('padding', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            />
                        </div>
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Margin</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.margin || '0px'}
                                onChange={(e) => handleStyleChange('margin', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            />
                        </div>
                    </div>
                </div>

                {/* You can add more customizable properties here, e.g., for images, forms, etc. */}
            </div>
        </div>
    );
};

export default PropertiesPanel;
