import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import Rulers from './Rulers';
import SelectionOverlay from './SelectionOverlay';
import QuickEditToolkit from './QuickEditToolkit';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
    RESIZE_HANDLE: 'resizeHandle',
    CHILD_ELEMENT: 'childElement',
};

// Tính toán tọa độ chính xác trong canvas
const getCanvasPosition = (mouseX, mouseY, canvasElement, zoomLevel) => {
    if (!canvasElement) {
        console.error('Canvas element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }
    const canvasRect = canvasElement.getBoundingClientRect();
    const scrollX = canvasElement.scrollLeft || 0;
    const scrollY = canvasElement.scrollTop || 0;
    const rawX = (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100);
    const x = Math.max(0, rawX);
    const y = Math.max(0, rawY);
    return { x, y };
};

// Snap to grid
const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;
    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });
    return { x: snappedX, y: snappedY };
};

// Tính bounds của element
const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

// Component con
const ChildElement = ({ child, parentId, onSelectChild, isSelected, zoomLevel, onEdit, onDuplicate, onDelete }) => {
    const { id, type, content, styles } = child;
    const [{ isDragging }, drag] = useDrag(() => ({
        type: ItemTypes.CHILD_ELEMENT,
        item: { id, parentId, type, content, styles, isChild: true },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    }));

    const handleClick = (e) => {
        e.stopPropagation();
        onSelectChild(parentId, id);
    };

    const adjustedStyles = {
        ...styles,
        fontSize: styles.fontSize ? `${parseFloat(styles.fontSize) / (zoomLevel / 100)}px` : undefined,
        margin: styles.margin ? `${parseFloat(styles.margin) / (zoomLevel / 100)}px` : undefined,
        cursor: isDragging ? 'grabbing' : 'pointer',
    };

    return (
        <div
            ref={drag}
            className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''}`}
            style={adjustedStyles}
            onClick={handleClick}
        >
            {type === 'heading' && <h2 style={adjustedStyles}>{content}</h2>}
            {type === 'paragraph' && <p style={adjustedStyles}>{content}</p>}
            {isSelected && (
                <QuickEditToolkit
                    element={{ id, type, content, styles }}
                    position={{ x: 0, y: -40 }}
                    onEdit={() => onEdit(parentId, id)}
                    onDuplicate={() => onDuplicate(parentId, id)}
                    onDelete={() => onDelete(parentId, id)}
                    onMoveUp={() => {}}
                    onMoveDown={() => {}}
                    onClose={() => {}}
                />
            )}
        </div>
    );
};

// Render nội dung component
const renderComponentContent = (type, componentData, styles, zoomLevel, children = [], onSelectChild, selectedChildId, parentId, onEdit, onDuplicate, onDelete) => {
    const adjustedStyles = {
        ...styles,
        fontSize: styles.fontSize ? `${parseFloat(styles.fontSize) / (zoomLevel / 100)}px` : undefined,
        padding: styles.padding ? `${parseFloat(styles.padding) / (zoomLevel / 100)}px` : undefined,
        margin: styles.margin ? `${parseFloat(styles.margin) / (zoomLevel / 100)}px` : undefined,
        borderRadius: styles.borderRadius ? `${parseFloat(styles.borderRadius) / (zoomLevel / 100)}px` : undefined,
        width: styles.width ? `${parseFloat(styles.width) / (zoomLevel / 100)}px` : undefined,
        height: styles.height ? `${parseFloat(styles.height) / (zoomLevel / 100)}px` : undefined,
    };

    switch (type) {
        case 'section':
            return (
                <div style={{ ...adjustedStyles, display: 'flex', flexDirection: 'column', gap: `${10 / (zoomLevel / 100)}px`, height: '100%' }}>
                    {children.map((child) => (
                        <ChildElement
                            key={child.id}
                            child={child}
                            parentId={parentId}
                            onSelectChild={onSelectChild}
                            isSelected={selectedChildId === child.id}
                            zoomLevel={zoomLevel}
                            onEdit={onEdit}
                            onDuplicate={onDuplicate}
                            onDelete={onDelete}
                        />
                    ))}
                </div>
            );
        case 'heading':
            return <h2 style={adjustedStyles}>{componentData.content || 'Tiêu đề'}</h2>;
        case 'paragraph':
            return <p style={adjustedStyles}>{componentData.content || 'Đoạn văn'}</p>;
        case 'button':
            return (
                <button
                    style={{ ...adjustedStyles, cursor: 'pointer' }}
                    onClick={componentData.onClick ? () => eval(componentData.onClick) : undefined}
                    onMouseEnter={componentData.onHover ? () => eval(componentData.onHover) : undefined}
                >
                    {componentData.content || 'Nút'}
                </button>
            );
        default:
            return <div style={{ ...adjustedStyles, color: '#ff0000' }}>Unknown Component: {type}</div>;
    }
};

// Component Element
const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onEdit,
         onDuplicate,
         onDelete,
         onMoveUp,
         onMoveDown,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {}, children = [] } = element;
        const elementRef = useRef(null);
        const [isDragging, setIsDragging] = useState(false);
        const [showToolkit, setShowToolkit] = useState(false);

        const [{ isDragging: dragActive }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: () => {
                setIsDragging(true);
                return { id, elementSize: size, elementPosition: position[viewMode] || { x: 0, y: 0 }, isExisting: true, styles, componentData, type, children };
            },
            end: (item, monitor) => {
                setIsDragging(false);
                const dropResult = monitor.getDropResult();
                if (!dropResult || !dropResult.moved) return;
                onUpdatePosition(item.id, dropResult.newPosition, 'replace');
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode, styles, componentData, type, children]);

        const [{ isDragging: resizeDragging }, resizeDrag] = useDrag(() => ({
            type: ItemTypes.RESIZE_HANDLE,
            item: { id, handle: 'bottom-right' },
            end: (item, monitor) => {
                const delta = monitor.getDifferenceFromInitialOffset();
                if (delta) {
                    const newSize = {
                        width: Math.max(50, size.width + delta.x / (zoomLevel / 100)),
                        height: Math.max(50, size.height + delta.y / (zoomLevel / 100)),
                    };
                    onUpdateSize(id, newSize);
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, zoomLevel, onUpdateSize]);

        useEffect(() => {
            drag(getEmptyImage(), { captureDraggingState: true });
        }, [drag]);

        const handleMouseDown = useCallback((e) => {
            e.stopPropagation();
            onSelectElement(id, e.ctrlKey);
            setShowToolkit(true);
        }, [id, onSelectElement]);

        const handleContextMenu = useCallback((e) => {
            e.preventDefault();
            e.stopPropagation();
            onContextMenu(id, { x: e.clientX, y: e.clientY });
        }, [id, onContextMenu]);

        const adjustedStyles = {
            position: 'absolute',
            transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
            width: size.width / (zoomLevel / 100),
            height: size.height / (zoomLevel / 100),
            zIndex: position.z || 1,
            cursor: dragActive || resizeDragging ? 'grabbing' : 'grab',
        };

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${dragActive ? 'lpb-canvas-element-dragging' : ''}`}
                style={adjustedStyles}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
                onMouseEnter={() => setShowToolkit(true)}
                onMouseLeave={() => setShowToolkit(false)}
            >
                {renderComponentContent(
                    type,
                    componentData,
                    styles,
                    zoomLevel,
                    children,
                    onSelectChild,
                    selectedChildId,
                    id,
                    onEdit,
                    onDuplicate,
                    onDelete
                )}
                {isSelected && (
                    <>
                        <div
                            ref={resizeDrag}
                            className="lpb-selection-handle lpb-selection-handle-bottom-right"
                            style={{ cursor: 'se-resize', width: 8, height: 8, background: '#2563eb', borderRadius: '50%' }}
                        />
                        {showToolkit && (
                            <QuickEditToolkit
                                element={element}
                                position={{ x: adjustedStyles.width + 10, y: -40 }}
                                onEdit={() => onEdit(id)}
                                onDuplicate={() => onDuplicate(id)}
                                onDelete={() => onDelete(id)}
                                onMoveUp={() => onMoveUp(id)}
                                onMoveDown={() => onMoveDown(id)}
                                onClose={() => setShowToolkit(false)}
                            />
                        )}
                    </>
                )}
            </div>
        );
    }
);

// Component Canvas
const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
         onEditElement,
         onDuplicateElement,
         onMoveElementUp,
         onMoveElementDown,
         onSelectChild,
         selectedChildId,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);
        const [dragPreview, setDragPreview] = useState(null);

        const canvasBounds = canvasRef.current
            ? {
                width: canvasRef.current.offsetWidth / (zoomLevel / 100),
                height: canvasRef.current.offsetHeight / (zoomLevel / 100),
            }
            : { width: 1200, height: 1000 };

        const getSnapPoints = useCallback(() => {
            const points = [
                { x: 0, y: 0 },
                { x: canvasBounds.width, y: canvasBounds.height },
                { x: canvasBounds.width / 2, y: canvasBounds.height / 2 },
            ];
            pageData.elements.forEach((el) => {
                const bounds = getElementBounds(el);
                points.push(
                    { x: bounds.left, y: bounds.top },
                    { x: bounds.right, y: bounds.bottom },
                    { x: bounds.centerX, y: bounds.centerY }
                );
            });
            return points;
        }, [pageData.elements, canvasBounds]);

        const [{ isOver, dragItem }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT, ItemTypes.CHILD_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) return;

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.EXISTING_ELEMENT && item.isExisting) {
                    const draggingElement = pageData.elements.find((el) => el.id === item.id);
                    if (draggingElement) {
                        setDragPreview({
                            id: item.id,
                            x: snapped.x,
                            y: snapped.y,
                            size: draggingElement.size,
                            type: draggingElement.type,
                            componentData: draggingElement.componentData,
                            styles: draggingElement.styles,
                            children: draggingElement.children,
                        });
                    }
                } else if (item.json) {
                    setDragPreview({
                        id: 'preview',
                        x: snapped.x,
                        y: snapped.y,
                        size: item.json.size || { width: 200, height: 50 },
                        type: item.json.type,
                        componentData: item.json.componentData,
                        styles: item.json.styles,
                        children: item.json.children || [],
                    });
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    return { moved: false };
                }

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (item.json && !item.isExisting) {
                    const newElement = {
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData,
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped,
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                    };
                    onAddElement(newElement);
                    toast.success('Đã thêm phần tử!');
                } else if (item.id && item.isExisting && !item.isChild) {
                    const elementToUpdate = pageData.elements.find((el) => el.id === item.id);
                    if (elementToUpdate) {
                        const updatedElement = {
                            ...elementToUpdate,
                            position: {
                                ...elementToUpdate.position,
                                [viewMode]: snapped,
                            },
                        };
                        onUpdatePosition(item.id, updatedElement.position, 'replace');
                        toast.success('Đã di chuyển phần tử!');
                    }
                }
                setDragPreview(null);
                setGuidelines([]);
                return { moved: true, newPosition: snapped };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                dragItem: monitor.getItem(),
            }),
        }), [pageData.elements, viewMode, zoomLevel, gridSize, showGrid, canvasBounds, onAddElement, onUpdatePosition]);

        const handleContextMenu = useCallback((id, position) => {
            setContextMenu({ id, position });
        }, []);

        const handleCanvasClick = useCallback((e) => {
            if (e.target === canvasRef.current) {
                onSelectElement([]);
                onSelectChild(null, null);
                setContextMenu(null);
            }
        }, [onSelectElement, onSelectChild]);

        const handleKeyDown = useCallback(
            (e) => {
                const ids = Array.isArray(selectedIds) ? selectedIds : [];
                if (!ids.length) return;

                e.preventDefault();
                e.stopPropagation();

                const step = e.shiftKey ? 10 : 1;

                ids.forEach((id) => {
                    const element = pageData.elements.find((el) => el.id === id);
                    if (element) {
                        const newY = e.key === 'ArrowUp' ? Math.max(0, (element.position[viewMode]?.y || 0) - step) : (element.position[viewMode]?.y || 0) + step;
                        const newX = e.key === 'ArrowLeft' ? Math.max(0, (element.position[viewMode]?.x || 0) - step) : (element.position[viewMode]?.x || 0) + step;
                        switch (e.key) {
                            case 'ArrowUp':
                            case 'ArrowDown':
                                onUpdatePosition(id, { [viewMode]: { x: element.position[viewMode]?.x || 0, y: newY } }, 'absolute');
                                break;
                            case 'ArrowLeft':
                            case 'ArrowRight':
                                onUpdatePosition(id, { [viewMode]: { x: newX, y: element.position[viewMode]?.y || 0 } }, 'absolute');
                                break;
                            case 'Delete':
                            case 'Backspace':
                                onDeleteElement(id);
                                break;
                            case 'Escape':
                                onSelectElement([]);
                                onSelectChild(null, null);
                                setContextMenu(null);
                                break;
                            default:
                                break;
                        }
                    }
                });
            },
            [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, onSelectChild, viewMode, pageData.elements]
        );

        useHotkeys('ctrl+a', (e) => {
            e.preventDefault();
            onSelectElement(pageData.elements.map((el) => el.id));
        }, [pageData.elements, onSelectElement]);

        useHotkeys('ctrl+g', (e) => {
            e.preventDefault();
            const ids = Array.isArray(selectedIds) ? selectedIds : [];
            if (ids.length > 1) {
                onGroupElements(ids);
            }
        }, [selectedIds, onGroupElements]);

        useEffect(() => {
            const handleClickOutside = () => setContextMenu(null);
            if (contextMenu) {
                document.addEventListener('click', handleClickOutside);
                return () => document.removeEventListener('click', handleClickOutside);
            }
        }, [contextMenu]);

        const renderDragPreview = () => {
            if (!dragPreview) return null;

            const { x, y, size, type, componentData, styles, children } = dragPreview;
            const previewStyle = {
                position: 'absolute',
                transform: `translate(${x}px, ${y}px)`,
                width: size.width / (zoomLevel / 100),
                height: size.height / (zoomLevel / 100),
                border: '2px dashed #3b82f6',
                background: 'rgba(59, 130, 246, 0.1)',
                pointerEvents: 'none',
                zIndex: 1000,
                opacity: 0.5,
                ...styles,
            };

            return (
                <div className="lpb-drag-preview" style={previewStyle}>
                    {renderComponentContent(type, componentData, styles, zoomLevel, children, onSelectChild, selectedChildId, null, onEditElement, onDuplicateElement, onDeleteElement)}
                </div>
            );
        };

        return (
            <div className="lpb-canvas-wrapper" onKeyDown={handleKeyDown} tabIndex={0} style={{ outline: 'none' }}>
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    style={{
                        position: 'relative',
                        minHeight: '100%',
                        background: pageData.canvas?.background || '#ffffff',
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top left',
                        overflow: 'visible',
                    }}
                    onClick={handleCanvasClick}
                >
                    <Rulers canvasWidth={pageData.canvas?.width || 1200} canvasHeight={1000} zoomLevel={zoomLevel} />
                    <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                    {pageData.elements.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={Array.isArray(selectedIds) && selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            onUpdateSize={onUpdateSize}
                            onEdit={onEditElement}
                            onDuplicate={onDuplicateElement}
                            onDelete={onDeleteElement}
                            onMoveUp={onMoveElementUp}
                            onMoveDown={onMoveElementDown}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={showGrid ? gridSize : Infinity}
                            onContextMenu={handleContextMenu}
                            canvasBounds={canvasBounds}
                            canvasRef={canvasRef}
                            onSelectChild={onSelectChild}
                            selectedChildId={selectedChildId}
                        />
                    ))}
                    {renderDragPreview()}
                    <SelectionOverlay
                        selectedElements={pageData.elements.filter((el) => Array.isArray(selectedIds) && selectedIds.includes(el.id))}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />
                    {contextMenu && (
                        <div
                            className="lpb-context-menu"
                            style={{
                                position: 'fixed',
                                left: contextMenu.position.x,
                                top: contextMenu.position.y,
                                zIndex: 10000,
                                background: '#fff',
                                border: '1px solid #e5e7eb',
                                borderRadius: '6px',
                                boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                                minWidth: '140px',
                            }}
                        >
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '10px 16px', cursor: 'pointer' }}
                                onClick={() => {
                                    onDeleteElement(contextMenu.id);
                                    setContextMenu(null);
                                }}
                            >
                                Delete
                            </div>
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '10px 16px', cursor: 'pointer' }}
                                onClick={() => {
                                    onGroupElements([contextMenu.id]);
                                    setContextMenu(null);
                                }}
                            >
                                Group
                            </div>
                        </div>
                    )}
                </div>
            </div>
        );
    }
);

export default Canvas;
