import React, { Suspense, useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import '../styles/Dashboard.css';

const UserDashboardContent = React.lazy(() => import('./UserDashboardContent'));
const AdminDashboardContent = React.lazy(() => import('./AdminDashboardContent'));

const Dashboard = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);

    useEffect(() => {
        if (user?.role) {
            setUserRole(user.role);
        } else if (localStorage.getItem('token')) {
            try {
                const decodedToken = jwtDecode(localStorage.getItem('token'));
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('Error decoding token:', err);
            }
        }
        setLoading(false);
    }, [user]);

    const renderDashboard = () => {
        if (loading) return <div>Đang tải...</div>;

        switch (userRole) {
            case 'admin':
                return (
                    <Suspense fallback={<div>Đang tải Admin Dashboard...</div>}>
                        <AdminDashboardContent />
                    </Suspense>
                );
            case 'user':
                return (
                    <Suspense fallback={<div>Đang tải User Dashboard...</div>}>
                        <UserDashboardContent />
                    </Suspense>
                );
            default:
                return <div>Role không hợp lệ hoặc chưa đăng nhập</div>;
        }
    };

    return (
        <div className="dashboard-container">
            <Header role={userRole} />
            <div className="dashboard-main">
                <Sidebar role={userRole} />
                <div className="dashboard-content">{renderDashboard()}</div>
            </div>
        </div>
    );
};

export default React.memo(Dashboard);
