import React, { useState, useEffect, useCallback } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import { parse } from 'node-html-parser';
import Canvas from '../components/create-page/Canvas';
import ComponentLibrary from '../components/create-page/ComponentLibrary';
import PropertiesPanel from '../components/create-page/PropertiesPanel';
import Toolbar from './create-page/Toolbar';
import api from '@landinghub/api';
import '../styles/CreateLanding.css';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

// Định nghĩa các biến CSS tương ứng với CreateLanding.css
const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

// Hàm parse styles từ chuỗi style
const parseStyles = (styleString) => {
    if (!styleString) return {};
    return styleString.split(';').reduce((acc, style) => {
        const [key, value] = style.split(':').map(s => s.trim());
        if (key && value) {
            const camelKey = key.replace(/-([a-z])/g, (g) => g[1].toUpperCase());
            acc[camelKey] = value;
        }
        return acc;
    }, {});
};

// Hàm parse HTML từ S3 thành pageData
export const parseHTMLToPageData = (html) => {
    try {
        console.log('Parsing HTML from S3:', html);
        const root = parse(html, { blockTextElements: { style: true } });
        const pageContainer = root.querySelector('.page-container');
        if (!pageContainer) {
            console.warn('No .page-container found in HTML');
            toast.warn('HTML không chứa .page-container. Khởi tạo trang trống.');
            return { canvas: { width: 1200, background: '#ffffff' }, elements: [] };
        }

        const elements = [];
        pageContainer.querySelectorAll('.ladi-section, .lpb-canvas-element').forEach((elementNode, index) => {
            const type = elementNode.getAttribute('data-type') || 'section';
            const styles = parseStyles(elementNode.getAttribute('style') || '');
            const componentData = elementNode.getAttribute('data-component') ? JSON.parse(elementNode.getAttribute('data-component')) : {};
            const position = {
                desktop: elementNode.getAttribute('data-position-desktop') ? JSON.parse(elementNode.getAttribute('data-position-desktop')) : { x: parseFloat(styles.left) || 0, y: parseFloat(styles.top) || 0 },
                tablet: elementNode.getAttribute('data-position-tablet') ? JSON.parse(elementNode.getAttribute('data-position-tablet')) : { x: parseFloat(styles.left) || 0, y: parseFloat(styles.top) || 0 },
                mobile: elementNode.getAttribute('data-position-mobile') ? JSON.parse(elementNode.getAttribute('data-position-mobile')) : { x: parseFloat(styles.left) || 0, y: parseFloat(styles.top) || 0 },
            };
            const size = {
                width: parseFloat(styles.width) || (type === 'section' ? 420 : 200),
                height: parseFloat(styles.height) || (type === 'section' ? 400 : 50),
            };

            const children = [];
            if (type === 'section') {
                const containerNode = elementNode.querySelector('.ladi-container');
                if (containerNode) {
                    containerNode.querySelectorAll('.lpb-child-element').forEach((child, childIndex) => {
                        const childType = child.getAttribute('data-type') || child.tagName.toLowerCase();
                        const childStyles = parseStyles(child.getAttribute('style') || '');
                        const childComponentData = child.getAttribute('data-component') ? JSON.parse(child.getAttribute('data-component')) : {};
                        const childPosition = {
                            desktop: child.getAttribute('data-position-desktop') ? JSON.parse(child.getAttribute('data-position-desktop')) : { x: parseFloat(childStyles.left) || 0, y: parseFloat(childStyles.top) || 0 },
                            tablet: child.getAttribute('data-position-tablet') ? JSON.parse(child.getAttribute('data-position-tablet')) : { x: parseFloat(childStyles.left) || 0, y: parseFloat(childStyles.top) || 0 },
                            mobile: child.getAttribute('data-position-mobile') ? JSON.parse(child.getAttribute('data-position-mobile')) : { x: parseFloat(childStyles.left) || 0, y: parseFloat(childStyles.top) || 0 },
                        };
                        const childSize = {
                            width: parseFloat(childStyles.width) || 200,
                            height: parseFloat(childStyles.height) || 50,
                        };

                        if (childType === 'heading') {
                            children.push({
                                id: `child-${index}-${childIndex}-${Date.now()}`,
                                type: 'heading',
                                componentData: { content: child.textContent.trim() || 'Tiêu đề' },
                                styles: childStyles,
                                position: childPosition,
                                size: childSize,
                            });
                        } else if (childType === 'paragraph') {
                            children.push({
                                id: `child-${index}-${childIndex}-${Date.now()}`,
                                type: 'paragraph',
                                componentData: { content: child.textContent.trim() || 'Đoạn văn' },
                                styles: childStyles,
                                position: childPosition,
                                size: childSize,
                            });
                        } else if (childType === 'button') {
                            children.push({
                                id: `child-${index}-${childIndex}-${Date.now()}`,
                                type: 'button',
                                componentData: { content: child.textContent.trim() || 'Nút' },
                                styles: childStyles,
                                position: childPosition,
                                size: childSize,
                            });
                        } else if (childType === 'image') {
                            children.push({
                                id: `child-${index}-${childIndex}-${Date.now()}`,
                                type: 'image',
                                componentData: { src: child.getAttribute('src') || '' },
                                styles: childStyles,
                                position: childPosition,
                                size: childSize,
                            });
                        } else if (childType === 'form') {
                            const fields = [];
                            child.querySelectorAll('input').forEach((input) => {
                                const inputStyles = parseStyles(input.getAttribute('style') || '');
                                fields.push({
                                    type: input.getAttribute('type') || 'text',
                                    placeholder: input.getAttribute('placeholder') || 'Nhập...',
                                    padding: inputStyles.padding || '8px',
                                    borderRadius: inputStyles.borderRadius || '8px',
                                    border: inputStyles.border || '1px solid #ccc',
                                    fontSize: inputStyles.fontSize || '1rem',
                                });
                            });
                            const button = child.querySelector('button');
                            const buttonStyles = button ? parseStyles(button.getAttribute('style') || '') : {};
                            children.push({
                                id: `child-${index}-${childIndex}-${Date.now()}`,
                                type: 'form',
                                componentData: {
                                    title: child.querySelector('h3')?.textContent.trim() || '',
                                    fields,
                                    buttonText: button?.textContent.trim() || 'Gửi',
                                    buttonBackground: buttonStyles.background || '#2563eb',
                                    buttonColor: buttonStyles.color || '#fff',
                                },
                                styles: childStyles,
                                position: childPosition,
                                size: childSize,
                            });
                        }
                        // Thêm case cho các loại khác từ EXTENDED_LIBRARY
                    });
                }
            }

            elements.push({
                id: `element-${index}-${Date.now()}`,
                type: type,
                componentData,
                position,
                size,
                styles,
                children,
            });
        });

        const pageData = { canvas: { width: 1200, background: '#ffffff' }, elements };
        console.log('Parsed pageData:', JSON.stringify(pageData, null, 2));
        return pageData;
    } catch (err) {
        console.error('Lỗi khi parse HTML:', err);
        toast.error('Lỗi khi phân tích nội dung trang. Khởi tạo trang trống.');
        return { canvas: { width: 1200, background: '#ffffff' }, elements: [] };
    }
};

// Hàm render HTML tĩnh từ pageData
export const renderStaticHTML = (pageData) => {
    console.log('Rendering static HTML with pageData:', JSON.stringify(pageData, null, 2));

    const canvasWidth = pageData.canvas?.width || '1200px';
    const canvasBackground = pageData.canvas?.background || '#ffffff';

    let html = `
<!DOCTYPE html>
<html lang="vi">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Landing Page</title>
    <style>
        :root {
            --primary: ${CSS_VARIABLES['--primary']};
            --primary-dark: ${CSS_VARIABLES['--primary-dark']};
            --primary-light: ${CSS_VARIABLES['--primary-light']};
            --secondary: ${CSS_VARIABLES['--secondary']};
            --accent: ${CSS_VARIABLES['--accent']};
            --success: ${CSS_VARIABLES['--success']};
            --warning: ${CSS_VARIABLES['--warning']};
            --danger: ${CSS_VARIABLES['--danger']};
            --dark: ${CSS_VARIABLES['--dark']};
            --light: ${CSS_VARIABLES['--light']};
            --white: ${CSS_VARIABLES['--white']};
            --text-primary: ${CSS_VARIABLES['--text-primary']};
            --text-secondary: ${CSS_VARIABLES['--text-secondary']};
            --text-light: ${CSS_VARIABLES['--text-light']};
            --text-inverse: ${CSS_VARIABLES['--text-inverse']};
            --border-color: ${CSS_VARIABLES['--border-color']};
            --border-radius: ${CSS_VARIABLES['--border-radius']};
            --shadow: ${CSS_VARIABLES['--shadow']};
        }
        body {
            font-family: 'Inter', Arial, sans-serif;
            margin: 0;
            padding: 0;
            background: ${canvasBackground};
        }
        .page-container {
            position: relative;
            width: ${canvasWidth};
            min-height: 100vh;
            margin: 0 auto;
            background: ${canvasBackground};
        }
        .ladi-section {
            width: 100%;
            max-width: 1200px;
            margin: 0 auto;
            position: relative;
            overflow: hidden;
        }
        .ladi-section-background {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 0;
        }
        .ladi-overlay {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 1;
            pointer-events: none;
        }
        .ladi-container {
            position: relative;
            z-index: 2;
            padding: 20px;
            min-height: 100%;
            box-sizing: border-box;
        }
        .lpb-canvas-element, .lpb-child-element {
            position: absolute;
            cursor: move;
        }
        .lpb-canvas-element:hover, .lpb-child-element:hover {
            outline: 2px dashed var(--primary);
        }
        @media (max-width: 768px) {
            .page-container {
                width: 100%;
            }
            .ladi-section {
                width: 100%;
                min-width: auto;
            }
            .lpb-child-element {
                left: 0 !important;
                width: 100% !important;
            }
        }
        @media (max-width: 480px) {
            .lpb-child-element {
                left: 0 !important;
                width: 100% !important;
            }
        }
        .ladi-card, .ladi-product, .ladi-testimonial, .ladi-box {
            padding: 16px;
            box-sizing: border-box;
        }
        .ladi-menu {
            display: flex;
            gap: 20px;
            list-style: none;
            padding: 0;
        }
        .ladi-menu a {
            color: inherit;
            text-decoration: none;
        }
        .ladi-grid {
            display: grid;
        }
        .ladi-list {
            list-style: none;
            padding: 0;
        }
        .ladi-video {
            max-width: 100%;
        }
        .ladi-icon {
            display: inline-block;
        }
        .ladi-gallery {
            display: grid;
            gap: 10px;
        }
        .ladi-carousel {
            overflow: hidden;
            position: relative;
        }
        .ladi-tabs {
            display: flex;
            flex-direction: column;
        }
        .ladi-tabs-nav {
            display: flex;
            gap: 10px;
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-tab {
            padding: 8px 16px;
            cursor: pointer;
        }
        .ladi-tab-content {
            padding: 16px;
        }
        .ladi-accordion-item {
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-accordion-title {
            padding: 8px;
            cursor: pointer;
        }
        .ladi-table {
            width: 100%;
            border-collapse: collapse;
        }
        .ladi-table th, .ladi-table td {
            border: 1px solid var(--border-color);
            padding: 8px;
            text-align: left;
        }
        .ladi-survey {
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .ladi-html {
            overflow: auto;
        }
        .ladi-collection {
            display: flex;
            flex-direction: column;
            gap: 20px;
        }
        .upload-input {
            margin: 10px 0;
        }
    </style>
    <script>
        function handleDragStart(event) {
            event.dataTransfer.setData('text/plain', event.target.id);
            event.target.style.opacity = '0.5';
        }
        function handleDragOver(event) {
            event.preventDefault();
        }
        function handleDrop(event) {
            event.preventDefault();
            const id = event.dataTransfer.getData('text/plain');
            const element = document.getElementById(id);
            const rect = event.currentTarget.getBoundingClientRect();
            const x = event.clientX - rect.left;
            const y = event.clientY - rect.top;
            element.style.left = x + 'px';
            element.style.top = y + 'px';
            element.style.opacity = '1';
            console.log('Dropped element:', id, 'at position:', { x, y });
        }
        function handleFileUpload(event, elementId) {
            const file = event.target.files[0];
            if (file) {
                console.log('Uploading file:', file.name, 'for element:', elementId);
                // Simulate S3 upload (replace with actual API call)
                const formData = new FormData();
                formData.append('file', file);
                fetch('/api/upload', {
                    method: 'POST',
                    body: formData
                })
                .then(response => response.json())
                .then(data => {
                    console.log('Uploaded to S3:', data.url);
                    const img = document.getElementById(elementId + '-img');
                    if (img) img.src = data.url;
                })
                .catch(error => console.error('Upload failed:', error));
            }
        }
    </script>
</head>
<body>
    <div class="page-container" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">
    `;

    const renderElement = (element, isChild = false, parentType = '') => {
        const { id, type, componentData = {}, position = {}, size = {}, styles = {}, children = [] } = element;

        // Normalize styles and position
        const positionStyles = position.desktop ? `left: ${position.desktop.x || 0}px; top: ${position.desktop.y || 0}px;` : '';
        const sizeStyles = size ? `width: ${size.width || 200}px; height: ${size.height || 50}px;` : '';
        const cssStyles = styles ? Object.entries(styles).filter(([key]) => key !== 'position').map(([key, value]) => `${key}: ${value};`).join(' ') : '';
        const elementClass = isChild ? `lpb-child-element ladi-${type}` : `lpb-canvas-element ladi-${type}`;
        const elementPosition = isChild ? `position: absolute; ${positionStyles}` : (position.desktop ? `position: absolute; transform: translate(${position.desktop.x || 0}px, ${position.desktop.y || 0}px);` : '');

        let elementHtml = `<div id="${id}" class="${elementClass}" style="${elementPosition} ${sizeStyles} ${cssStyles}" data-type="${type}">`;

        switch (type) {
            case 'section':
                if (componentData.structure === 'ladi-standard') {
                    elementHtml += `
                        <div class="ladi-section" style="width: 100%; height: 100%; position: relative;">
                            <div class="ladi-section-background" style="background-color: ${componentData.backgroundColor || 'transparent'}; background-image: ${componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none'}; background-size: cover; background-position: center;"></div>
                            <div class="ladi-overlay" style="background-color: ${componentData.overlayColor || 'transparent'}; opacity: ${componentData.overlayOpacity || 0};"></div>
                            <div class="ladi-container" style="padding: ${componentData.padding || '20px'}; width: 100%; height: 100%; position: relative;" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">
                    `;
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div></div>`;
                } else {
                    elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '0'}; background: ${componentData.background || 'transparent'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                }
                break;
            case 'container':
            case 'box':
                elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'transparent'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'heading':
                const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                    ? componentData.level
                    : 'h2';
                elementHtml += `<${headingLevel} style="font-size: ${componentData.fontSize || '1.5rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '700'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Tiêu đề'}</${headingLevel}>`;
                break;
            case 'paragraph':
                elementHtml += `<p style="font-size: ${componentData.fontSize || '1rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '400'}; line-height: ${componentData.lineHeight || '1.6'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Đoạn văn'}</p>`;
                break;
            case 'button':
                elementHtml += `<button style="padding: ${componentData.padding || '8px 16px'}; border-radius: ${componentData.borderRadius || '8px'}; background: ${componentData.background || '#2563eb'}; color: ${componentData.color || '#fff'}; border: ${componentData.border || 'none'}; cursor: pointer; font-size: ${componentData.fontSize || '1rem'}; font-weight: ${componentData.fontWeight || '600'}; text-align: ${componentData.textAlign || 'center'}; ${cssStyles}">${componentData.content || 'Nút'}</button>`;
                break;
            case 'image':
                elementHtml += `
                    <div>
                        <input type="file" class="upload-input" accept="image/*" onchange="handleFileUpload(event, '${id}')">
                        <img id="${id}-img" src="${componentData.src || 'https://via.placeholder.com/150?text=Image'}" alt="${componentData.alt || 'Hình ảnh'}" style="max-width: ${componentData.maxWidth || '100%'}; height: ${componentData.height || 'auto'}; border-radius: ${componentData.borderRadius || '0'}; ${cssStyles}" />
                    </div>`;
                break;
            case 'form':
                elementHtml += `<form style="display: flex; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h3 style="margin: ${componentData.titleMargin || '0'}; font-size: ${componentData.titleFontSize || '1.2rem'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (Array.isArray(componentData.fields) && componentData.fields.length > 0) {
                    componentData.fields.forEach((field, index) => {
                        elementHtml += `<input type="${field.type || 'text'}" placeholder="${field.placeholder || field.label || 'Nhập...'}" style="padding: ${field.padding || '8px'}; border-radius: ${field.borderRadius || '8px'}; border: ${field.border || '1px solid #ccc'}; font-size: ${field.fontSize || '1rem'};">`;
                    });
                } else {
