                    elementHtml += `<p style="font-size: ${componentData.fontSize || '1rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '400'}; line-height: ${componentData.lineHeight || '1.6'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Đoạn văn'}</p>`;
                    break;
                case 'button':
                    elementHtml += `<button style="padding: ${componentData.padding || '8px 16px'}; border-radius: ${componentData.borderRadius || '8px'}; background: ${componentData.background || '#2563eb'}; color: ${componentData.color || '#fff'}; border: ${componentData.border || 'none'}; cursor: pointer; font-size: ${componentData.fontSize || '1rem'}; font-weight: ${componentData.fontWeight || '600'}; text-align: ${componentData.textAlign || 'center'}; ${cssStyles}">${componentData.content || 'Nút'}</button>`;
                    break;
                case 'image':
                    elementHtml += `<img src="${componentData.src || 'https://via.placeholder.com/150?text=Image'}" alt="${componentData.alt || 'Hình ảnh'}" style="max-width: ${componentData.maxWidth || '100%'}; height: ${componentData.height || 'auto'}; border-radius: ${componentData.borderRadius || '0'}; ${cssStyles}" />`;
                    break;
                case 'form':
                    elementHtml += `<div style="display: flex; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; ${cssStyles}">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h3 style="margin: ${componentData.titleMargin || '0'}; font-size: ${componentData.titleFontSize || '1.2rem'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                    }
                    if (Array.isArray(componentData.fields) && componentData.fields.length > 0) {
                        componentData.fields.forEach((field, index) => {
                            elementHtml += `<input type="${field.type || 'text'}" placeholder="${field.placeholder || field.label || 'Nhập...'}" style="padding: ${field.padding || '8px'}; border-radius: ${field.borderRadius || '8px'}; border: ${field.border || '1px solid #ccc'}; font-size: ${field.fontSize || '1rem'}; width: 100%; box-sizing: border-box;" />`;
                        });
                    } else if (!children.some(child => child?.type === 'input')) {
                        elementHtml += `<input type="${componentData.inputType || 'text'}" placeholder="${componentData.placeholder || 'Nhập...'}" style="padding: ${componentData.inputPadding || '8px'}; border-radius: ${componentData.inputBorderRadius || '8px'}; border: ${componentData.inputBorder || '1px solid #ccc'}; font-size: ${componentData.inputFontSize || '1rem'}; width: 100%; box-sizing: border-box;" />`;
                    }
                    if (!children.some(child => child?.type === 'button')) {
                        elementHtml += `<button type="submit" style="background: ${componentData.buttonBackground || '#2563eb'}; color: ${componentData.buttonColor || '#fff'}; padding: ${componentData.buttonPadding || '8px 16px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText || 'Gửi'}</button>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                    break;
                case 'section':
                case 'container':
                    elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '0'}; background: ${componentData.background || 'transparent'}; ${cssStyles}">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                    }
                    if (componentData.subtitle && !children.some(child => child?.type === 'paragraph')) {
                        elementHtml += `<p style="font-size: ${componentData.subtitleFontSize || '1.2rem'}; margin: ${componentData.subtitleMargin || '0'}; color: ${componentData.subtitleColor || '#4b5563'};">${componentData.subtitle}</p>`;
                    }
                    if (componentData.content && !children.some(child => child?.type === 'paragraph')) {
                        elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>`;
                    }
                    if (Array.isArray(componentData.features) && componentData.features.length > 0 && !children.some(child => child?.type === 'list')) {
                        elementHtml += `<ul style="list-style: none; padding: ${componentData.featuresPadding || '0'}; margin: ${componentData.featuresMargin || '0'}; display: flex; flex-direction: ${componentData.featuresDirection || 'column'}; gap: ${componentData.featuresGap || '8px'};">`;
                        componentData.features.forEach((feature) => {
                            elementHtml += `<li style="font-size: ${componentData.featureFontSize || '1rem'}; color: ${componentData.featureColor || '#1f2937'};">${feature}</li>`;
                        });
                        elementHtml += `</ul>`;
                    }
                    if (componentData.ctaText && !children.some(child => child?.type === 'button')) {
                        elementHtml += `<button style="padding: ${componentData.ctaPadding || '10px 20px'}; border-radius: ${componentData.ctaBorderRadius || '8px'}; background: ${componentData.ctaBackground || '#2563eb'}; color: ${componentData.ctaColor || '#fff'}; border: ${componentData.ctaBorder || 'none'}; cursor: pointer; font-size: ${componentData.ctaFontSize || '1rem'}; font-weight: ${componentData.ctaFontWeight || '600'};">${componentData.ctaText}</button>`;
                    }
                    if (componentData.contact && !children.some(child => child?.type === 'paragraph')) {
                        elementHtml += `<p style="font-size: ${componentData.contactFontSize || '0.9rem'}; margin: ${componentData.contactMargin || '0'}; color: ${componentData.contactColor || '#4b5563'};">${componentData.contact}</p>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                    break;
                case 'divider':
                    elementHtml += `<hr style="border: none; background: ${componentData.color || '#e5e7eb'}; height: ${componentData.thickness || '2px'}; width: ${componentData.width || '100%'}; margin: ${componentData.margin || '0'}; ${cssStyles}" />`;
                    break;
                case 'spacer':
                    elementHtml += `<div style="background: ${componentData.background || 'transparent'}; height: ${componentData.height || '40px'}; width: ${componentData.width || '100%'}; ${cssStyles}"></div>`;
                    break;
                case 'popup':
                    elementHtml += `<div style="display: flex; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; text-align: ${componentData.textAlign || 'center'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'rgba(255, 255, 255, 0.9)'}; border-radius: ${componentData.borderRadius || '12px'}; box-shadow: ${componentData.boxShadow || '0 4px 6px rgba(0, 0, 0, 0.1)'}; z-index: ${styles.zIndex || 100}; ${cssStyles}">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.5rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                    }
                    if (componentData.content && !children.some(child => child?.type === 'paragraph')) {
                        elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>`;
                    }
                    if (componentData.buttonText && !children.some(child => child?.type === 'button')) {
                        elementHtml += `<button style="padding: ${componentData.buttonPadding || '10px 20px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; background: ${componentData.buttonBackground || '#2563eb'}; color: ${componentData.buttonColor || '#fff'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText}</button>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                    break;
                case 'modal':
                    elementHtml += `<div style="display: flex; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'rgba(255, 255, 255, 0.9)'}; border-radius: ${componentData.borderRadius || '12px'}; box-shadow: ${componentData.boxShadow || '0 4px 6px rgba(0, 0, 0, 0.1)'}; z-index: ${styles.zIndex || 100}; ${cssStyles}">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.5rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                    }
                    if (componentData.content && !children.some(child => child?.type === 'paragraph')) {
                        elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>`;
                    }
                    if (componentData.closeButton && !children.some(child => child?.type === 'button')) {
                        elementHtml += `<button style="padding: ${componentData.buttonPadding || '8px 16px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; background: ${componentData.buttonBackground || '#6b7280'}; color: ${componentData.buttonColor || '#fff'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; align-self: flex-end; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText || 'Đóng'}</button>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                    break;
                default:
                    elementHtml += `<div style="color: ${componentData.errorColor || '#ff0000'}; padding: 10px; border: 1px dashed #ff0000; border-radius: 4px; ${cssStyles}">Unknown Component: ${type}</div>`;
                    break;
            }
        }

        elementHtml += `</div>`;
        return elementHtml;
    };

    pageData.elements.forEach((element) => {
        html += renderElement(element);
    });

    html += `
    </div>
</body>
</html>
    `;

    return html.trim();
};
