import React from 'react';
import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';

const SelectionOverlay = ({ selectedElements, onResize, zoomLevel }) => {
    return (
        <>
            {selectedElements.map((el) => (
                <ResizableBox
                    key={el.id}
                    width={el.size.width}
                    height={el.size.height}
                    onResizeStop={(e, { size }) => onResize(el.id, size)}
                    style={{
                        position: 'absolute',
                        left: el.position.desktop.x / (zoomLevel / 100),
                        top: el.position.desktop.y / (zoomLevel / 100),
                    }}
                    handleStyles={{
                        topLeft: { display: 'block' },
                        top: { display: 'block' },
                        topRight: { display: 'block' },
                        right: { display: 'block' },
                        bottomRight: { display: 'block' },
                        bottom: { display: 'block' },
                        bottomLeft: { display: 'block' },
                        left: { display: 'block' },
                    }}
                >
                    <div className="lpb-canvas-element-selected" />
                    <div className="lpb-selection-handle lpb-selection-handle-top-left" />
                    <div className="lpb-selection-handle lpb-selection-handle-top" />
                    <div className="lpb-selection-handle lpb-selection-handle-top-right" />
                    <div className="lpb-selection-handle lpb-selection-handle-right" />
                    <div className="lpb-selection-handle lpb-selection-handle-bottom-right" />
                    <div className="lpb-selection-handle lpb-selection-handle-bottom" />
                    <div className="lpb-selection-handle lpb-selection-handle-bottom-left" />
                    <div className="lpb-selection-handle lpb-selection-handle-left" />
                </ResizableBox>
            ))}
        </>
    );
};

export default SelectionOverlay;
