// F:\landinghub-iconic\apps\web\src\pages\AuthPage.js
import React, { useState, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Login from '../components/Login';
import Register from '../components/Register';
import Background from '../components/Background';
import { GoogleLogin } from '@react-oauth/google';
import { jwtDecode } from 'jwt-decode';
import '../styles/AuthPage.css';

const AuthPage = () => {
    const { setUser } = useContext(UserContext);
    const [activeTab, setActiveTab] = useState('login');
    const [error, setError] = useState(null);

    const handleGoogleSuccess = (credentialResponse) => {
        try {
            const decoded = jwtDecode(credentialResponse.credential);
            console.log('Decoded Token from Google:', decoded); // Debug: Kiểm tra token
            const userData = {
                role: decoded.role || 'user', // Giả định role, cần backend cung cấp
                name: decoded.name || decoded.given_name || decoded.email.split('@')[0], // Lấy name từ các trường khả dụng
                userId: decoded.sub,
            };
            localStorage.setItem('token', credentialResponse.credential); // Lưu token
            setUser(userData); // Cập nhật context
            window.location.href = '/dashboard'; // Reload và điều hướng
        } catch (err) {
            setError('Lỗi khi decode token: ' + err.message);
        }
    };

    const handleGoogleFailure = (error) => {
        console.error('Google Login Failed:', error);
        setError('Đăng nhập thất bại. Vui lòng thử lại.');
    };

    return (
        <Background>
            <div className="auth-page">
                <div className="tab-container">
                    <label className="tab_label" onClick={() => setActiveTab('login')}>
                        Login
                    </label>
                    <label className="tab_label" onClick={() => setActiveTab('register')}>
                        Register
                    </label>
                    <div className={`indicator ${activeTab}`}></div>
                </div>

                <div className="tab-content">
                    {activeTab === 'login' && (
                        <Login
                            onGoogleSuccess={handleGoogleSuccess}
                            onGoogleFailure={handleGoogleFailure}
                            error={error}
                        />
                    )}
                    {activeTab === 'register' && <Register />}
                </div>
            </div>
        </Background>
    );
};

export default AuthPage;
