    const handleBatchRegenerate = async () => {
        if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën t·∫°o l·∫°i screenshot cho t·∫•t c·∫£ templates? Qu√° tr√¨nh n√†y c√≥ th·ªÉ m·∫•t v√†i ph√∫t.")) {
