import React from 'react';
import ResizableBox from './ResizableBox';

const SelectionOverlay = ({ selectedElements, onResize, zoomLevel }) => {
    if (!selectedElements.length) return null;

    const getBounds = () => {
        const bounds = selectedElements.reduce(
            (acc, el) => ({
                left: Math.min(acc.left, el.position.desktop.x),
                top: Math.min(acc.top, el.position.desktop.y),
                right: Math.max(acc.right, el.position.desktop.x + el.size.width),
                bottom: Math.max(acc.bottom, el.position.desktop.y + el.size.height),
            }),
            {
                left: Infinity,
                top: Infinity,
                right: -Infinity,
                bottom: -Infinity,
            }
        );
        return {
            x: bounds.left,
            y: bounds.top,
            width: bounds.right - bounds.left,
            height: bounds.bottom - bounds.top,
        };
    };

    const bounds = getBounds();

    return (
        <ResizableBox
            bounds={bounds}
            onResize={onResize}
            zoomLevel={zoomLevel}
            handleStyles={{
                topLeft: { width: 8, height: 8, background: '#2563eb', borderRadius: '50%' },
                topRight: { width: 8, height: 8, background: '#2563eb', borderRadius: '50%' },
                bottomLeft: { width: 8, height: 8, background: '#2563eb', borderRadius: '50%' },
                bottomRight: { width: 8, height: 8, background: '#2563eb', borderRadius: '50%' },
            }}
        />
    );
};

export default SelectionOverlay;
